css:
@-moz-document domain("ligmar.ru") {
.preloader.preloader-page,
.preloader-smith,
.preloader-academy { display: none !important; }

app-clan-members > div { position: relative; }

app-clan-members .mb-50 { top: 104px;
	position: absolute;
	width: 100%; }

app-clan-members > div > .panel.shadow.no-last-child { margin-top: 110px; margin-bottom: 50px; }

.body, .body-chat, body, html {-webkit-user-select: auto !important;}

/*[[chat]]*/
}

style_setting_options.value:
.accordion .panel-title .right i.gi-arrow { display: none !important; }

.body-chat.desktop { right: calc(50% - 320px) !important;}

.body-320,
.width-320,
.body-320 alert div,
.body-320 footer { max-width: 640px !important; }

.body-320 .vip .info .vip-lvl,
.body-320 .vip .info:before { right: -24px !important; }

.body-320 app-index .panel.accordion .panel-body { max-height: 800px !important; }
