css:
@-moz-document url-prefix("https://www.heroeswm.ru"), url-prefix("https://www.lordswm.com")
{
td.wb font {
color: rgb(255, 0, 0) !important;
}
/*Background*/
  body
  {
    background-color: #2c251c;
  }

  .wblight
  {
    background-color: transparent;
  }

  .wbwhite
  {
    background-color: #2c251c;
  }
/*Font*/
  td, a, .pi
  {
    color: #635d5d;
  }

  #breadcrumbs a
  {
    color: #bfbfbf;
  }

  input
  {
    background-color: #2c251c;
    border-color: #444;
  }

  textarea, select
  {
    color: #bfbfbf;
    background-color: #2c251c;
  }
/*hwm_GO*/
  td.wb
  {
    background-color: #2c251c !important;
  }
/*forum*/
  table.table3 th
  {
    background-color: #2c251c;
  }

  table.forum tr.message_footer td
  {
    background-color: #2c251c;
    color: #bfbfbf;
  }

  table.table3
  {
    background-color: #2c251c;
  }

  table.c_darker td, table.c_darkers td a:link, table.c_darkers td a:visited
  {
    color: #bfbfbf !important;
    background-color: #2c251c !important;
  }

  table.c_darker td, table.c_darker td a:link, table.c_darker td a:visited
  {
    color: #858e90;
  }

  table.forum tr.second
  {
    background-color: #2c251c;
  }

  .fsm
  {
    color: #bfbfbf;
  }

  font
  {
    color: #797979 !important;
  }

#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(1) {
filter: invert(100) hue-rotate(180deg);
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(1) > td:nth-child(4) > table > tbody > tr:nth-child(2) {
background: #d8d8d8;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(1) > td:nth-child(8) > table {
background: #d8d8d8;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(1) > img {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(2) {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(3) > img {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(5) > img {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(6) {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
#main_top_table > tbody > tr > td > table > tbody > tr:nth-child(3) > td > table > tbody > tr > td:nth-child(7) {
filter: invert(100) hue-rotate(180deg);
border-bottom: #e7d7d7 solid;
}
.wblight2 {
    BACKGROUND-COLOR: #332f2f !important;
}
body > center > table > tbody > tr > td > table > tbody > tr:nth-child(2) > td.wbwhite > table > tbody > tr:nth-child(5) > td:nth-child(5)
.wbwhite tr.wb {
    background: #151515;
}

td.wbwhite tr:nth-child(even) {
  background: #2c251c;
}
.wbwhite tr.wb td {
    background: none;
}
.wbwhite tr.wb {
    background: #19191c;
}
body > center > table:nth-child(2) > tbody > tr > td > table > tbody > tr:nth-child(2) > td.wbwhite > table > tbody > tr:nth-child(1) {
    background: #2c251c;
}
li.subnav font {
    color: red !important;
}
.wb2 {
BACKGROUND-COLOR: #262222;
}
.TextBlockContent {
    background: none;
    background-color: #241f1fb8;
}
}

@-moz-document url-prefix("https://www.heroeswm.ru/tavern.php"), url-prefix("https://www.lordswm.com/tavern.php")
{
body > center > table:nth-child(2) > tbody > tr > td > center > table > tbody > tr > td {
filter: invert(100) hue-rotate(180deg);
}
.wbwhite {
    background-color: #e8e4de;
}

}
