css:
@-moz-document domain("sellingplatformconnect.amadeus.com") {


div.yuimenubar li.yuimenubaritem {
    padding: 0 4px 4px 4px;
} /* Slim header */

.df div.menuSection div.yuimenubarcoin div.yuimenubar {
    height: 18px;
    background: no-repeat #005eb8 url(https://www.em1.sellingplatformconnect.amadeus.com/modules/df/img/df-ama-logo.gif) -29px -16px;
    padding-top: 0px;
    padding-left: 112px;
} /* Slim header */

.df .desktop_menu_right {
    right: 82px;
} /* Move Sign out more towards left */

.df .um-logout-container {
    color: #005eb8;
    font-size: 10px;
} /* Last Login Resize and Blue */

.df .placeholder-main {
    top: 40px;
} /* Command Page starts at.. */

.df .uicToolBar.searchToolbar {
    height: 36px;
    padding: 0;
    margin-top: -34px;
    margin-left: 360px;
} /* 2 Toolbars -> 1 Toolbar */

.placeholder-top {
    height: 40px;
} /* Header height */

.df .toolbar .uicToolBar .item span.uicIcon.iconSave, .df .toolbar .uicToolBar .item span.uicIcon.iconPrint {
    height: 20px;
    margin-top: -5px;
} /* Safe and Print Button slim */

.df div.toolbar .uicToolBar.uicToolBarCoin {
    padding-left: 1px;
}  /* Safe and Print Button reposition */

.df .uicToolBar .uicToolbarSelectButton .uicToolbarButtonText, .df .uicToolbarSelectButton uicToolbarButtonText.hover {
    height: 20px;
    line-height: 17px;
} /* New Booking File Button slim */

.df .uicToolBar .uicToolbarSelectButton.item {
    margin-top: -3px;
} /* New Booking File Button reposition */

.df li.uicToolbarSelectButton.item em {
    height: 20px;
    margin-top: 4px;
} /* New Booking File Button Arrow Down slim */

.df button.uicButton.newprofile, .df button.uicButton.newcommandpagebtn, .df button.uicButton.newtoma, .df button.uicButton.newbahn {
    height: 20px;
    margin-top: -8px;
    border: 1px solid #00d037;
} /* New Command Page Button slim and green */

.df button.uicButton.newprofile:hover, .df button.uicButton.newcommandpagebtn:hover, .df button.uicButton.newtoma:hover, .df button.uicButton.newbahn:hover {
    background: #00d037 !important;
} /* New Command Page Button green */

.df .toolbar .toolbarSection .uicToolBar button.uicButton * {
    color: #00d037 !important;
} /* New Command Page Button green */

.df .toolbar .toolbarSection .uicToolBar button.uicButton:hover * {
    background: #00d037 !important;
    color: white !important;
} /* New Command Page Button green */

.df .quicksearch .xSimpleFrame, .df .quicksearch .xSimpleFrame input {
    height: 20px;
} /* Retrieve Booking input slim + nested*/

.df .sellweb2optToolbar .quicksearch {
    height: 40px;
    padding: 4px 0 0 4px;
} /* Retrieve Booking input and more reposition */

.df .quicksearch button {
    height: 20px;
} /* Retrieve Button resize */

.df .quicksearch button>span {
    height: 18px;
} /* Retrieve Button resize */

.df button.xButton .xFrameContent {
    line-height: 18px;
} /* Retrieve Button reposition text */

.df .quicksearch .xWidget {
    height: 20px!important;
} /* Advanced Button resize */

.df #yourSmartFlowsButton {
    line-height: 24px;
} /* Your Smart Flows Button reposition */

.df .smkRunningToolbar>div, .df .smkRunningToolbar>div>div {
    height: 20px!important;
} /* Command Page Header background element resize to avoid conflicts */

.uicTaskFile .uicTaskFileBody {
    padding: 0px;
    clear: left;
    visibility: hidden;
    height: 0px;
} /* Command Page Header background element hidden to avoid conflicts */

.df .pnrnavbar .pnrResponsibleOffice {
    margin-top: -9px;
    line-height: 26px;
    margin-right: 70px;
} /* Responsible office reposition */

.df .placeholder-main {
    bottom: 25px!important;
} /* Footer slim (Main page ends at..) */

.df [_placeholder="bottom"] {
    height: 26px!important;
} /* Footer slim */

.df ul.uicTaskbar li div.c span.uicTaskbarText {
    height: 26px;
    line-height: 25px;
} /* Footer slim */

.df .uicTaskbar {
    height: 26px;
} /* Footer slim */

.df .uicTaskbar li div.icon {
    top: -3px;
} /* Footer Icon reposition */

.df .bf-center-no-qm  {
    right: 15px;
} /* Hidden Goto Bar slim */

.df .eastPanCollapsed {
    width: 15px;
} /* Hidden Goto Bar slim */

.df .bf-center-qm, .df .bf-center-no-qm {
    top: 19px;
} /* Command Page Header Slim (starts at..) */

.df .uicTaskFile {
    height: 19px;
} /* Command Page Header Slim (height) */

.df .uicTaskFile .uicTaskFileHeader .title {
    margin-top: -3px;
    padding-top: 0px;
    padding-left: 30px;
} /* Command Page Header Slim reposition */

.df .uicTaskFile .uicTaskFileHeader .cryptic {
    background: url(https://www.em1.sellingplatformconnect.amadeus.com/modules/df/img/taskHeaderCryptic.gif) no-repeat scroll white;
} /* Command Page Header Icon reposition */

.uicTaskFile .uicTaskFileHeader .title strong {
    padding: 2px 60px 0 0!important;
} /* Graphic Mode Header Icon repostion */

.df .pnrnavbar .uicTaskFile .uicTaskFileHeader .file, .df .profile .uicTaskFile .uicTaskFileHeader .profile, .df .profilefollowup .uicTaskFile .uicTaskFileHeader .profile {
    background-position: 0px 0px;
} /* Graphic Mode Header Icon repostion */

.CSS34 .menu, .CSS33 .menu, .CSS32 .menu {
    right: 63px;
} /* split screen buttons moved to the left to make room for history box */

.df .app-banner-container {
    visibility: hidden;
/*  margin-top: -800px; */
    z-index: 0;
} /* remove add banner */

.df .eastPan {
    top: 55px;
}

.df .ylayout-collapse-east {
    width: 110px;
    background-color: #00d037;
}

}
