css:
/* i really want this to be global */


/*  定义全局颜色变量 */
:root {
  --blue-color: #3e8dbc; 
  --blue-bg-color: #3e8dbc; 
  --white-color:  #fff;
  --white-bg-color:  #fff;
}
/*  按钮 */
.m-btn {
    width: 96px;
    height: 32px;
    background-color: var(--blue-bg-color);
}
.mm-button__primary {
    background-color: var(--blue-bg-color);
    border-color: var(--blue-color);
    color: #fff;
}
.mm-button__primary:hover,.mm-button__primary:active{ 
background-color: var(--blue-bg-color);
    border-color: var(--blue-color);
}
/*  选项卡 */
.mm-tabs-active-bar {  
    background-color: var(--blue-bg-color);
}
.mm-tabs-item.mm-tabs-item--active,.mm-tabs-item:hover{
color:var(--blue-color);
}

.component-dynamic-item-base .c-link:hover{
color: var(--blue-color);
}
/* 横线 */
.not-editable-inpu,.normal-textarea-field .not-editable-input.no-value{
    border-bottom: 1px solid #eee;
}

.customer-list-table-wrap .table-name-column:hover .company-name-link{color: var(--blue-color);}
.layout-sidebar__handle{
background: var(--blue-bg-color);
}
.layout-sidebar__handle:hover{
background: #000;
}
/* logo */
.layout-sidebar__logo {
    padding: 0;
background:#222;
}
.layout-sidebar__logo i {
    font-size: 0;
    color: aliceblue;
    width: 100%;
    height: 66px;
    opacity: .7;
    display: block;
    background: url(https://www.alpapowder.cn/logo_crm.png) no-repeat left center;
}
.layout-sidebar--collapse .layout-sidebar__logo {
    padding: 0;
    background: url(https://www.alpapowder.cn/CRM.png) no-repeat center;
}
.layout-sidebar--collapse .layout-sidebar__logo i {
background:none !important;
}
/* 以下为左侧菜单项 */
.layout-sidebar {
    box-shadow: none;
    background: #313131;
}
/* 菜单宽度 */
.layout-sidebar .layout-menu:not(.mm-menu--collapse) {
    width: 200px;
}
.riven-layout-right {
    padding: 0 0 0 200px;
}
/* 每个菜单样式 */
.layout-sidebar .mm-menu-item, .layout-sidebar .mm-submenu-title {
    height: 43px;
    line-height: 43px;
    border-bottom: 1px solid rgba(255,255,255,.04);
}
.layout-sidebar .layout-menu .mm-submenu-title:hover{
    color: var(--white-color);
}
.layout-sidebar .mm-submenu.mm-submenu--opened>.mm-submenu-title .mm-menu-title, .layout-sidebar .mm-submenu.mm-submenu--opened>.mm-submenu-title .mm-submenu-icon-arrow {
    color: var(--blue-color);
}
.layout-sidebar .layout-menu .mm-menu-item.active {
    background-color: var(--blue-bg-color) !important;
}
.layout-sidebar .mm-submenu.active>.mm-submenu-title .icon.common-iconfont{
color: var(--blue-color);
}
.mm-menu-popper .mm-menu-item.active {
     background-color: var(--blue-bg-color) !important;
}
.layout-sidebar .mm-submenu.active>.mm-submenu-title .mm-menu-title,.layout-sidebar .mm-submenu.mm-submenu--opened>.mm-submenu-title .icon.common-iconfont{
color: var(--blue-color);
}

/* 菜单图标 */
.layout-sidebar .icon.common-iconfont {
    font-size: 14px;
    color: rgba(255,255,255,0.3);
}
.layout-sidebar .mm-submenu-title .mm-submenu-icon-arrow {
    color: #444;
}
/*右侧客户列表*/
.customer-pro-detail{padding:20px;}
.mm-table table thead{
      font-size: 16px;
}
