css:
@-moz-document domain(nitrotype.com) {

  .dash-copyContainer {
    background: #134499;
/*background: linear-gradient(38deg, rgba(32,36,42,1) 30%, rgba(12,13,13,1) 100%);*/
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 9px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
    border: none;
    border-bottom: 6px solid #072f75;
box-shadow: 0px 0px 4px rgb(0,0,0,0.3);
   }

  .dash-copy
  {
    color: #fff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
	font-size: 25px;
    
  }

  ::selection
  {
    background-color: #fff;
    color: #000;
  }
  

}
