css:
@-moz-document regexp("https://admin.karte.io/p/.*/service.*") {
/* UI */
/* - Button */
.sw-Btn._primary:focus { background: #21887f }
.sw-Btn._support:focus,
.sw-Btn._basicLight:focus { background: rgba(0,16,14, .2) }

.sw-Textarea textarea,
.el-select .el-input .el-input__inner {
    box-shadow: 0 0 1px rgba(0,16,14, .5);
    background: rgba(255,255,255, .75);
}
.el-select .el-input.is-disabled .el-input__inner {
    box-shadow: none !important;
}

/* Components */
/* - Service Inner Header - be sticky */
.Setting .Setting_Heading {
    position: sticky;
    top: 0;
/*     top: 20px; */
    background: #fff;
    padding: 8px 1.1em 8px 10px;
    margin: -25px -10px 20px;
    box-shadow:
        0  7px 3px -6px rgba(0, 0, 0, 0.05),
        0 -6px 3px -6px rgba(0, 0, 0, 0.05);
}
.Setting:first-child .Setting_Heading { margin-top: -10px }

/* Action */
.Action_Info .Action_Name { margin-bottom: 0 }
.Action_Info .Action_Name ~ span {
    font-size: 12px;
    line-height: 1.3333333;
}
}
