@-moz-document domain(new.rro.ch) {
* {
  	--main-color:  #F2F2F2;
 	--main-bg-color: #181A1B;
  	--second-bg-color: #3F3F3F;
  	scrollbar-color: #3F3F3F #1C1E1F;
	}
  
    ::-moz-selection{
      color: black;
      background-color: rgb(0,191,255,0.75);
      }

  ::selection{
      color: black;
      background-color: rgb(0,191,255,0.75);
      }
  
    ::-webkit-scrollbar {
  	width: 25px;
	}
  
  h1, h2, h3, h4, h5, p, a, li, address, span, label, div.captchaBox, div.ng-star-inserted, div.commentSent.ng-star-inserted, div.noResults.ng-star-inserted, button.ng-star-inserted, a.ng-star-inserted {
  	color: var(--main-color) !important;
	}

    span.ghostery-pb-tracker, div.ghostery-trackerContainer, div#ghostery-title, div#ghostery-box, div#ghostery-minimize, div#ghostery-trackerList, span.ghostery-tracker, span#ghostery-breaking-tracker {
  	 background-color: var(--second-bg-color) !important;
     border-color: var(--second-bg-color) !important;
  	 } 
  
  span.icon-menu, div.headerButtonsRight, span.icon-suche.searchButton, span.icon-menu.navigationButton, .topNav {
  	color: var(--main-bg-color) !important;    
  	 }

 section, div.noContainer.voter:hover, div.yesContainer.voter.voted:hover, div.wrapper.cloak, div.underline, div.captionToggler, div.ad-top-wrapper-desktop, div.content, div.wrapper, html, main, nav, .ng-star-inserted, footer, header, div.container, div.nsm-content, div.rc-anchor.rc-anchor-normal.rc-anchor-light, .white.ng-star-inserted, .ng-star-inserted {
  	background-color: var(--main-bg-color) !important;
	}

  div {
  	background-color: var(--main-bg-color);
	}

  div#ghostery-purple-box, span.percentage.ng-star-inserted, div.logosDesktop, div.navigationContainer, div.headerButtonsRight, span {
  	background-color: hsla(0,0%,0%,0) !important;
	}

  input, ::placeholder, div.cookieBar.visible, select {
    background-color: var(--second-bg-color) !important;
    border-color: var(--main-color) !important;
    border-width: 0.5px;
    color: var(--main-color) !important;
	}
}

