    background: #ed2761;
    border-color: #ed2761;
    color: #1c1c1c !important;
}
.button--secondary:hover,.button--secondary:focus{
    background: #1c1c1c;
    border-color: #ed2761;
    color: #ed2761 !important;
}
.pagination .active a{
    color: #ed2761 !important;
}
/*LOGIN*/
.site__content_container,.content{
    background: #1c1c1c;
}
