#logonavcon{
    background: #1c1c1c;
}
body.blog,body.single,body.category,body.page.page-id-2704.page-template.page-template-tpl-fullwidth.page-template-tpl-fullwidth-php,body.page-template{
    background: #1c1c1c !important;
}
form#nebula-hero-search input{
