css:
@-moz-document domain("youtube.com") {
ytd-compact-video-renderer #video-title.ytd-compact-video-renderer:hover,
#text.ytd-channel-name:hover,
ytd-rich-grid-video-renderer[mini-mode] #video-title.ytd-rich-grid-video-renderer:hover {
    text-decoration: underline;
    color: #128ee9;
}
ytd-rich-grid-video-renderer[mini-mode] #video-title.ytd-rich-grid-video-renderer,
#video-title.ytd-rich-grid-video-renderer {
    color: #167ac6;
    font: 13px arial,sans-serif !important;
}

#title.ytd-rich-grid-renderer {
    font-size: 1.5rem !important;
}
#metadata-line {
    cursor: text;
}
/* Remove "add to queue" */
ytd-thumbnail-overlay-toggle-button-renderer.style-scope:nth-child(2) {
    display: none;
}
#grid-header {
    margin: 18px 0 10px 18px !important;
}
.style-scope.ytd-rich-grid-video-renderer {
    margin-bottom: 0px !important;
    margin-top: 1px !important;
}
ytd-rich-item-renderer {
    margin-bottom: 9px;
    margin-right: 5px;
    margin-left: 5px;
}
#guide-inner-content {
    scrollbar-width: none;
    position: relative;
    left: 1px;
}
#guide-content {
    position: relative;
    left: 96%;
    background: #272727 !important;
    border-radius: 5px 0 0 0;
    width: 200px;
}
ytd-guide-entry-renderer {
    height: 35px;
    width: 198px;
    color: #999;
    background: linear-gradient(to bottom,#292929 0,#252525 100%);
    border-top: 1px solid #323232;
    border-bottom: 1px solid #1b1b1b;
    font-size: 11px;
}
ytd-guide-entry-renderer[active] .title.ytd-guide-entry-renderer,
ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer {
    color: white;
}
ytd-guide-entry-renderer[active] .title.ytd-guide-entry-renderer {
    font: 11px arial,sans-serif !important;
}
.title.ytd-guide-entry-renderer {
    color: #999;
    font: 11px arial,sans-serif !important;
}
ytd-guide-entry-renderer[active] {
    background: #1c1c1c !important;
	  border-right: 4px solid #c4302b;
      width: 195px;
}
ytd-guide-renderer {
    width: 200px !important;
}
ytd-mini-guide-renderer {
    display: none;
}
/* YouTube Old Home Style by figgycity50*/
@media (max-width:2000px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 6 !important;
        --ytd-rich-grid-posts-per-row: 6 !important;
    }
}

@media (max-width:1600px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 5 !important;
        --ytd-rich-grid-posts-per-row: 5 !important;
    }
}

@media (max-width:1300px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 4 !important;
        --ytd-rich-grid-posts-per-row: 4 !important;
    }
}

@media (max-width:1000px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 3 !important;
        --ytd-rich-grid-posts-per-row: 3 !important;
    }
}

@media (max-width:700px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 2 !important;
        --ytd-rich-grid-posts-per-row: 2 !important;
    }
}

@media (max-width:400px) {
    ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 1 !important;
        --ytd-rich-grid-posts-per-row: 1 !important;
    }
}


ytd-rich-grid-renderer {
    width: 75.6%;
    margin: auto;
}

ytd-rich-shelf-renderer {
    border-top-width: 1px;
}

#show-more-button.ytd-rich-shelf-renderer {
    border-bottom-width: 1px;
}

#title.ytd-rich-grid-renderer, #title.ytd-rich-shelf-renderer {
    font-size: 1.5rem;
    line-height: 1.5rem;
}

#grid-header.ytd-rich-grid-renderer, #rich-shelf-header.ytd-rich-shelf-renderer {
    margin: 18px 0 18px 18px;
}

#rich-shelf-header.ytd-rich-shelf-renderer {
    margin: 18px 0 18px 0px;
}

#avatar-link {
    display: none;
}

#video-title.ytd-rich-grid-video-renderer {
    font-size: 1.4rem;
    line-height: 1.8rem;
    max-height: 3.5rem;
}

h3.ytd-rich-grid-video-renderer {
    margin: 6px 0 4px 0;
}

#metadata-line.ytd-video-meta-block span.ytd-video-meta-block, #text.complex-string.ytd-channel-name {
    font-size: 1.3rem;
}



/* MASTHEAD */
/* MASTHEAD */
/* MASTHEAD */

/* YouTube Logo */
#logo-red-icon-container, #logo-icon-container{
    content: url(https://i.imgur.com/DMogC3v.png);
    background: url(http://s.ytimg.com/yt/imgbin/www-refresh-vflk40U3E.png);
	background-position: 0px -280px !important;
    height: 40px !important;
    width: 100px !important;
    z-index: 1;
  }
ytd-topbar-logo-renderer {
    position: relative;
    left: 21.1%;
    top: 3%;
}
#header ytd-topbar-logo-renderer {
    display: none;
}
ytd-topbar-menu-button-renderer.style-scope:nth-child(2) {
    display: none !important;
}
/* Background */
#primary, #masthead, #guide-wrapper, #content.style-scope.ytd-app {
    background: url(https://i.imgur.com/RRbjcR1.png);
}
/* Search Bar */
ytd-searchbox {
    top: 1% !important;
    position: relative;
}
ytd-searchbox#search.stylescope.ytd-masthead {
    max-width: 419px !important;
}
#search-form {
    max-width: 419px !important;
}
#search.ytd-searchbox {
    top: 1px !important;
    position: relative;
}
#container.style-scope.ytd-searchbox {
    padding-left: 1px;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
}
#search-icon-legacy {
    height: 32px !important;
    width: 36px !important;
    border-color: #ccc #ccc #aaa;
	background-image: linear-gradient(to bottom,#fafafa 0,#dcdcdc 100%);
	color: #333;
	border-bottom: 1px solid #aaa;
}
yt-icon.style-scope.ytd-searchbox {
    content: url(https://i.imgur.com/DMogC3v.png);
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/www-refresh-vflk40U3E.png);
	background-position: -19px -104px;
    position: relative;
    left: -3px;
    width: 15px !important;
    height: 15px !important;
    margin: 0px 5px;
}
form#search-form.style-scope.ytd-searchbox {
    width: 419px;
    height: 32px;
    left: -10.17%;
    position: relative;
}

/* HOME VIDEOS */
/* HOME VIDEOS */
/* HOME VIDEOS */

/* Remove YouTube TV Ads */
ytd-primetime-promo-renderer {
    display: none !important;
}
/* Remove Youtube Stories */
#dismissable.style-scope.ytd-rich-shelf-renderer {
    display: none;
}
/* Fix Recommended Space Between Videos */
#contents.style-scope.ytd-rich-grid-renderer {
    margin-top: 0px;
    position: relative;
    left: 14px;
    top: -1px;
}
ytd-rich-grid-renderer {
    background: white;
    padding-left: 18px;
    padding-right: 18px;
}

/* Square profile pictures */
yt-img-shadow {
    mask: none !important;
    border-radius: 0 !important;
}
}

@-moz-document regexp("http*.://.*.youtube.com/") {
.style-scope {
    font: 11px arial,sans-serif !important;
}
.yt-simple-endpoint.style-scope.yt-formatted-string:hover {
    color: #167ac6 !important;
}
}

@-moz-document regexp("http*.://.*.youtube.com/watch.*") {
ytd-watch-flexy {
    background: url(https://i.imgur.com/RRbjcR1.png);
}
#player-container-outer.style-scope.ytd-watch-flexy {
    margin: 0 0 0 0 !important;
}
/*ytd-watch-flexy[flexy_] #player-container-inner.ytd-watch-flexy {
    padding-top: 56.25% !important;
}
ytd-watch-flexy[flexy_] {
    --ytd-watch-flexy-width-ratio: 16 !important;
    --ytd-watch-flexy-height-ratio: 9 !important;
}*/
.ytp-miniplayer-button.ytp-button {
    display: none;
}
.ytp-size-button.ytp-button {
    display: none;
}
ytd-compact-autoplay-renderer {
    border-bottom: none !important;
    padding-bottom: 0px;
    margin-bottom: 0px;
}
ytd-compact-autoplay-renderer #head {
    display: none !important;
}
yt-formatted-string#text.style-scope.ytd-button-renderer:hover,
yt-formatted-string.style-scope.yt-next-continuation:hover {
    text-decoration: underline;
}
}
