css:
@-moz-document domain("newsletter.codefiction.tech"){
html {
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

body {
  margin: 0;
  padding: 0;
  background: #100806;
  font-family: "Nunito", sans-serif;
  font-size: 16px;
  color: #eee;
}

h1,
h2,
h3,
h4,
h5,
h6 {
   color: #eee;
  margin-top: 0;
  margin-bottom: 0.5rem;
}

a {
  color: #ff6600;
  text-decoration: none;
  word-wrap: break-word;
}

a:hover {
  color: #eee;
}

.container {
  margin: 0 auto;
}

.top-bar {
  background: #ff6600;
  padding: 1rem;
  color: #100806;
}

ul.nav {
  list-style-type: none;
  color: #100806;
  border-top: 3px double #ff8d42;
  font-size: 13px;
}

ul.nav li {
  list-style-type: circle;
}

.content {
  background: #100806;
  border: 1px solid #efefef;
  border-radius: 3px;
  box-shadow: 0 0 4px rgba(0, 0, 0, 0.08);
}

.message--header {
  display: flex;
  justify-content: space-between;
}

.reaction-count {
  display: flex;
  align-items: center;
  color: #ff6600;
}

.thumbs-icon {
  width: 1.125rem;
  margin-right: 0.5rem;
}

.avatar {
  display: inline-flex;
  border-radius: 50%;
  height: 36px;
  width: 36px;
}

.avatar img {
  border-radius: 50%;
}

.user-info {
  display: flex;
}

.username {
  /* margin-left: 1rem; */
  font-size: 1rem;
  font-weight: 600;
  flex-shrink: 0;
  align-self: flex-end;
  margin-bottom: 0;
  color: #989898;
}

.username span {
  font-size: 0.75rem;
  margin-left: 0.5rem;
  color: #b7b7b7;
}

.message {
  padding: 1.5rem;
  border-bottom: 1px solid #efefef;
}

.message--content {
  margin-top: 1rem;
  display: block;
}

.message--link {
  color: #b7b7b7;
  display: block;
  align-items: center;
  font-size: 0.9rem;
  margin-top: 0.5rem;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}

.message--link img {
  margin-right: 0.5rem;
  transform: translate(0, 2px);
}

ol.data-list li {
  margin: 18px 0;
}

.header {
  margin-top: 1.5rem;
  margin-bottom: 1.7rem;
}

.brand {
  display: inline-flex;
  align-items: center;
  font-weight: 600;
  text-transform: uppercase;
}

.brand--title {
  margin-left: 11px;
  color: #555;
}

.sidebar.right {
  background: #444;
  border-left: 2px solid #aaa;
}
.user {
  padding: 20px 0 0 35px;
  height: 150px;
}
.user img {
  width: 130px;
  clip: rect(0px, 130px, 150px, 0px);
  position: absolute;
}

.nav.right {
  border-top: none;
}
.nav.right li {
  font-size: 13px;
  font-style: italic;
}
.nav.right li a {
  color: #999;
}
a.button {
  background: #777;
  color: white;
  display: inline-block;
  padding: 8px 15px;
  margin: 20px 0 0 35px;
  text-decoration: none;
  font-size: 13px;
}

.message-item span {
  color: #666;
  word-wrap: break-word;
}

@media (min-width: 576px) {
  .container {
    max-width: 540px;
  }
}

@media (min-width: 768px) {
  .container {
    max-width: 720px;
  }
}

@media (min-width: 992px) {
  .container {
    max-width: 960px;
  }
}

@media (min-width: 1200px) {
  .container {
    max-width: 1140px;
  }
}

@media (min-width: 1800px) {
  .container {
    max-width: 1600px;
  }
}





}
