/* ==UserStyle==
@name         Dark Blue Zones
@namespace    github.com/DaemonExMachina/userstyles
@description  live longer, better, *darker*
@version      1.0.2
@author       DaemonExMachina <dev@daemonexmachina.com>
@license      CC-BY-SA-4.0
==/UserStyle== */

@-moz-document domain("bluezones.com") {
  :root {
    --text-1       : #fff;
    --text-2       : #bbb;
    --bg-body      : #222;
    --bg-dark-grey : #333;
  }

  body, input,
  .lf-step,
  .off-canvas-content {
    background-color : var(--bg-body);
  }

  .life-step-page {
    background-color : var(--bg-body) !important;
  }

  .is-dropdown-submenu {
    background-color : var(--bg-dark-grey);
  }

  body,
  h1, h2, h3, h4, h5, h6,
  .off-canvas-content,
  .g-category__block a,
  .g-text__title,
  header .menu > li > a,
  .life-heading h4,
  .g-header.scrolled header .menu > li > a,
  .life-step-page p strong,
  .life-week-page p strong,
  .life-page-we p strong {
    color : var(--text-1);
  }

  p,
  .medium-4 h2 a,
  .filtercontainer p,
  .filtercontainer li,
  .filtercontainer li a,
  .life-heading p,
  .life-step-page p,
  .life-week-page p,
  .life-page-we p,
  .life-step-page ul li,
  .life-week-page ul li,
  .life-page-we ul li {
    color : var(--text-2);
  }

  section[style*='ikaria-homepage'],
  section[style*='FB-Carousel'],
  section[style*='slide-blue-zones-test'],
  section[style*='Oatmeal'],
  section[style*='mediterranean-cave'],
  section[style*='mediterranean-2'],
  section[style*='bg-newsletter'],
  section[data-image-src*='banner'],
  div[style*='bg-newsletter'] {
    background-color      : rgba(0, 0, 0, .65) !important;
    background-blend-mode : darken;
  }

  div.newsletter-image {
    filter : brightness(.5);
  }

  .g-category__wrp:nth-child(odd),
  .g-header.scrolled header,
  header {
    background-color : var(--bg-dark-grey);
  }

  #wp_page_numbers li a,
  #wp_page_numbers ul li.page_info {
    color            : var(--text-1);
    background-color : var(--bg-dark-grey);
  }

  #wp_page_numbers li a:hover,
  #wp_page_numbers li.active_page a,
  #wp_page_numbers li.active_page a:hover {
    background-color : #963;
  }

  /* Scrollbars */

  *::-webkit-scrollbar,
  *::-webkit-scrollbar-track,
  *::-webkit-scrollbar-thumb {
    width            : 8px;
    background-color : var(--bg-2);
  }

  *::-webkit-scrollbar-thumb {
    background-color : #999;
  }
}
