@-moz-document url-prefix("https://fs.blog"), url-prefix("http://fs.blog") {
  body,
  .bgcolor-f2f2f2 {
    color            : #fff;
    background-color : #222;
  }

  body, button, input, select, textarea {
    font-weight : normal;
  }

  .d-header,
  header {
    background-color : #444;
  }

  .logo .a {
    fill : #fff;
  }

  .tab_container {
    background-color : #222;
  }

  .rte h2,
  ul.desktop-nav li a,
  h2.entry-title a,
  h2.entry-title {
    color : #fff;
  }

  .section-sidebar-blog-single ul li a {
    color : #bbb;
  }

  ul.blog-categories-nav li a,
  ul.desktop-nav li a.signup, ul.desktop-nav li a.login {
    color : #bbb;
  }

  .column100.section-single-content-wrapper {
    color            : #fff;
    background-color : #333;
  }

  .entry-content blockquote p {
    color : #ccc;
  }

  .price-card-gift .section-text_block_wrapper {
    border-color : #555;
  }

  .price-card,
  .price-card-gift .section-text_block_wrapper,
  .bgcolor-cccccc {
    background-color : #555;
  }


  .full.gray,
  .bgcolor-eeeeee {
    background-color : #444;
  }

  ul.footer-ul li a {
    color : #bbb;
  }

  .section-article-quote p.cite,
  aside.quote .title {
    color : #bbb;
  }

  aside.quote .title,
  blockquote {
    background-color : #333;
    border-left      : 5px solid #777;
  }

  img {
    background-color : #bbb;
    border-radius    : 10px;
  }
}
