@-moz-document domain(fiditech.nl) {

html, body {
    overflow: hidden;
    background-color: black;
	color: white;
}

.menu {
    overflow: visible;
    position: relative;
    width: 100%;
    background-color: black;
	color: white;
}

.menu .curPage .title {

    color: white;
	text-shadow: #ffc 1px 1px 1px;
    font-size: 120%;
}



.tree {
    background-color: black;
	border: 1px solid #bbb;
	padding: 10px 0 10px 0;
	overflow: auto;
	white-space: nowrap;
	position: relative;
	color: white;
}


.object-menu {
    background-color: rgba(0, 0, 0, 0);
    text-align: center;
    border: 1px solid #ccc;
    border-top-color: rgb(204, 204, 204);
    border-top-style: solid;
    border-top-width: 1px;
    border-bottom-color: rgb(204, 204, 204);
	border-top: 0;
	margin-top: 6px;
    border-bottom-color: black;
	background-color: black;
	color: white;
}

.PageTree .elemContent {
    border: 1px solid #bbb;
    border-top-color: rgb(187, 187, 187);
    border-top-style: solid;
    border-top-width: 1px;
	border-top: 0;
	z-index: 1;
	background-color: black;
	color: white;
}


.PageTree .elemCommonContent {
    border: 1px solid #bbb;
    border-bottom-color: rgb(187, 187, 187);
    border-bottom-style: solid;
    border-bottom-width: 1px;
	border-bottom: 0;
	padding: 5px 5px 0 5px;
	background-color: black;
	color: white;
}


.menu-items {
    position: absolute;
    z-index: 9999;
    background-color: black;
	border: 1px solid #ccc;
	padding: 2px 4px;
	box-shadow: 3px 3px 1px #888;
	color: white;
}


.menu > .menubar > UL > LI UL {
    position: absolute;
    display: none;
    margin-top: -1px;
    overflow: visible;
    background-color: black;
    border: 1px solid #aaa;
    font-size: 90%;
    box-shadow: 4px 4px 10px #777;
    border-radius: 3px;
    border-top-left-radius: 3px;
}

