css:
@-moz-document domain("descopera.ro") {
html {
    --color-black: #000;
    --color-dark-grey: #121212;
    --color-medium-grey: #212121;
    --color-light-grey: #bfbfbf;
    --color-white: rgba(255, 255, 255, 0.9);
    --color-red: #d74b1f;
    --color-dark-red: #b33d19;
    --color-green: #008000;
}

.bg-white,
.bg-grey {
    background-color: transparent;
}

#main {
    background-color: var(--color-black);
}

.archive__container {
    background-color: var(--color-black);
    padding: 10px 30px;
}

.single__container,
.articles--columns .article__content,
.articles--md-3-c .article, 
#pushmenu .pushmenu-close a,
.qc-cmp-ui,
.qc-cmp-nav-bar.qc-cmp-top,
.qc-cmp-nav-bar.qc-cmp-bottom,
.qc-cmp-purposes-page-content,
.qc-cmp-ui .qc-cmp-table-header,
.qc-cmp-publisher-purposes-table .qc-cmp-table-row,
.qc-cmp-vendor-column-header .qc-cmp-company-column,
.qc-cmp-vendor-list .qc-cmp-vendor-row-header,
.qc-cmp-vendor-list .qc-cmp-vendor-row{
    background-color: var(--color-dark-grey) !important;
}

.single__tags a,
.single__media .feature-image-caption,
#pushmenu {
    background-color: var(--color-medium-grey);
}

#pushmenu .pushmenu-close a span:after, 
#pushmenu .pushmenu-close a span:before {
    background-color: var(--color-white);
}

.archive .intro .title,
#breadcrumbs,
.article__title,
.single__title,
.single__content,
.single__content .anchor a,
.single__media .feature-image-caption,
.headline,
#pushmenu .pushmenu-close a,
#pushmenu .pushmenu-content .pushmenu-menu ul li a,
#pushmenu .pushmenu-content .pushmenu-pages a,
.qc-cmp-purposes-page-content .qc-cmp-sub-title,
.qc-cmp-purposes-page-content .qc-cmp-messaging,
.qc-cmp-ui .qc-cmp-table-header,
.qc-cmp-ui .qc-cmp-purpose-info,
.qc-cmp-ui .qc-cmp-messaging,
.qc-cmp-ui .qc-cmp-sub-title,
.qc-cmp-toggle-status,
.qc-cmp-company-cell,
.qc-cmp-ui .qc-cmp-vendor-list,
.qc-cmp-ui .qc-cmp-vendor-list-title,
.qc-cmp-ui .qc-cmp-alt-action,
.qc-cmp-alt-action, .qc-cmp-alt-action:not([href]):not([tabindex]) {
    color: var(--color-white) !important;
}

.single__breadcrumbs,
.single__content p>a,
.single__tags,
.single__meta,
img {
    color: var(--color-light-grey);
}

.navigation {
    border-top: 1px solid var(--color-white);
    border-bottom: 1px solid var(--color-white);
}

.single__content .anchor {
    border-top: 1px solid var(--color-white); 
}

.article--list.add-border:not(:last-of-type) {
    border-bottom: 1px solid var(--color-white);   
}

.qc-cmp-table-header,
.qc-cmp-table-row {
    border: 1px solid var(--color-white) !important; 
}

.qc-cmp-vendor-list tr {
    border: 1px solid var(--color-light-grey) !important; 
}
}
