@-moz-document domain("golem.de") {
@media (prefers-color-scheme: dark) {

    body {
        background: #222;
        color: #d7d7d7;
    }
    
    #header, #footer {
        background: unset;
    }
    
    #header {
        position: fixed;
        z-index: 1000;
        background-color: #000;
    }
    
    #gservices {
        padding-top: 80px;
        background-color: #131313;
    }

    #grandwrapper, #screen {
        border: 0px;
        background-color: #000;
    }
    
    #grandwrapper {
        padding-left: 10px;
        padding-right: 10px;
    }

    .head1, .head2, .head3, .head4 {
        color: #fff !important;
    }
    
    .head1 {
        color: #b1b1b1 !important;
    }
    
    .head2, .head3, .head4 {
        color: #e84d0e !important;
    }
       
    .icon-comments, .icon-video, .icon-audio {
        color: #b1b1b1 !important;
    }

    .list-articles li, .activity #large-forum li, .iqadlinetop, .forum #thread-list li, .forum #thread-detail li {
        border-top: solid 1px #5b5b5b;
    }
    
    .dli {
        border-top: solid 1px #5b5b5b !important;
    }
    
    .forum #thread-detail h3 strong, span.user {
        color: unset !important;    
    }
    
    #phorum div.information {
    border: unset !important;
    background-color: #584a47;
}
    
    a, .supplementary form.gservprg>input, aside form.gservprg>input {
        color: #e84d0e !important;
    }
    
    .golem-external-url {
        background: url("data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%20width%3D%2214%22%20height%3D%2214%22%20viewBox%3D%220%200%2024%2024%22%3E%3Cpath%20fill%3D%22%23e84d0e%22%20d%3D%22M14.016%203H21v6.984h-2.016V6.42L9.187%2016.22%207.78%2014.812l9.798-9.797h-3.563V3zm4.968%2015.984V12H21v6.984q0%20.797-.61%201.406t-1.405.61H5.015q-.843%200-1.43-.61T3%2018.984V5.014q0-.796.586-1.405T5.016%203H12v2.016H5.016v13.97h13.97z%22/%3E%3C/svg%3E") no-repeat 100% 60% transparent !important;
    }
    
    .toc__list, #comments, .threads {
        background: unset !important;
    }
    
    #comments, .threads {
        border: unset !important;
    }
    

    
    .g2 {
        display: none;
    }
    
    .g4 {
        width: 100%;
    }
    

}

    img {
        border-radius: 5px;
    }
}
