css:
@-moz-document domain("wprawo.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scollbar-color: #e33d3d #1c1c1c;
}
a,h1,h1,h3,h4{
    color: #e33d3d !important;
}

p,span,a:hover{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
html input[type="button"]{
    background: #1c1c1c !important;
    color: #e33d3d !important;
    border: 1px solid #e33d3d !important;
}
html input[type="button"]:hover{
    color: #1c1c1c !important;
}
input[type="text"],textarea,input[type="email"],input[type="password"]{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #e33d3d !important;
}
/*HEADER*/
/*HEADER*/
/*STRONA-GŁÓWNA*/
.post-summary{
    color: #fff;
}
.section-heading .h-text{
    background: transparent;
    color: #e33d3d !important;
}
.section-heading::after{
    background: #e33d3d;
    top: 0;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.term-badges.floated a{
    color: #fff !important;
    border: 1px solid #e33d3d;
}
.term-badges.floated a:hover,.widget .widget-heading > .h-text,.single-post-share .post-share-btn,.entry-terms.post-tags a,.entry-content.single-post-content blockquote::before{
    background: #1c1c1c !important;
    color: #e33d3d !important;
    border: 1px solid #e33d3d;
}
.single-post-share a.post-share-btn:hover,.entry-terms.post-tags a:hover{
    background: #e33d3d !important;
    color: #1c1c1c !important;
}
.widget .widget-heading::after{
    display: none;
}
.entry-content .wp-polls-ul label,.entry-content .wp-caption-text, .entry-content figcaption,.comment-list .comment-meta .comment-author{
    color: #e33d3d;
}
.section-heading::after,.entry-terms.post-tags .terms-label{
    background: #e33d3d !important;
}
.section-heading.multi-tab .bs-pretty-tabs-container{
    background: #1c1c1c;
    margin-top: 1px;
}
.section-heading .other-link .h-text{
    background: #1c1c1c !important;
    color: #e33d3d !important;
}
.bs-pretty-tabs-container .bs-pretty-tabs-elements{
    border: 0;
}
.section-heading .other-link{
    border: 1px solid #e33d3d;
}
.comment-respond .comment-submit{
    height: 50px;
}
.entry-content.single-post-content blockquote, .single-page-simple-content .entry-content blockquote{
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.archive-title .term-badges span.term-badge a,.pagination.bs-links-pagination a{
    background: #1c1c1c !important;
    color: #e33d3d !important;
    border: 1px solid #e33d3d !important;
}
.archive-title .term-badges span.term-badge a:hover,.pagination.bs-links-pagination a:hover{
    color: #1c1c1c !important;
}
/*PODSTRONY*/
/*FOOTER*/
.site-footer{
    background: #1c1c1c;
}
.site-footer .copy-1{
    color: #fff;
}
.site-footer .copy-1:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #e33d3d;
    cursor: none;
}
/*FOOTER*/
}

