@-moz-document domain(genk.vn) {
	/* your code goes here */
body, .genk-wrapper.detail .genk-body-wrapper {
    background: #000000;
}

#ContentPlaceHolder1_ContentPlaceHolder1_divNews{
background-color: #242424;
}
  .kscli, .knswli {
    padding: 25px 0;
    border-bottom: 1px solid #ed2e2e;
}
  .thtn-item-title a:hover {
    color: #f3ae7c;
}
.thtn-item-title a {
    font-size: 13px;
    line-height: 18px;
    color: #fff;
    transition: color .3s;
    -webkit-transition: color .3s;
    -moz-transition: color .3s;
    text-decoration: none;
    font-weight: 700;
}
  
  .krwli a {
    font-size: 13px;
    line-height: 18px;
    color: #e69872;
    text-decoration: none;
    position: relative;
}
  .wrapper-tin-hay-trong-ngay {
    background: #222222;
    width: 100%;
}
  
  .link-content-footer a {
    position: relative;
    text-decoration: none;
    font-size: 22px !important;
    font-family: Times New Roman;
    line-height: 32px;
    display: block;
    color: #feffff !important;
    margin-bottom: 40px;
    text-align: left;
}
  
  .VCSortableInPreviewMode[type="content"] {
    border: #000 solid 1px;
    padding: 10px;
    background-color: #424242;
    text-align: left;
    box-sizing: border-box;
}
  
.gfnp-title a {
    margin-top: 12px;
    display: block;
    font-family: SFD-Bold;
    text-decoration: none;
    font-size: 26px;
    line-height: 30px;
    color: #fff;
    text-decoration: none;
    transition: color .3s;
    -webkit-transition: color .3s;
    -moz-transition: color .3s;
}
  
  .klwfnswn-title a:hover, .gbwnpw-left .gbwnpw-title a:hover, .gbwnpw-right .gbwnpwr-tile a:hover, .gbwnpwrl-title:hover {
    color: #f7d6b9;
}
  
.kscliw-title a, .knswli-title a {
    font-family: SFD-Bold;
    font-size: 22px;
    line-height: 28px;
    color: #d2d2d2;
    text-decoration: none;
    display: block;
    margin-bottom: 10px;
    margin-top: -5px;
    transition: all .3s;
    -webkit-transition: all .3s;
    -moz-transition: all .3s;
}
  
  .kscliw-sapo, .knswli-sapo {
    font-size: 13px;
    font-family: Arial;
    color: #c7c7c7;
    line-height: 19px;
}
  
  
  .klwfnl-sapo {
    font-size: 14px;
    line-height: 20px;
    color: #b9b6b6;
    margin: 10px 0;
}
.khw-ads-wrapper {
    background: #333333;
    padding: 20px 0;
    width: 100%;
    display: block;
    text-align: center;
}
.kbwc-title {
    font-family: SFD-Bold;
    font-size: 34px;
    line-height: 38px;
    color: #ffffff;
}

.knc-sapo {
    font-family: SFD-Bold;
    font-size: 17px;
    line-height: 22px;
    display: block;
    margin-bottom: 12px;
    padding-top: 0;
    border-top: 0;
    font-weight: bold;
    position: relative;
    margin-top: 12px;
    color: #e0dcdc;
}

.knc-content p {
    font-size: 15px;
    line-height: 25px;
    color: #d2d2d2;
    font-family: "Segoe UI",Georgia,Serif;
    padding: 0;
    -webkit-font-smoothing: subpixel-antialiased;
    margin-bottom: 15px;
}


}
