css:
@-moz-document domain("bazos.cz") {
body, div {
    background-color: #000000;
    color: #ffffff;
}
body, td, div, p, a, span {
    color: #ffffff;
}
.nadpisnahlavni A, .nadpisnahlavni A:hover {
    color: #ffffff
}
.listah {
    background-color: #FF6600;
    margin: 3px;
    border: 1px solid #BF4C00;
    padding: 4px;
    border-radius: 3px;
}
input, select, textarea, button {
    background-color: #000000;
    color: #ffffff;
}
.pridati {
    background-color: #000000;
    border: 1px solid #E4C2AB;
}
.act > b {
    color: #ffffff;
}
.act > a {
    color: #ffffff;
}
.act {
    background-color: #000000;
    color: #ffffff;
}
.actkat {
    background-color: #000000;
    color: #ffffff;
}
.listainzerat {
    background-color: #FF6600;
    padding: 5px;
    border: 1px solid #BF4C00;
    border-radius: 3px;
}
button, input[type=submit] {
    padding-left: 7px;
    padding-right: 7px;
    background-color: #000000;
    color: #ffffff;
}
.flickity-button:hover {
    background: black;
    cursor: pointer;
}
.galleryobal:hover {
    background-color: #000000;
}
.act:hover {
    background-color: #000000;
    color:#ffffff;
    width:100%;
}
.act:hover a {
    color:#ffffff;
    /*word-spacing: initial;*/
    /*margin:initial;*/
}
.inzeraty:hover {
    background-color: #000000;
}
.bodyr {
    background-color: #000000;
}
.barevne {
    background-color: #FF6600;
    border: 0;
    border-radius: 3px;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
}
.ztop {
    color: #ffffff;
    font-weight: bold;
}
}
