css:
@-moz-document url-prefix("https://binarysearch.io") {
.roomlist,
body,
html {
    --bg-color: #141414;
    --fg-color: #473C3D;
    --tx-color: #ccc;
    --ac-color: #00ae86;
    --sh-color: #444;
    --hv-color: #555;
    background-color: var(--bg-color) !important;
    --block-text-color: var(--tx-color);
    --block-background-color: var(--fg-color);
    --block-accent-color: var(--ac-color);
    --block-shadow-color: var(--sh-color);
}

div.roomlist-header section.create-room-button,
div.roomlist-header section.create-room-button .block,
nav {
    background-color: var(--bg-color) !important;
    --block-text-color: var(--bg-color);
    --block-background-color: var(--fg-color);
    --block-accent-color: var(--ac-color);
    --block-shadow-color: var(--sh-color);
}

.friends .friends-list-wrapper .friends-list .friend .friend-left .friend-detail .subtitle,
nav a .friend,
nav a.block,
nav div.non-critical,
nav svg,
.dropdown .menu .menu-item {
    color: var(--tx-color);
}

.dropdown .menu .menu-item:hover,
.friends .friend:hover,
.groups-dropdown .group-select:hover,
.groups-dropdown .groups-dropdown-wrapper .option:hover,
div.notifications-list .notification:hover {
    background: var(--hv-color);
}

.groups-dropdown .groups-dropdown-wrapper {
    background: var(--fg-color);
}

.room-list .room-card {
    --block-text-color: var(--bg-color);
}

.room-list .room-card > div,
.roomlist,
body,
html {
    color: var(--tx-color)!important;
}

div.message a {
    color: #0ad3c6;
}

div.roomlist-header section.create-room-button,
div.roomlist-header section.create-room-button .block {
    color: var(--bg-color);
}

.groups-dropdown .groups-dropdown-wrapper .option .subtitle,
.room-center-top-bar span,
div.leaderboard-container,
div.leaderboard-container a,
div.message a.username,
div.notifications-list div.timestamp span {
    color: var(--tx-color);
}

.avatar-container .avatar .mask {
    background: #202020;
}

.avatar-container .avatar .mask .offline .offline-inside {
    background: var(--bg-color);
}

.block,
div.notifications-list,
div.profile div.info a.block,
div.question-top-bar div.question-top-bar-content div.tab {
    color: var(--tx-color);
    --block-text-color: var(--bg-color);
    --block-background-color: var(--fg-color);
    --block-accent-color: var(--ac-color);
    --block-shadow-color: var(--sh-color);
}

div.profile a,
div.profile p,
div.profile span {
    color: var(--tx-color)!important;
}

.history .room-sessions .content .session-item:hover {
    background: var(--hv-color);
}

.modal-container {
    background: rgb(18, 18, 18, .9);
}

.feedback-container .feedback .thankyou-feedback {
    color: var(--tx-color);
    background-color: #333;
}

form.settings .block-disabled input,
form.settings input {
    color: var(--tx-color);
    background-color: var(--fg-color);
}

.room-container .room .handle {
    background-color: var(--bg-color);
    border: 1px solid #eee;
}

div.question-top-bar div.question-top-bar-content div.tab {
    background: var(--bg-color);
}

.info-wrapper .info-content :not(pre) > code,
.info-wrapper .info-content code,
.info-wrapper .info-content pre {
    color: var(--tx-color);
    background-color: var(--fg-color);
}

div.room-center-top-bar {
    color: var(--bg-color);
    background-color: var(--bg-color);
}

.submissions .submission.active,
.submissions .submission:hover,
.submissions .submissions-tabs .submission-tab.active:hover {
    background: var(--hv-color);
}

.submissions .submissions-tabs .submission-tab:hover {
    background: #111;
}

.submissions .submissions-tabs .submission-tab.active {
    color: #fff;
    border-bottom: 3px solid #fff;
}

.console-wrapper .console .content .nothing-here,
.language-select .language-select__control .language-select__value-container .language-select__single-value,
.submissions .submission .details,
.submissions .submission .left .title .content span,
.submissions .submission .right span {
    color: var(--tx-color);
}

.react-calendar-heatmap .color-empty {
    fill: var(--tx-color);
}

#ace-editor .ace_gutter,
.editor {
    background: var(--bg-color);
}

.language-select .language-select__control,
.language-select .language-select__menu {
    background: var(--fg-color);
}

.language-select .language-select__control,
.language-select .language-select__menu div:hover {
    background: var(--hv-color);
}
}
