css:
@-moz-document url-prefix("https://landing.google.com/sre/sre-book") {
html{
    background: #001;
    color: #eee !important;    
}

.heading{
    color: #ddd;
}
.logo{

    border-bottom:none;
    box-shadow: 0 10px 20px -10px rgba(0,0,0,.5); 
}
.logo img{
        filter: hue-rotate(90deg) !important;
}
.header, .footer{
    background: #112 !important;
    border:none;
}

.header h2{
    color: #eee;
}

.content p{
    color: #bbb;
}

ul li, figcaption {
    color: #bbe;
}

.content .highlight{
	background: #112;
    color: #999;
}

.dt-heading{
    color: #bbb;
}

code{
    color: #6a6 !important;
}
code strong{
    color: #bbb;
}

.subheaders{
    color: #ddd;
}

.simplelist{
    color: #bbb;
}

#overlay-element{
    background: #223;
}

#overlay-element a {
        color: #ccc !important;
}

#overlay-element a:hover {
    background: #445;
    color:aaa;
}

#overlay-element li.active{
    background:#112;
}

::-webkit-scrollbar-thumb {
  background: #888 !important;
}
}
