css:
@-moz-document domain("thebeaverton.com") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #cd2027 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #cd2027 !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="email"],input[type="password"],textarea,input::placeholder{
    background-color: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #cd2027 !important;
}
/*HEADER*/
.header,.row,#mobile-menu,#quick_search .searchform,#full-menu .full-menu > li.menu-item-has-children.menu-item-mega-parent .thb_mega_menu_holder{
    background: #1c1c1c;
}
#content-container .click-capture{
    background: #000000a3;
}
.header .social-holder.style2 #quick_search{
    width: 75px;
}
/*HEADER*/
/*MAINPAGE*/
.mailing_list{
    background: #1c1c1c;
}
.post-links .post-link.share-article-loop::before{
    filter: invert();
}
.post .post-author{
    color: #fff;
}
/*MAINPAGE*/
/*ARTICLE*/
.sidebar .sidebar_inner,.post .post-gallery{
    background: #1c1c1c;
}
.post .article-tags,.post .post-author em{
    color: #fff;
}
/*ARTICLE*/
/*SUBPAGES*/
/*TAG*/
#archive-title{
    background: #1c1c1c !important;
}
/*TAG*/
/*SUBPAGES*/
/*FOOTER*/
#footer .row{
    background: #1c1c1c;
}
.copyright:before{
    content: "Dark theme by Maciejozaman | ";
    color: #cd2027;
    cursor: none;
}
/*FOOTER*/
}
