css:
@-moz-document url-prefix("https://pdicost.univ-orleans.fr/") {
:root {
    --bgc: #25272a;
    --header-bg: #44484e;
    --light-white: #DDDDDD;
    --light-black: #171717;
    --primary-blue: #3a7bb1;
    --secondary-blue: #136aff;
}

/* HEADER */

#header .aui-header {
    background-color: var(--header-bg);
}
.aui-header .aui-dropdown2-trigger, #header .aui-header .aui-nav-link, #header .aui-header .aui-nav-imagelink, #header .aui-header a {
    color: var(--light-white);
}

.aui-header .aui-quicksearch input[type='text'], .aui-header .aui-quicksearch input[type='text'][type='text']:focus {
    background-color: var(--primary-blue);
}

/* ALL */

body {
    color: var(--primary-blue);
}

h1, h2, h3, h4, .wiki-content h1, .wiki-content h2, .wiki-content h3, .wiki-content h4, .pagetitle, .steptitle, .substeptitle, .formtitle, table.confluenceTable td.confluenceTd.highlight, table.confluenceTable td.confluenceTd.highlight > p, table.confluenceTable th.confluenceTh, table.confluenceTable th.confluenceTh > p, .wiki-content .tablesorter-header-inner, table.admin th, .form-element-large, .form-element-small, #toolbar #format-dropdown .format-h1 a, #toolbar #format-dropdown .format-h2 a, #toolbar #format-dropdown .format-h3 a, #toolbar #format-dropdown .format-h4 a {
    color: var(--light-white);
}

.recently-updated-sidebar .update-item-meta {
    color: var(--light-white);
}

.aui-page-panel {
    background-color: var(--bgc);
}

/* ----------------------------------------- WIKI ----------------------------------------------- */ 

/* FOOTER */

#footer {
    background-color: var(--bgc);
}

#footer-logo, body #footer-logo, body.aui-page-fixed #footer-logo, body.aui-page-hybrid #footer-logo {
    background-color: var(--bgc);
}

#footer .footer-body, #footer .footer-body a {
    color: var(--light-white);
}

/* SIDE BAR */

.ia-fixed-sidebar, .acs-side-bar {
    background-color: var(--bgc);
}

.acs-tree-item, .acs-side-bar a, .acs-side-bar td a, #acs-nav-list-main .acs-nav-item-label, #acs-nav-list-quick .acs-nav-item-label, .acs-nav-list .acs-nav-item-label, .acs-nav-sections p, .acs-tree-item .node-title, .acs-side-bar-flyout h2 {
    color: var(--primary-blue);
}

.ia-secondary-header-title.wiki, .ia-secondary-currentPage-title .label {
    color: var(--light-white);
}

.acs-side-bar .current-item .acs-nav-item-link, .collapsed .quick-links-section:hover, .collapsed .advanced-links-section:hover, .collapsed .ia-secondary-header-title.wiki .parent-item>a:hover, .collapsed .ia-secondary-header-title.page-tree:hover, .acs-side-bar-flyout li.acs-tree-item a:hover, .acs-side-bar-flyout li.acs-tree-item a:focus, li.acs-nav-item .acs-nav-item-link:hover, li.acs-nav-item .acs-nav-item-link:focus {
    background-color: var(--header-bg);
}

/* MAIN PAGE */

.page-metadata, .page-metadata ul li a:link, .page-metadata ul li a:focus, .page-metadata ul li a:hover, .page-metadata ul li a:active, .page-metadata ul li a:visited {
    color: var(--light-white);
}

#title-text a {
    color: var(--secondary-blue);
}

.recently-updated-social .update-item-details {
    color: var(--light-white);
}

.aui-button.aui-button-subtle {
    color: var(--primary-blue);
}

/* DASHBOARD */ 

.content-header {
    background-color: var(--bgc);
}

.aui-sidebar .aui-sidebar-wrapper {
    background-color: var(--bgc);
}

small {
    color: var(--light-white);
}

.list-container a, .list-container a:visited {
    color: var(--primary-blue);
}

/* PEOPLE */

body.people #main-header {
    background-color: var(--bgc);
}

.aui-page-panel .aui-page-panel-nav {
    background-color: var(--bgc);
}

/* CALENDAR */

body.dashboard #main-header {
    background-color: var(--bgc);
}

div.plugin-calendar .sub-calendar-panel .sub-calendar-names .no-subcalendars-msg {
    color: var(--light-white);
}

/* SETTIGNS */

body.profile #main-header {
    background-color: var(--bgc);
}

.pagebody {
    background-color: var(--bgc);
}

.aui-button, a.aui-button, .aui-button:visited {
    color: var(--primary-blue);
}

.aui-navgroup-horizontal .aui-nav>li a, .aui-navgroup-horizontal .aui-nav>li a:link, .aui-navgroup-horizontal .aui-nav>li a:visited {
    color: var(--primary-blue);
}

.aui-navgroup-vertical .aui-nav>li>a, .aui-nav-vertical>li>a {
    color: var(--primary-blue);
}

.aui-nav-heading {
    color: var(--light-white);
}


form.aui legend, form.aui label {
    color: var(--light-white);
}

form.aui div.description {
    color: var(--light-white);
}


/* OTHER */

.stickable .stick, .stickable .stuck {
    background-color: var(--bgc);
}

.SearchDrawerPanel_drawer-panel-content__2uGQD {
    background-color: var(--bgc);
}

#recent-view-items-section a div div {
    color: var(--light-white);
}

#recent-space-items-section a div {
    color: var(--light-white);
}

body#com-atlassian-confluence {
    background-color: var(--bgc);
}

.userpassword-section h2, .login-section h2, .signup-section h2 {
    color: var(--light-white)
}

form.aui .checkbox>label, form.aui .radio>label {
    color: var(--primary-blue);
}



/* -------------------------------------------------------- GIT ------------------------------------------------------ */


/* DASHBOARD */ 


.repository-list-container .search-results-container .search-results-item a {
    color: var(--light-white);
}

.repository-list-container h4.title {
    color: var(--secondary-blue);
}

.repository-list-container .search-results-container .search-results-item a > .item-wrapper > .item-description {
    color: var(--primary-blue);
}

.dashboard-pull-requests-all-empty p {
    color: var(--primary-blue);
}

/* PROJECTS */

.aui-page-header {
    background-color: var(--bgc);
}

.bitbucket-theme #content table.aui > tbody > tr, .bitbucket-theme #content table.aui > tfoot > tr {
    background-color: var(--bgc);
}

.paged-table thead th {
    color: var(--light-white);
}

.paged-table tr.focused td {
    background-color: var(--light-white);
}

table.aui>tbody>tr, table.aui>tfoot>tr {
    color: var(--primary-blue);
}

/* PROJECTS SELECTED GROUP */

.filterable-entity-table .paged-table-filter {
    background-color: var(--bgc);
}

/* SPECIFIC PROJECT */

a:active, a {
    color: var(--primary-blue);
}

.filebrowser-table .directory-file-history.message a {
    color: var(--light-white);
}

.filebrowser-table td.timestamp {
    color: var(--light-white);
}

.filebrowser-table tr.focused-file .directory-file-history.message::after, .filebrowser-table tr:hover .directory-file-history.message::after {
    background-image: none;
}

.commits-table tbody tr:hover .message::after {
    background-image: none;
}

body.aui-page-focused, body.aui-page-notification {
    background-color: var(--bgc);
}

.commits-table .message::after {
    background-image: none;
}

.commits-table .message .message-subject .commit-link {
    color: var(--light-white);
}

.bitbucket-theme a.secondary-link {
    color: var(--primary-blue);
}

.bitbucket-theme #content table.aui > tbody > tr:hover {
    background-color: var(--head-bg);
}

tr.focused  td.message  span  a {
    color: var(--light-black)!important;
}

/* PEOPLE */ 

section#content {
    background-color: var(--bgc);
}

.entity-item > h1 ~ .user-detail  {
    color: var(--primary-blue);
}

/* SETTINGS */

.aui-navgroup~.aui-page-panel .aui-page-panel-nav, .aui-sidebar~.aui-page-panel .aui-page-panel-nav, .aui-page-header~.aui-page-panel .aui-page-panel-nav {
    background-color: var(--bgc);
}
}
