css:
@-moz-document domain("10fastfingers.com") {
inputfield, words {
    border-radius: 5px;
    font-family: monospace;
    z-index: 10000000;
    color: orange;
    border: 2px black;
}
words {
    border-radius: 1px;
}
body {
    background-color: black !important;
    background-image: none !important;
    background: none;
    color: orange;
}
div.row {
    background-image: none !important;
}
#speedtest-main #words {
    margin-left: auto !important;
    margin-right: auto !important;
}
.main-layout #main-content, .main-layout .main-content {
    background-color: black;
    border: 2px solid orange;
    width: 1036px;
    height: 900px;
}
@media(min-width: 1200px) {
    .col-lg-4 {
        width: 32%;
    }
}
body * {
    box-shadow: none;
}
h2 {
    color: orange;
}
span {
}
#row1:nth-child(2) {
    color: red !important;
}
.panel-success>.panel-heading {
    background-color: #e09100;
}
.panel-body {
    background-color: orange;
}
#table-data td {
    background-color: black;
}
a.list-group-item.active {
    background-color: #d98d01;
    border: 1px solid orange;
    border-right: 1px solid orange;
}
a.list-group-item.active:hover {
    background-color: black;
    border: 3px solid gray;
    border-right: 1px solid orange;
    color: #d98d01;
}
a.list-group-item {
    background-color: black;
    border: 1px solid orange;
    
}
#badge-box {
    background-color: black;
    border: 3px solid orange;
}
#inputfield {
    border: 3px solid orange !important;
    box-shadow: none;
    background: #ffe2ae;
}
#input-row {
    background-color: black !important;
    border: 2px solid orange;
}
#participation {
    background-color: orange;
}
#activity-module div.list-group-item {
    background-color: black !important;
    border: 2px solid orange;
}
.top-first-bg {
    background: none !important;
    background-color: black !important;
}
#auswertung-result {
    font-family: monospace;
    font-size: 10px !important;
    border: 5px solid black;
    box-shadow: none !important;
    border-radius: 10px !important;
    background-color: green;
    margin-left: auto !important;
    margin-right: auto !important;
    position: relative !important;
    width: min-content !important
}
.ads {
    display: none;
}
.value {
    font-size: 27.5px !important;
}
.name {
    background-color: orange;
}
.top-first-bg {
    border-bottom: 10px solid orange !important;
}
.value {
    background-color: darkorange !important;
}
#table-data {
    background-color: #202020 !important;
    color: orange;
    border: 10px solid black;
    border-collapse: collapse !important;
}
#content-bg {
    background: none;
    background-color: orange !important;
}
#main-content-trenner {
    background: none;
    background-color: gold !important;
}
@media(min-width: 1000px) {
    .col-md-6 {
        width: 49%;
        margin-left: 5px;
    }
}
td {
    background-color: orange;
}
.nav-tabs {
    border-bottom: 3px solid #d97700;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    background-color: orange;
    border: 2.5px solid orange;
}
.nav-tabs>li>a {
    background-color: #d97700;
}
#table, #profile-data-table {
    background-color: darkorange;
}
#wpm {
    background-color: black;
    border-color: black;
    border: 3px solid green;
}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
    background-color: orange;
}
#speedtest-main #auswertung-box #auswertung-result h3 {
    background-color: #d79620 !important;
    background: none;
}
#speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score div, #speedtest-main #auswertung-box #auswertung-result #result-table td#wpm #top-score {
    display: none;
}
#reload-btn {
    background-color: gray !important;
    border: 2px solid white !important;
}
#speedtest-main #reload-btn:hover {
    background-color: orange !important;
}
#reload-btn:hover .glyphicon-refresh:before {
    background-color: orange !important;
}
.glyphicon-refresh {
    background-color: gray !important;
}
#words .highlight-wrong {
    color: black;
    background-color: #FF3000;
    border-radius: 10px;
    opacity: 1;
}
#words .highlight-correct {
	color: black !important;
    opacity: 1;
}
#words {
    background-color: #D36E00 !important;
    border: 3px solid orange !important;
}
.highlight {
    border-radius: 10px !important;
}
#words .highlight {
    color: black !important;
    background-color: white;  
}
#timer {
	background-color: #23272A !important;
    color: #23272A;
}
}
