css:
@-moz-document domain("anandtech.com") {
.header {
	width: auto;
}
html body section.content{
    min-width: 1001px;
    width: 100%;
}
html body section.content section.main_cont{
    min-width: 680px;
    width: calc(100% - 340px);
    margin: 0 10px;
}
html body section.content section.main_cont section.main_cont{
    min-width: 680px;
    width: auto;
}
html body section.content section.main_cont div.featured_banner{
    margin: 0 auto;
}
html body section.content section.main_cont div.sponsored_add.fl-lt.hide_resp2,
html body section.content section.main_cont div.sponsored_add.fl-rt.hide_resp2{
    min-width: 330px;
    width: calc(50% - 20px);
}
html body section.content section.main_cont div a{
    margin: 0 auto;
}
html body section.content section.main_cont div.cont_box1.l_ div.cont_box1_txt,
html body section.content section.main_cont section.main_cont div.cont_box1 div.cont_box1_txt{
    min-width: 360px;
    width: calc(100% - 320px);
}
html body section.content section.main_cont div.review_blog div.blog_top_left{
    width: calc(100% - 80px);
}
html body section.content section.main_cont div.breadcrumb_area.hide_resp2 nav.breadcrumb{
    width: 100%;
}

/* Deals */
html body section.content section.main_cont div.sidepadding.review div.articleContent p{
    margin-top: 16px;
}

/* bench */
html body section.content section.main_cont div.cpu_bechmark_area{
    width: 680px;
    margin: 0 auto;
}
html body section.content section.main_cont div#benches{
    width: 680px;
}

@media screen and (max-width: 1023px) {
    html body section.content{
        min-width: 670px;
        width: 100%;
    }
    html body section.content section.main_cont{
        min-width: 680px;
        width: auto;
        margin: 0 30px;
    }
}
@media screen and (max-width: 767px) {
    html body section.content{
        min-width: 0px;
        width: 100%;
    }
    html body section.content section.main_cont{
        min-width: 0px;
        width: auto;
        margin: auto;
    }
}

}
