css:
@-moz-document url-prefix("https://praktikum.yandex.ru/") {
/* Home Page*/ 

button {
  background: rgb(0,102,223) !important
}

/* Sidebar */

.sidebar__link {
  height: 25px;
}
.sidebar__content {
  color: rgb(111,130,132);
  background: #022c47 !important;
}

.sidebar__skills_expanded .sidebar__skills-items {
  background: rgb(0,20,37) !important
}

/* Header */
.header {
  color: rgb(0,102,223) !important
}
/* ===== Page ===== */

.current-lesson__description {
  color: rgb(0,102,223);
  background: rgb(0,36,65) !important
}

.page__content {
  color: rgb(0,102,223);
  background: rgb(2,32,41)
}

.course-card {
  color: rgb(0,102,223);
  background: #022c47 !important;
}

.current-lesson__your-lesson, .program__courses-header, .course-card__info-item, .course-card__info-item_active {
  color: rgb(0,102,223) !important
}

/* ===== Footer ===== */
.footer__section, .footer__org {
  color: rgb(111,130,132) !important
}

/* Course popup*/ 
.course-modal-content {
  color: rgb(0,102,223) !important;
  background: #022c47 !important;
}

.course-modal-content .tab__text, .course-description__content, .course-topics, .course-navigation, .course-topic__lesson-title {
  color: rgb(0,102,223) !important;
}


/* Theory */
.trainer-navigation__content {
  background: #022c47 !important;
  border-right: 1px solid rgb(0, 18, 33) !important
}

.trainer__lesson_type_theory, .quiz__option {
  color: rgb(0,102,223) !important;
  background: #022c47 !important;
}

.big-theory, .quiz__content, .paragraph {
    color: rgb(0,102,223) !important;
}


/* Trainer page*/ 

/* ===== Page ===== */
.task-message, .trainer-editor__tabs-header, .tab_active, .trainer-tasks-panel, .task-description, .trainer-footer__actions, .theory-panel__theory-viewer, .markdown_type_theory {
  color: rgb(0,102,223) !important;
  background: #022c47 !important;
}

.CodeMirror, .CodeMirror-gutter, .code-block {
  background: rgb(27, 89, 156) !important;
}

}
