css:
@-moz-document domain(youtube.com) {
/* FUCKYOU LOGO CONTAINER */
.exp-invert-logo.inverted-hdpi #header:before,
.exp-invert-logo.inverted-hdpi .ypc-join-family-header .logo,
.exp-invert-logo.inverted-hdpi #footer-logo .footer-logo-icon,
.exp-invert-logo.inverted-hdpi #yt-masthead #logo-container .logo,
.exp-invert-logo.inverted-hdpi #masthead #logo-container,
.exp-invert-logo.inverted-hdpi .admin-masthead-logo a,
.exp-invert-logo.inverted-hdpi #yt-sidebar-styleguide-logo #logo {
    background: no-repeat url(https://i.imgur.com/bdtF8fU.png);
    background-size: 120px 35px;
    height: 35px;
    width: 120px
}

/* DARK MODE CODE */
f:root {
    --dgyt-bg-color-dark: rgb(15, 15, 15);
    --dgyt-bg-color-medium: rgb(33, 33, 33);
    --dgyt-cell-bg-color: rgba(0, 0, 0, 0.2);
    --dgyt-menu-bg-color: rgba(21, 21, 21, 0.8);
    --dgyt-button-color: rgba(255, 255, 255, 0.1);
    --dgyt-button-color-hover: rgba(255, 255, 255, 0.22);
    --dgyt-border-color: rgba(0, 0, 0, 0.2);
    --dgyt-text-main: rgb(192, 192, 192);
    --dgyt-text-secondary: rgb(142, 142, 142);
    --dgyt-text-highlight: rgb(255, 255, 255);
    --dgyt-color-red: rgb(204, 24, 30);
    --dgyt-color-blue: rgb(22, 122, 198);
    --dgyt-color-yellow: rgb(245, 213, 98);
    --dgyt-color-orange: rgb(255, 85, 0);
    --dgyt-color-purple: rgb(156, 39, 176);
    --dgyt-color-green: rgb(76, 175, 80);
}

.yt-uix-button,
.yt-uix-button:hover,
.guide-item,
.guide-item:hover {
    transition: background-color 0.3s!important;
}

.guide-flyout-trigger.on-hover,
.guide-item:hover,
.guide-item:hover .yt-deemphasized-text,
.guide-collapsible-item .yt-uix-expander-head:hover {
    background-color: rgba(255, 255, 255, 0.22)!important;
    color: rgb(255, 255, 255)!important;
}

::-moz-selection {
    background-color: rgb(22, 122, 198)!important;
    color: rgb(255, 255, 255)!important;
}

::selection {
    background-color: rgb(22, 122, 198)!important;
    color: rgb(255, 255, 255)!important;
}

body {
    color: rgb(192, 192, 192)!important;
}

#body-container {
    background: rgb(15, 15, 15)!important;
}

a,
a:hover {
    color: rgb(22, 122, 198);
}

h1,
h2,
h3,
h4,
.playlist-videos-list .yt-ui-ellipsis,
.yt-ui-ellipsis,
.yt-lockup,
.branded-page-module-title,
.branded-page-module-title a,
.yt-thumb {
    background-color: transparent!important;
}

body.static-yt-logo #yt-masthead .doodle-removed #logo {
    background-position: 0!important;
}

.yt-lockup-dismissable .watched-badge {
    content: " \1F441"!important;
    color: rgb(255, 255, 255)!important;
    opacity: 0.6!important;
    padding: 0!important;
    font-size: 2em!important;
    background-color: transparent!important;
    height: 100%!important;
    width: 100%!important;
    text-align: center!important;
    line-height: 100px!important;
    text-shadow: 0 0 5px rgb(15, 15, 15)!important;
    top: 0!important;
    left: 0!important;
    transition: opacity 0.24s ease-in-out!important;
}

.yt-lockup-dismissable .watched-badge:hover {
    opacity: 0!important;
    transition: opacity 0.24s ease-in-out!important;
}

.yt-lockup-dismissable .watched .video-thumb {
    -webkit-filter: blur(2px)!important;
    filter: blur(2px)!important;
    transition: 0.48s!important;
}

.yt-lockup-dismissable .watched:hover .video-thumb {
    -webkit-filter: blur(0)!important;
    filter: blur(0)!important;
    transition: 0.48s!important;
}

.yt-card {
    background-color: rgb(33, 33, 33)!important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2)!important;
}

.yt-uix-button-text,
.branded-page-module-title,
.branded-page-module-title-text,
.branded-page-module-title a,
.view-count,
.yt-dialog-fg-content,
.yt-uix-overlay-fg-content,
.yt-dialog-fg-content .yt-dialog-title,
.yt-uix-overlay-fg-content .yt-dialog-title {
