css:
@-moz-document domain("www.worldometers.info") {
:root
{
    --dark-background: #212121;
    --dark-brighter: #424242;
    --dark-color: #e0e0e0;
    --dark-opened-item: #616161;
    --dark-border-radius: 15px;
    --dark-theme-color: #c62828;
}

body,
.content-inner,
.navbar-default,
.label-counter,
.panel,
footer,
footer > *,
th,
.panel-default > .panel-heading,
.nav-tabs > li.active > a,
.tabbable-line-cases > .tab-content,
.tabbable-line-deaths > .tab-content,
.EmbeddedTweet,
[style*="Fafafa"],
[style*="fafafa"],
[style*="efefef"],
.modal-content,
.selectize-input,
table,
.table
{
    background: var(--dark-background) !important;
    background-color: var(--dark-background) !important;
    color: var(--dark-color) !important;
}

[style*="F0F0F0"],
.selectize-control.single .selectize-input.input-active
{
    background: grey !important;
    color: var(--dark-color);
}

.col-md-4,
[id*="google"]
{
    display: none;
}

.panel-default > .panel-heading,
.nav-tabs > li.active > a:hover,
.nav-tabs > li > a:hover,
tbody > tr:hover,
.form-control,
.blockquote,
[style*="F3F3F3"],
[style*="FCF8F8"],
[style*="efefef"],
[style*="FBFBFB"],
[bgcolor*="FCF8F8"],
[bgcolor*="F0F0F0"],
[bgcolor*="F2F2F2"],
[bgcolor*="F8F8F8"],
[bgcolor*="F9F9F9"],
blockquote,
button,
.inactive-header,
.inactive-header > *,
.active-header,
.counter-body > *,
.counter-header,
.counter-header > *,
.counter-body
{
    background: var(--dark-brighter) !important;
    background-color: var(--dark-brighter) !important;
    color: var(--dark-color) !important;
    border-left: 0 !important;
}

rect
{
    fill: var(--dark-brighter) !important;
    color: var(--dark-color) !important;
}

.highcharts-title,
.highcharts-subtitle,
.highcharts-axis-title,
.highcharts-yaxis-labels > text,
.highcharts-xaxis-labels > text,
.highcharts-legend-item > text,
.highcharts-point
{
    fill: var(--dark-color) !important;
}

[style*="FFEEAA"],
[style*="FFFF00"],
.total_row_world > *
{
    background-color: darkgoldenrod !important;
}

.sorting:after
{
    opacity: 0.6 !important;
}

.maincounter-number
{
    color: red;
}

[style*="EAF7D5"]
{
    background-color: darkgreen !important;
}

.tooltip:active
{
    background-color: black !important;
}
}
