css:
@-moz-document domain("devblogs.microsoft.com") {
body.custom-background {
    background-color: #3C3C3C;
}
body {
    color: #f4f4f4;
}

a {
    color: CornflowerBlue;
}

/* Menu bar */
.c-uhfh > div:first-child {
    background-color: black;
}

.c-uhfh > [class*="theme-"] .c-action-trigger,
.c-uhfh > [class*="theme-"] .c-logo,
.c-uhfh > [class*="theme-"] .c-uhf-nav-link,
.c-uhfh > [class*="theme-"] .c-uhf-menu a,
.c-uhfh > [class*="theme-"] .c-uhf-menu button,
.c-uhfh > [class*="theme-"] .c-me:not(.msame_open) .msame_Header_name {
    color: #f4f4f4;
}

/* First level menu */
.c-uhfh > div:first-child .c-uhf-nav-link {
    color: #f4f4f4;
    background-color: black;
}

.c-uhfh > div:first-child .c-uhf-menu > button:hover,
.c-uhfh > div:first-child .c-uhf-menu > button:focus {
    background: black;
}

/* Second level menu */
.c-uhfh > div:first-child .c-uhf-menu a,
.c-uhfh > div:first-child .c-uhf-menu button {
    color: white;
    background-color: black;
}

.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > a:hover,
.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > a:focus,
.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > button:hover,
.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > button:focus,
.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > [aria-expanded="true"],
.c-uhfh > [class*="theme-"] .c-uhf-menu > ul > li > ul {
    background-color: black;
}

/* Articles */
.landing-main,
.entry-area,
.postcontent {
    background: black;
}

.land-desc {
    color: #f4f4f4;
}

.inner-border {
    box-shadow: inset 0px 0px;
    -webkit-box-shadow: inset 0px 0px;
    -moz-box-shadow: inset 0px 0px;
}

blockquote {
    background-color: #3C3C3C;
}

/* Code sample */
pre {
    background-color: #f4f4f4;
}

/* Table */
.table-responsive {
    color: black;
}

/* Pagination */
.post-tags {
    color: LightSkyBlue;
    background-color: #3C3C3C;
}

.page-item .page-link.prev,
.page-item .page-link.next,
.pagination-lg .page-item .page-link.prev,
.pagination-lg .page-item .page-link.next {
    color: #f4f4f4;
}

.page-link {
    color: CornflowerBlue;
}

/* Links boxes */
.box-style,
.card {
    background-color: black;
}

.card-title {
    color: #f4f4f4 !important;
}
.card a:link,
.card a:visited {
    color: #f4f4f4;
}

/* Bottom cards */
.related-articles article .post-card {
    background-color: black;
}
.related-articles article .post-card .post-desc,
.related-articles article .post-card .post-desc em,
.author-details .post-date {
    color: #f4f4f4;
}

/* Footer */
a.stayinformedsite,
a.stayinformed {
    color: #f4f4f4;
}

.c-uhff-base,
.c-uhff {
    background: transparent;
}

.c-uhff-base ul.c-list,
.c-uhff-base > a.c-uhff-link.c-uhff-lang-selector,
.c-uhff-nav .c-heading-4,
.c-uhff-nav a.c-uhff-link {
    color: #f4f4f4;
}

.cattagsarea {
    background-color: black;
}

/* Comments */
.commentlist li ul.children,
article.comment header,
article.comment .comment-content,
.commentlist li footer {
    background-color: black;
}

ul li.comment,
.comment-date{
    color: #f4f4f4;
}
}
