css:
@-moz-document url-prefix("https://jbpress.ismedia.jp/") {
  html body div.l-wrap div.l-contents {
      min-width: 1000px;
      width: calc(100% - 100px);
      margin: 0px 10px;
  }

  /* top_page*/
  html body div.l-wrap div.l-contents div.l-main {
      margin: 0px 0px;
      min-width: 650px;
      width: calc(100% - 320px);
  }
  html body div.l-wrap div.l-contents div.l-main section.m-latest div.m-latest-list a.m-latest-list-item {
      margin: 0 auto;
  }
  .l-inner {
      /* margin: 0px 10px;*/
      min-width: 1000px;
      width: calc(100% - 100px);
      margin-left: 10px;
  }
  .top-feature-main,
  .top-feature-vlist {
      width: calc(50% - 2px);
      flex-basis: unset;
  }
  .top-feature-slist {
      width: 100%;
      flex-basis: unset;
      z-index: 1;
  }
  .top-feature-slist-item {
      width: calc(33% - 5px);
  }
  .top-feature-slist-item__info {
      flex-basis: unset;
      width: calc(100% - 100px);
      padding: 10px 12px;
  }
  .m-latest-list-item {
      flex-basis: unset;
      width: calc(50% - 2px);
      margin: 0 auto;
  }
  .m-latest-list-item:nth-child(2n+1) {
      margin: 0 auto;
  }
  .m-latest-list-item__img {
      min-width: unset;
      flex-basis: unset;
      max-width: 350px !important;
      margin: 0 auto;
  }
  .m-latest-list-item__img > img {
      height: 170px !important;
      object-fit: cover !important;
  }
  html body div.l-wrap div.sns-fixed.js-sns-fixed {
      position: fixed;
      transform: unset;
      -webkit-transform: unset;
      margin: 0px;
      left: calc(100% - 70px);
  }

  @media screen and (max-width: 1100px) {
      html body div.l-wrap div.sns-fixed.js-sns-fixed {
          position: fixed;
          left: 1014px;
      }
  }
  /* articles*/
  html body div.l-wrap div.l-contents main {
      min-width: 700px;
      width: calc(100% - 320px);
  }
  html body div.l-wrap div.l-contents main article.article div.article-body {
      padding: 20px 20px;
  }
  .m-article-3colm-list {
      margin-right: 10px;
  }
  .m-article-3colm-unit {
      flex-basis: unset !important;
      width: calc(33% - 5px) !important;
      margin: 24px 2px 0 !important;
  }
  .m-article-3colm-unit__img {
      flex-basis: unset !important;
      min-width: unset !important;
  }
  .m-article-3colm-unit:nth-child(3n-1) {
      margin: 24px 2px 0 !important;
  }
  @media screen and (max-width: 1028px) {
      /* hide sidebar */
      .l-wrap {
          min-width: unset !important;
      }
      .l-contents {
          min-width: unset !important;
          width: 100% !important;
      }
      .header-nav-unit__ttl > a,
      .header-nav-unit__ttl > span {
          padding: 0;
      }
      .header-nav-unit {
          width: calc(100% / 10);
          font-size: 1.5vw;
      }
      .l-inner {
          min-width: 842px !important;
          width: unset !important;
      }
      .top-feature-main-item__img {
          min-width: unset;
      }
      .l-sub {
          display: none;
      }
      .l-main {
          width: 100% !important;
      }
      .m-latest-list-item {
          flex-basis: unset;
          width: calc(33% - 2px);
      }
      .m-latest-list-item__img {
          min-width: unset;
          flex-basis: unset;
      }
      /* .l-main.--wid {
          width: 100% !important;
          margin-right: 0;
      }*/
  }
}
