css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org"), domain("craigslist.co.uk") {

body {
background:#4C5844 url(http://farm3.static.flickr.com/2190/2652054311_b71af184d8.jpg?v=0) !important;
color:#CFCBC2 !important;
background-position:center top !important;
background-repeat:repeat !important;
}

table{
background-color:#2A2A2A !important;
color:#6EBCFF !important;
width:100% !important;
}

.wwl td a, #leftbar, #postlks li, #leftbar li {
background-color:#2A2A2A!important;
background-repeat:no-repeat!important;
border:1px solid #44617A!important;
color:#6EBCFF!important;
font-size:10px!important;
font-weight:bold!important;
padding:5px 7px 5px 14px!important;
}

.www td a, table.city, .hp a, #leftbar td{
background:#333!important;
background-repeat:no-repeat!important;
color: #6ebcff !important;
-moz-border-radius:3px !important
}

.www td a:hover, .hp a:hover {
background:#3B3B37 !important;
cursor:pointer !important;
}

.www b , .city b, .ban, #topban div, #logo{
background:#2A2A2A!important;
border:1px solid #8BC53F!important;
color:#8BC53F!important;
cursor:pointer!important;
font-weight:bold!important;
-moz-border-radius:3px !important
}

blockquote{
background-color:#2A2A2A !important;
color:#6EBCFF !important;

margin:0px !important;
}

a {
color:#6EBCFF !important;
text-align:center !important;
}

.bchead {
background:#000 !important;
border-color:#8BC53F !important;
}

.toc h4, #search div {
background:#1a1a1a!important;
border:1px solid #8BC53F!important;
color:#8BC53F!important;
font-weight:bold!important;
-moz-border-radius:3px !important
}

.hp div.col ul li, .hp .city h5 {
background-color:#2A2A2A!important;
background-image: none;
background-repeat: repeat;
background-attachment: scroll;
background-position: 0% 0%;
padding-top: 1px;
padding-right: 1px;
padding-bottom: 1px;
padding-left: 1px;
border-bottom-width: 1px;
border-bottom-style: solid;
text-align: center
}
.hp div.col h4 {
background:#333!important;
background-image: none!important;
background-repeat: repeat!important;
background-attachment: scroll !important;
background-position: 0% 0%!important;
font-size: 85%!important;
text-align: center !important;
font-weight: 600!important;
white-space: nowrap!important;
}

}
