css:
@-moz-document domain(www.vultr.com) {

.nav--h .nav__dropdown,.dropdown__menu,.resources__navbar-inner ,body {
	background-color:black;
	color:#0f0;
}
pre {
	filter:invert();
}
.code-title, .code-title h6 {
	background-color:black;
	color:#fd4400;
}
.nav__link,.resources__navbar .breadcrumb__link:not(:hover) {
	color:#ffaf0a;
}
.pt__cell,.nav__link:hover,.fill {
    color:#0f0;
}
.label, .label--default,.input-group {
	filter:invert();
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
	color:#7300ff;
}
a, p,.scroll-up .site-navbar .nav > .nav__item > .nav__link {
	color:#099d8b;
}
.list-group__item ,.article b, .article strong {
	color:#ff0080;
}
.resources__navbar .breadcrumb__item.is-active .breadcrumb__link,.table thead td, .table thead th {
	color:#aa1d1d;
}
.nav__link:hover {
	filter:invert();
	transition-duration:2s;
}
.section--dark-md::after,.section--dark-md::before ,.banner__background {
	background-image:none;
}
.locations-map__popover,.application__hover-icon,.box__content,.footer, .locations-map__info,.tile,.scroll-up .site-navbar,.feature--boxed {
	background-color:#000;
	border:solid 1px green;
}
.svg-banner-shape {
	visibility:hidden;
}
.svg-illustration--map .fill-gray-darker {
	fill :red;
}
.site-navbar--docs, .resources__sidebar, .widget, .footer, .banner:not(.banner--shape) {
	background:black;
}
.nav--boxed .nav__item-desc {
	color:red;
}
.footer__nav .nav__link {
    color:yellow;
}
.site-navbar .navbar__menu {
	background-color:black;
}
.resources__sidebar {
	width:auto;
}
.section--dark-md {
	background:none black;
}


}
