css:
/**
 * Wb Aero (Glasser)
 * Menu and Navigator Toolbars
 * by wifflebunz http://userstyles.org/users/23718
 *
 * This contains just the basics (url bar & menu bar).
 * You can add other components like the Bookmarks Toolbar, 
 * AIOS toolbar, DownloadThemAll! window, etc., by adding 
 * my other styles, or your own, or some other styles, or 
 * whatever.
 * So you can mix and match whatever you want.
 *
 * Updates:
 *  [6/10/2009]
 *  - A bunch of fixes for 3.5b99 (the "Firefox 3.5 Preview")
 *  - The searchbar dropmarker now glows orange when a new
 *    search engine is discovered
 *  - Removed all the "flair" from the urlbar favicon
 *  - New style for urlbar on secure sites, mostly mimics IE8
 *  - Got rid of the excess throbber code, only throbbers in
 *    the nav toolbar are changed
 *  [4/24/2009]
 *  - updated the search bar buttons
 */


@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://browser/content/browser.xul") {

#main-window #navigator-toolbox
{
-moz-appearance:none !important;
border:0 none !important;
padding:0 !important;
margin:0 !important;
}

#main-window #navigator-toolbox toolbar
{
-moz-appearance:toolbox !important;
border:0 none !important;
padding:0 !important;
margin:0 !important;
}

#main-window #navigator-toolbox #toolbar-menubar,
#main-window #navigator-toolbox #toolbar-menubar menubar,
#main-window #navigator-toolbox #nav-bar
{
-moz-appearance:none !important;
}

#main-window #nav-bar,
#main-window #nav-bar[iconsize="small"],
#main-window #nav-bar[iconsize="large"]
{
padding: 0 0 4px !important;
margin: 0 !important;
}

#main-window #navigator-toolbox #toolbar-menubar
{
opacity:0.99999 !important;
padding:0 !important;
margin:0 !important;
}

#main-window #navigator-toolbox
{
background-color:black !important;
}

#main-window #urlbar #identity-box
{
margin-left: -1px !important;
border: 0 !important;
border-left: 1px solid transparent !important;;
border-right: 2px solid transparent !important;
list-style-image: none !important;
background: none !important;
padding-left: 0 !important;
-moz-border-radius:0 !important;
-moz-border-radius-bottomleft:0 !important;
-moz-background-clip:padding !important;
-moz-box-shadow: none !important;
outline: 0 !important;
}

#main-window #urlbar[level="high"] #identity-box:hover,
#main-window #urlbar[level="high"] #identity-box:active
{
-moz-background-clip: border !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAWCAIAAACOpGH9AAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEVJREFUeNpi+PvvP8Plt79R8EUoPvf6N8OpV78Yjr38xbDo1nemX/8YmBgYGJh+/f3P9BvI/v3vP9Pf/xAxJAzU9wsgwAAyRCx53oDDJgAAAABJRU5ErkJggg==") !important;
background-position: 0 0 !important;
background-color: #89D0F5 !important;
-moz-border-left-colors: rgba(255,255,255,0.75) !important;
-moz-border-right-colors: rgba(60,127,177,0.99) rgba(255,255,255,0.75) !important;
}

#main-window #urlbar #identity-box label
{
outline: 0 !important;
color: #111 !important;
opacity: .65 !important;
padding-left: 0 !important;
margin:0 !important;
font-style: normal !important;
font-weight: normal !important;
}

#main-window #urlbar[level="high"] #identity-box:hover label,
#main-window #urlbar[level="high"] #identity-box:active label
{
opacity: 1 !important;
color: #000 !important;
}

#page-proxy-stack
{
background-image: none !important;
}

#main-window #urlbar .autocomplete-textbox-container
{

background-color: transparent !important
}

#main-window #searchbar .searchbar-textbox,
#main-window #urlbar
{
-moz-background-clip: padding !important;
background-color: rgba(255,255,255,0.75) !important;
opacity: 0.99999 !important;
border: 3px solid !important;
-moz-border-radius: 3px !important;
-moz-border-top-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(255,255,255,0.95) !important;
-moz-border-right-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(255,255,255,0.95) !important;
-moz-border-bottom-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(255,255,255,0.95) !important;
-moz-border-left-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(255,255,255,0.95) !important;
-moz-appearance: none !important;
}

#main-window #navigator-toolbox #searchbar .searchbar-textbox,
#main-window #navigator-toolbox #urlbar
{
height: 26px;
}

#main-window #urlbar[level="high"]
{
background-color: rgba(166,234,159,.8) !important;
-moz-border-top-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(45, 175, 23, 0.4) !important;
-moz-border-right-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(45, 175, 23, 0.4) !important;
-moz-border-bottom-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(45, 175, 23, 0.4) !important;
-moz-border-left-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(45, 175, 23, 0.4) !important;
}

#main-window #searchbar .searchbar-textbox[focused="true"],
#main-window #urlbar[focused="true"],
#main-window #urlbar[level="high"][focused="true"]
{
background-color: rgba(255,255,255,1) !important;
-moz-border-top-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(255,255,255,0.99) !important;
-moz-border-right-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(255,255,255,0.99) !important;
-moz-border-bottom-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(255,255,255,0.99) !important;
-moz-border-left-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(255,255,255,0.99) !important;
}

#main-window #searchbar .searchbar-textbox:hover:not([focused="true"]),
#main-window #urlbar:hover:not([focused="true"])
{
background-color: rgba(255,255,255,0.90) !important;
}

#main-window #urlbar[level="high"]:hover:not([focused="true"])
{
background-color: rgba(166,234,159,.7) !important;
-moz-border-top-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(45, 175, 23, 0.4) !important;
-moz-border-right-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(45, 175, 23, 0.4) !important;
-moz-border-bottom-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) rgba(45, 175, 23, 0.4) !important;
-moz-border-left-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) rgba(45, 175, 23, 0.4) !important;
}

#main-window #urlbar
{
border-right:none !important;
margin-left:4px !important;
-moz-border-radius-topright:0 !important;
-moz-border-radius-bottomright:0 !important;
}

/**
 * URL Bar dropmarker
 */

#urlbar .dropmarker-icon
{
display:none !important;
}

#urlbar > dropmarker
{
width:17px !important;
height: 22px !important;
border-right:2px solid !important;
border-left:2px solid !important;
-moz-border-left-colors:rgba(0,0,0,0) !important;
-moz-border-right-colors:rgba(0,0,0,0) !important;
background-position: center -1px !important;
background-repeat: no-repeat !important;
background-color: transparent !important;
}

#urlbar > dropmarker,
#urlbar[focused="true"] > dropmarker,
#urlbar[level="high"] > dropmarker
{
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAWCAYAAAAfD8YZAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEBJREFUeNpi/P//PwO5gImBAjCqeWRoZkEXYGRkxJnkgKmREa/N6ArwiTMRoxCXgUyEnIhLI9iLo7lqRGgGCDAAUD8cG686EegAAAAASUVORK5CYII=") !important;
background-color: transparent !important;
}

#urlbar > dropmarker:hover,
#urlbar > dropmarker:hover:active,
#urlbar[level="high"] > dropmarker:hover:active
{
margin-top:-1px !important;
margin-bottom:-1px !important;
}

#urlbar > dropmarker:hover,
#urlbar[level="high"] > dropmarker:hover
{
background-image: url(data:image/png;base64,) !important;
background-position: -6px 0 !important;
background-color: transparent !important;
-moz-image-region:auto !important;
-moz-border-left-colors:rgba(60,127,177,0.99) rgba(255,255,255,0.75) !important;
-moz-border-right-colors:rgba(60,127,177,0.99) rgba(255,255,255,0.75) !important;
}

#urlbar > dropmarker:hover:active,
#urlbar[level="high"] > dropmarker:hover:active
{
background-image: url(data:image/png;base64,) !important;
background-position: -6px 0 !important;
background-repeat: no-repeat !important;
-moz-image-region:auto !important;
margin-right:0 !important;
margin-left:0 !important;
border-right:1px solid !important;
border-left:2px solid !important;
-moz-border-left-colors:rgba(60,127,177,0.99) #9EB0BA !important;
-moz-border-right-colors:#9EB0BA !important;
}

/**
 * Searchbar
 */

#search-container 
{
-moz-border-radius: 0 !important;
-moz-box-flex: 0 !important;
max-width: 250px !important;
min-width: 250px !important;
}

#search-container .searchbar-textbox
{
margin-right: 0 !important;
}

#search-container .search-go-button
{
padding: 0 !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: auto !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
border: 0px solid transparent;
border-right: 1px solid transparent !important;
-moz-border-radius:0 !important;
margin-right: 13px !important;
}

#search-container .search-go-button:hover
{
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: auto !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
padding: 0 !important;
-moz-border-right-colors: rgba(60,127,177,1) !important;
}

#search-container .search-go-button:active,
#search-container .search-go-button:hover:active
{
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
padding: 0 !important;
}

#search-container .searchbar-engine-button
{
margin-left: 223px !important;
margin-right: -242px !important;
background: none !important;
height: 22px !important;
border: 0px solid !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
-moz-border-radius: 0 !important;
}

#search-container .searchbar-engine-button
{
-moz-box-shadow: 0px 0px 0px !important;
}

#search-container .searchbar-engine-button .searchbar-engine-image
{
display: none !important;
-moz-appearance: none !important;
}

#search-container .searchbar-engine-button[addengines="true"] .button-box
{
background: none !important;
}

/**
 * Search Bar dropmarker
 */

#search-container .searchbar-engine-button .searchbar-dropmarker-image
{
list-style-image: none !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAWCAYAAAAfD8YZAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEBJREFUeNpi/P//PwO5gImBAjCqeWRoZkEXYGRkxJnkgKmREa/N6ArwiTMRoxCXgUyEnIhLI9iLo7lqRGgGCDAAUD8cG686EegAAAAASUVORK5CYII=") !important;
background-position: center 0 !important;
background-repeat: no-repeat !important;
border:none !important;
width: 15px !important;
height: 22px !important;
margin-top:0px !important;
margin-bottom:0px !important;
}

#search-container .searchbar-engine-button[addengines="true"] .searchbar-dropmarker-image
{
background-image: url("data:image/png;base64,") !important;
}

#search-container .searchbar-engine-button:hover .searchbar-dropmarker-image
{
list-style-image: none !important;
background-image: url("data:image/png;base64,") !important;
}

#search-container .searchbar-engine-button[open="true"] .searchbar-dropmarker-image,
#search-container .searchbar-engine-button:active .searchbar-dropmarker-image
{
list-style-image: none !important; 
background-image: url("data:image/png;base64,") !important;
}


/**
 * Stop Button
 */

#stop-button,#stop-button:not([disabled="true"]):hover,
#stop-button[disabled="true"],#stop-button:not([disabled="true"]):hover:active
{
width:auto !important;
padding:0 !important;
margin:0 !important;
margin-right:0 !important;
-moz-appearance:none !important;
border:none !important;
}

#stop-button > .toolbarbutton-icon
{
background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABkAAAAWCAYAAAA1vze2AAAAB3RJTUUH2AUSEgsfcReHpgAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgA2D8////fppawMjoyEQPn4xaMmrJqCWjloxaMmrJqCV0BwC4UAQoq9aPPwAAAABJRU5ErkJggg==) no-repeat center center !important;
list-style-image:url(data:image/png;base64,) !important;
-moz-image-region:auto !important;
margin:0 !important;
padding:0 !important;
border-top:2px solid !important;
border-bottom:2px solid !important;
border-right:2px solid !important;
-moz-border-radius-topright:3px !important;
-moz-border-radius-bottomright:3px !important;
-moz-border-top-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.7) !important;
-moz-border-right-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) !important;
-moz-border-bottom-colors:rgba(255,255,255,0.5) rgba(50,50,50,0.3) !important;
}

/* #stop-button:not([disabled="true"]):hover > .toolbarbutton-icon */
#stop-button:hover > .toolbarbutton-icon
{
list-style-image:url(data:image/png;base64,) !important;
}

/* #stop-button:not([disabled="true"]):hover:active > .toolbarbutton-icon */
#stop-button:hover:active > .toolbarbutton-icon
{
list-style-image:url(data:image/png;base64,) !important;
}


/**
 * Reload Button
 */

#reload-button,
#reload-button:not([disabled="true"]):hover,
#reload-button[disabled="true"],
#reload-button:not([disabled="true"]):hover:active
{
width: 25px !important;
padding: 0 !important;
margin: 0 !important;
margin-left: -4px !important;
-moz-appearance: none !important;
border: none !important;
}

#reload-button > .toolbarbutton-icon
{
background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABkAAAAWCAYAAAA1vze2AAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAADFJREFUeNpi/P//PwOtAQsQ76e1JUwMdACjloxaMmrJqCWjloxaMmoJMmCkR0MCIMAAFxsG5uZ+74QAAAAASUVORK5CYII=) no-repeat center center !important;
list-style-image: url(data:image/png;base64,) !important;
-moz-image-region: auto !important;
margin: 0 !important;
padding: 0 !important;
border-top: 2px solid !important;
border-bottom: 2px solid !important;
-moz-border-top-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.7) !important;
-moz-border-bottom-colors: rgba(255,255,255,0.5) rgba(50,50,50,0.3) !important;
}

#reload-button:not([disabled="true"]):hover > .toolbarbutton-icon
{
list-style-image:url(data:image/png;base64,) !important;
}

#reload-button:not([disabled="true"]):hover:active > .toolbarbutton-icon
{
list-style-image:url("data:image/png;base64,") !important;
}

#urlbar-container:hover + #reload-button > .toolbarbutton-icon
{
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABoAAAAWCAYAAADeiIy1AAAAB3RJTUUH2AUSEhMgRWoywgAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAqSURBVHjaY2CgE2D8////U3pYxEQvH41aNGrRqEWjFo1aNGrRqEVDGgAAJx8ECnQ/ooIAAAAASUVORK5CYII=") no-repeat center center !important;
}

#urlbar-container:hover + #reload-button + #stop-button > .toolbarbutton-icon
{
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABkAAAAWCAYAAAA1vze2AAAAB3RJTUUH2AUSEhQC30vl4QAAAAlwSFlzAAALEwAACxMBAJqcGAAAAARnQU1BAACxjwv8YQUAAAAsSURBVHjaY2CgA2D8////U5pawMgozUQPn4xaMmrJqCWjloxaMmrJqCV0BwDGkAQo4O8k1wAAAABJRU5ErkJggg==") no-repeat center center !important;
}


/**
 * Throbbers
 */


#nav-bar > #navigator-throbber
{
margin: 0 0 0 3px !important;
list-style-image: none !important;
background: none !important;
-moz-box-shadow: none !important;
}
#nav-bar > #navigator-throbber[busy="true"]
{
list-style-image: url("data:image/gif;base64,") !important;
background: url("data:image/png;base64,") no-repeat center center !important;
-moz-image-region: auto !important;
}


/**
 * Back and Foward buttons (and dropmarker)
 */

#back-button,
#nav-bar[saved-iconsize] #back-button,
#back-button:hover,#nav-bar[saved-iconsize] #back-button:hover,
#back-button:active,#nav-bar[saved-iconsize] #back-button:active,
#back-button[disabled="true"],
#nav-bar[saved-iconsize] #back-button[disabled="true"],
#forward-button,
#nav-bar[saved-iconsize] #forward-button,
#forward-button:hover,
#nav-bar[saved-iconsize] #forward-button:hover,
#forward-button:active,
#nav-bar[saved-iconsize] #forward-button:active,
#forward-button[disabled="true"],
#nav-bar[saved-iconsize] #forward-button[disabled="true"]
{
list-style-image: url("data:image/png;base64,") !important;
}

#back-button,
#nav-bar[saved-iconsize] #back-button
{
-moz-image-region: rect(27px 28px 55px 0px) !important;
background: url("data:image/png;base64,") no-repeat !important;
background-position: center right !important;
}

#back-button:hover,
#nav-bar[saved-iconsize] #back-button:hover
{
-moz-image-region: rect(54px 28px 82px 0px) !important;
}

#back-button:active,
#nav-bar[saved-iconsize] #back-button:active
{
-moz-image-region: rect(81px 28px 109px 0px) !important;
}

#back-button[disabled="true"],
#nav-bar[saved-iconsize] #back-button[disabled="true"]
{
-moz-image-region: rect(0px 28px 28px 0px) !important;
}

#back-button > .toolbarbutton-menubutton-stack,
#nav-bar[saved-iconsize] #back-button > .toolbarbutton-menubutton-stack
{
background: url("data:image/png;base64,") no-repeat !important;
background-position: center left !important;
}

#forward-button,
#nav-bar[saved-iconsize] #forward-button
{
-moz-image-region: rect(27px 53px 55px 27px) !important;
background: url("data:image/png;base64,") no-repeat !important;
background-position: left center !important;
}

#forward-button:hover,
#nav-bar[saved-iconsize] #forward-button:hover
{
-moz-image-region: rect(54px 53px 82px 27px) !important;
}

#forward-button:active,
#nav-bar[saved-iconsize] #forward-button:active
{
-moz-image-region: rect(81px 53px 109px 27px) !important;
}

#forward-button[disabled="true"],
#nav-bar[saved-iconsize] #forward-button[disabled="true"]
{
-moz-image-region: rect(0px 53px 28px 27px) !important;
}

#forward-button > .toolbarbutton-menubutton-stack,
#nav-bar[saved-iconsize] #forward-button > .toolbarbutton-menubutton-stack
{
background: url("data:image/png;base64,") no-repeat !important;
background-position: left center !important;
}

#back-forward-dropmarker
{
-moz-appearance: none !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(0px 48px 24px 24px) !important;
border: 0 !important;
padding: 0 !important;
margin-left: -3px !important;
margin-top: 1px !important;
}

#back-forward-dropmarker[disabled="true"],
#back-forward-dropmarker[disabled="true"]:hover,
#back-forward-dropmarker[disabled="true"]:active,
#back-forward-dropmarker[disabled="true"]:hover:active
{
-moz-image-region: rect(0px 24px 24px 0px) !important;
}

#back-forward-dropmarker:hover
{
-moz-image-region: rect(0px 72px 24px 48px) !important;
}

#back-forward-dropmarker:hover:active
{
-moz-image-region:rect(0px 96px 24px 72px) !important;
}

}
