@-moz-document domain("spotdata.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #ee651a #1c1c1c;
}
*{
    box-shadow: none !important;
}
a,h1,h2,h3,h4{
    color: #ee651a !important;
}
p,span,label{
    color: #fff !important;
}
input[type="text"],input[type="email"],input[type="password"],input::placeholder,textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #ee651a !important;
}
.checkbox label::before{
    background: #1c1c1c !important;
}
.checkbox label.is-checked::before{
    border: 1px solid #ee651a !important;
    background: #ee651a !important;
}
/*HEADER*/
.navbar,.navbar-right.inline{
    background: #1c1c1c;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.login-form .modal-content,section .front-analyses .link{
    background: #1c1c1c;
    border: 2px solid #ee651a;
}
.login-form .bootstrap-dialog-header,#site-content.start,section,section .front-analyses,#cd-vertical-nav .cd-label{
    background: #1c1c1c;
}
.form-control{
    border-radius: 0;
}
#site-header .transparent-box{
    background: #0009;
}
#site-header .transparent-box .left,#site-header .transparent-box .right{
    background: #ffffff29;
}
#site-header .transparent-box .center{
    background: #0000006e;
}
.btn-report:hover{
    color: #1c1c1c !important;
}
.btn-primary{
    color: #fff !important;
    border: 1px solid #ee651a;
}
.btn-primary:hover{
    background: #1c1c1c;
    border: 1px solid #ee651a;
    color: #ee651a !important;
}
/*STRONA-GŁÓWNA*/
/*SUBPAGES*/
/*BLOG*/
.navigation-top,.nav-spotdata,.site-content-contain,.blog .site-main > article,.pagination-wrap,.single article,.archive .site-main > article{
    background: #1c1c1c;
}
.post-category,.entry-title,table strong{
    color: #ee651a;
}
.pagination-wrap .page-numbers,.widget_categories{
    background: #1c1c1c;
    border: 1px solid #ee651a;
}
.next.page-numbers,.prev.page-numbers{
    border: 1px solid #ee651a !important;
}
.icon.icon-arrow-right,.icon.icon-arrow-left{
    fill: #fff;
}
table .txt,.entry-content{
    color: #fff
}
.site-description{
    background: #0000006e;
