css:
@-moz-document domain("photogrist.com") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #434e58 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #f85555 !important;
}
a:hover{
    color: #434e58 !important;
}
p,span{
    color: #fff !important
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="email"],input[type="password"],input[type="search"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #434e58 !important;
}
/*HEADER*/
.menu-block .os_menu li a:hover{
    color: #f85555 !important;
    text-decoration: underline;
}
/*HEADER*/
/*MAIN-PAGE*/
.index-isotope.v3 article.pluto-post-box{
    background: #1c1c1c;
}
.index-isotope.v3 article.pluto-post-box .post-content{
    color: #fff;
}
/*MAIN-PAGE*/
/*GALLERY*/
.single article.pluto-page-box{
    background: #1c1c1c;
    border: 2px solid #434e58;
}
.single .post-meta-top .post-tags li a{
    background: #1c1c1c;
    border: 2px solid #434e58;
}
.single .post-meta-top .post-tags li a:hover,.single .post-meta-top .post-categories li a:hover{
    background: #434e58;
    border: 2px solid #434e58;
    color: #1c1c1c !important;
}
.single .post-meta-top .post-categories li a{
    border: 2px solid #fcf6d6;
}
.single article.pluto-page-box .post-meta{
    background: #1c1c1c;
    border-top-color: #434e58;
}
.single article.pluto-page-box .meta-like{
    background: #434e58;
    border-right-color: #434e58;
}
.widget-related-post .widget-content{
    color: #fff;
}
/*GALLERY*/
/*SUBPAGES*/
.page article.pluto-page-box{
    background: #1c1c1c;
    border: 2px solid #434e58;
}
.reading-mode{
    background: #434e58;
}
.reading-mode.page .type-page .single-post-top-features{
    background: #1c1c1c;
}
.wpcf7-form .wpcf7-submit{
    background: #1c1c1c !important;
    border: 2px solid #434e58;
    color: #f85555 !important;
}
.wpcf7-form .wpcf7-submit:hover{
    background: #434e58 !important;
    border: 2px solid #434e58;
    color: #f85555 !important;
}
/*SUBPAGES*/
/*FOOTER*/
.main-footer.color-scheme-light{
    background: #434e58;
}
.main-footer .footer-copyright{
    color: #fff;
    border-top-color: #fff !important;
}
.main-footer.color-scheme-light:before{
    content: "Dark theme by Maciejozaman";
    color: #fff;
    cursor: none;
    font-size: medium;
}
/*FOOTER*/
}
