/**
 * Wb Aero
 * Bookmarks Toolbar
 * by wifflebunz http://userstyles.org/users/23718
 *
 * UPDATES:
 * [8/01/2009]
 * - Better drag & drop markers
 * - Lots of fixes for non-Vista users
 * - Animated button hover
 * - Better support for buttons that aren't bookmarks
 * [7/01/2009]
 * - Updates for 3.5
 * - Changed the drop marker "chevron" at the end of the URL Bar
 * - Taller bar when using large icons
 * border-top:0px solid rgba(178,203,219,0) !important;
 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {

#browser { border-top: 0px !important; }
#navigator-toolbox { padding-bottom: 0px !important; }

#navigator-toolbox #PersonalToolbar {
border-top: 1px solid transparent !important;
border-left: 1px solid rgba(255,255,255,0.35) !important;
border-right: 1px solid rgba(248,248,248,0.5) !important;
border-bottom: 2px solid transparent !important;
opacity: 1 !important;
background-position: 0 -1px;
background-repeat: repeat-x;
-moz-background-clip: border !important;
-moz-appearance: none !important;
-moz-border-image: url("data:image/png;base64,") 1 1 2 1 / 1px 1px 2px 1px stretch stretch !important;
background-color: #fff !important;
padding: 0 !important;
margin: 0 !important;
color: white !important;
font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;
}

#navigator-toolbox[iconsize="small"] #PersonalToolbar {
height: 27px !important;
max-height: 27px !important;
min-height: 27px !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAbCAIAAAAyOnIjAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGtJREFUeNokirENgzAURD/PdkAIo0gZIqzBjCzCCJSpQpuKFkaIzXeCSa64d3o6GR8v5mVl1w9Rkw2q9mSMiaCJ966EqL+dff4ls7V3RL7k4p8CZwzOGsqLoyodja9prw3+5mWYnkLXHwIMAHMlKu7qvs6tAAAAAElFTkSuQmCC") !important;
}

#navigator-toolbox[iconsize="large"] #PersonalToolbar {
padding: 0px 2px !important;
height: 31px !important;
max-height: 31px !important;
min-height: 31px !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAfCAIAAACpqzA1AAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGtJREFUeNocyrENhDAMheHk2QHlgmAJJChZ4crb9sago6WihRUgMeBE1if/ko3/vGLZdiS5EbMkfCltvqJAuzhj0ttdOorkfzraAcY8sMYA1iIP6WYiOCZUjgvva4TGI3QfFfCbeovx+wowAHFFL3fRmWZaAAAAAElFTkSuQmCC") !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton {
-moz-appearance: none !important;
-moz-box-orient: horizontal !important;
margin: 0 !important;
padding: 0 !important;
color: #fff !important;
border: 3px solid transparent !important;
opacity: 1 !important;
background-position: 0 0px;
background-repeat: repeat-x;
-moz-background-clip: border !important;
text-shadow: 0px 1px 2px rgba(0,0,0,1), 1px 1px 3px rgba(0,0,0,0.5), 0 0 3px rgba(0,0,0,0.4) !important;
-moz-border-radius: 3px !important;
-moz-border-image: none !important;
background-image: none !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton > toolbarbutton {
margin: -3px -4px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton > .toolbarbutton-icon {
padding: 0 !important;
}
#navigator-toolbox[mode="icons"] #PersonalToolbar > toolbarbutton {
padding-right: 1px !important;
padding-left: 1px !important;
}
#navigator-toolbox #PersonalToolbar toolbarbutton[type="menu"] > dropmarker {
display: none !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton:not([open="true"]):not([checked="true"]):hover {
-moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton:active:hover,
#navigator-toolbox #PersonalToolbar toolbarbutton[open="true"],
#navigator-toolbox #PersonalToolbar toolbarbutton[checked="true"] {
-moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton[type="menu"] > toolbarbutton {
-moz-border-image: none !important;
}

#navigator-toolbox:not([mode="icons"]) #PersonalToolbar > toolbarbutton > .toolbarbutton-icon,
#navigator-toolbox #PersonalToolbar > #personal-bookmarks toolbarbutton .toolbarbutton-icon:not([label=""]) {
margin-right: 2px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton .toolbarbutton-text {
padding-right: 1px !important;
}

#navigator-toolbox #PersonalToolbar .toolbarbutton-1,
#navigator-toolbox #PersonalToolbar .toolbarbutton-menubutton-button {
padding-right: 3px !important;
}

#navigator-toolbox #PersonalToolbar toolbarseparator {
-moz-appearance: none !important;
list-style-image: none !important;
-moz-border-left-colors: transparent transparent rgba(0,0,0,.45) !important;
-moz-border-right-colors: transparent transparent rgba(255,255,255,.3) !important;
border: 1px solid transparent !important;
border-left-width: 3px !important;
border-right-width: 3px !important;
background: none !important;
min-width: 0 !important;
display: -moz-box !important;
margin: 0px 0px 0px !important;
padding: 0 !important;
max-width: none !important;
height: 24px !important;
max-height: 24px !important;
min-height: 24px !important;
-moz-border-radius: 0 !important;
}

#navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarseparator {
height: 26px !important;
margin-top: 1px !important;
margin-bottom: 1px !important;
max-height: 26px !important;
min-height: 26px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton.chevron {
list-style-image: url("data:image/png;base64,") !important;
padding-left: 7px !important;
padding-right: 3px !important;
}

#navigator-toolbox #PersonalToolbar toolbarbutton > dropmarker {
list-style-image: url("data:image/png;base64,") !important;
padding-right: 0px !important;
margin-right: 0px !important;
border: 1px solid;
}

#navigator-toolbox #PersonalToolbar toolbarbutton[type="menu-button"] > toolbarbutton {
padding-right: 0px !important;
}

#navigator-toolbox > #PersonalToolbar toolbarbutton[dragover="true"][open="true"] {
background-color: transparent !important;
background-image: none !important;
}

#navigator-toolbox > #PersonalToolbar > toolbarpaletteitem[dragover] {
background-position: 0 0 !important;
background-repeat: no-repeat !important;
background-image: url("data:image/png;base64,") !important;
padding-left: 8px !important;
padding-right: 7px !important;
border: 0 !important;
}
#navigator-toolbox > #PersonalToolbar > toolbarpaletteitem[dragover="right"] {
background-position: right 0 !important;
}

#navigator-toolbox > #PersonalToolbar .toolbar-drop-indicator-bar,
#navigator-toolbox > #PersonalToolbar .toolbar-drop-indicator {
-moz-appearance: none !important;
border: 0 !important;
padding: 0 !important;
list-style-image: none !important;
margin-top: 0 !important;
background-position: 0 0 !important;
background-repeat: no-repeat !important;
}

#navigator-toolbox[iconsize="small"] #PersonalToolbar .toolbar-drop-indicator-bar,
#navigator-toolbox[iconsize="small"] #PersonalToolbar .toolbar-drop-indicator {
height: 26px !important;
min-height: 26px !important;
max-height: 26px !important;
margin-bottom: -26px !important;
}
#navigator-toolbox[iconsize="large"] #PersonalToolbar .toolbar-drop-indicator-bar,
#navigator-toolbox[iconsize="large"] #PersonalToolbar .toolbar-drop-indicator {
height: 30px !important;
min-height: 30px !important;
max-height: 30px !important;
margin-bottom: -30px !important;
}
#navigator-toolbox[iconsize="small"] > #PersonalToolbar > toolbarpaletteitem[dragover],
#navigator-toolbox[iconsize="small"] #PersonalToolbar .toolbar-drop-indicator {
background-image: url("data:image/png;base64,") !important;
}
#navigator-toolbox[iconsize="large"] > #PersonalToolbar > toolbarpaletteitem[dragover],
#navigator-toolbox[iconsize="large"] #PersonalToolbar .toolbar-drop-indicator {
background-image: url("data:image/png;base64,") !important;
}
#navigator-toolbox[iconsize="small"] #PersonalToolbar toolbarbutton,
#navigator-toolbox[iconsize="small"] #PersonalToolbar toolbarbutton.chevron {
height: 24px !important;
max-height: 24px !important;
min-height: 24px !important;
}
#navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton,
#navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton.chevron {
height: 24px !important;
margin-top: 2px !important;
margin-bottom: 2px !important;
max-height: 24px !important;
min-height: 24px !important;
}
#main-window #navigator-toolbox[iconsize="large"] #PersonalToolbar toolbarbutton .toolbarbutton-icon {
max-height: 16px !important;
max-width: 16px !important;
}

}
