css:
/**
 * Wb Aero
 * AIOS All-in-one Sidebar
 * by wifflebunz http://userstyles.org/users/23718
 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("chrome://browser/content/browser.xul")
{

#aios-toolbar
{
/* IE8 Vista Style */
	background-image:url("data:image/png;base64,") !important;
	background-position:0 0 !important;
	background-color:#E3EEFB !important;

/* or...
 * JUST REMOVE BOTTOM BORDER
	background-image: url("data:image/png;base64,") !important;
	background-position: 0 bottom !important;
*/

	background-repeat:repeat-x !important;
	-moz-appearance:none !important;
	padding:0 0 0 1px !important;
	border-right:2px solid #8B919F !important;
	-moz-border-right-colors:#8B919F rgba(255, 255, 255, .6) !important;
	border-bottom:0 !important;
}

#aios-toolbar toolbarbutton {
background-position: 0 0 !important;
text-align: left !important;
max-width: 24px !important;
padding:2px 2px !important;
margin: 0 !important;
}

#aios-toolbar toolbarbutton image
{
margin:0 !important;
padding:0 !important;
max-width: 16px !important;
text-align: left !important;
}

#aios-toolbar toolbarbutton dropmarker {
display: none !important;
}

}
