css:
@-moz-document domain("stallman.org"){
  body {
    background-color: #100806;
    font-family: Sans Serif;
    color: #e0c69f;
  }
  
  :link {
    color: #fe8019;
  }
a:link:hover {
   color: #bb841a;
   font-style: italic;
}
:visited {
    color: #bb841a;
}

.columnt1 a:link:hover, .columnt1 a:visited:hover {
   color: #bb841a;
}

#talks {
    width: 700px;
    max-width: 100%;
    margin: 4px auto;
    background-color: #100806;
    padding: 1px 8px 4px 8px;
    border-radius: 8px;
}

#talks h3 {
    text-align: center;
   color: #839496;
}

.talk {
    white-space: pre;
    border-radius: 4px;
    background-color: #100806;
    margin: 8px;
    font-size: 16px;
    padding: 8px;
}

.talk b {
    font-family: sans-serif;
    font-size: 14px;
   color: #839496;
}

.talk p {
   color: #839496;
    margin: 0;
    font-weight: bold;
      font-family: sans-serif;

}
}
