@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://dta/content/dta/addurl.xul) {

dialog {
font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;
margin: 0 !important;
padding: 0 0 10px 0 !important;
}

dialog > .box-inherit.dialog-content-box {
-moz-appearance: none !important;
margin: 0 !important;
padding: 0 !important;
-moz-box-flex: 0 !important;
background-color: #F1FBFC !important;
-moz-border-image: url("data:image/png;base64,") 12 1 0 / 12px 1px 0 stretch !important;
}

dialog > separator,
#logo
{ display: none !important; }


#descr,
#batches {
margin: 0 6px 8px !important;
padding: 0 0px !important;
font-weight: normal !important;
color: #575757 !important;
}


#batches {
margin: 20px 32px 10px !important;
-moz-appearance: none !important;
border: 1px solid !important;
-moz-border-radius: 3px !important;
-moz-border-top-colors: rgba(50,50,50,0.5) !important;
-moz-border-right-colors: rgba(50,50,50,0.3)  !important;
-moz-border-bottom-colors:rgba(50,50,50,0.3)  !important;
-moz-border-left-colors: rgba(50,50,50,0.5)  !important;
padding: 6px !important;
background-color: rgba(255,255,255,0.75) !important;
-moz-background-clip: padding !important;
}
#batches > label {
}
#batches > label:first-child {
margin: -25px 0 0 -6px !important;
padding: 0 !important;
-moz-appearance: none !important;
font-weight: normal !important;
}
#batches > label:first-child + label {
margin-top: 10px !important;
}
#batches > label:first-child + label,
#batches > label:first-child + label + label {
font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;
color: #000 !important;;
}

dialog > grid {
margin: 0 0 12px !important;
padding: 0 !important;
background-color: transparent !important;
width: 100%;
}

dialog > .dialog-button-box {
background-color: #f0f0f0 !important;
min-height: 34px !important;
-moz-box-flex: 100 !important;
-moz-box-orient: horizontal !important;
-moz-box-align: start !important;
border-top: 2px solid ThreeDDarkShadow !important;
-moz-border-top-colors: ThreeDShadow #FFF !important;
}

}




@-moz-document url(chrome://dta/content/dta/manager.xul) {

#mainstack {
-moz-appearance: none !important;
background-color: transparent !important;
background-image: none !important;
border: 0px solid !important;
margin: 0 !important;
padding: 0 !important;
font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;
}

#downloads {
border: 0px solid !important;
-moz-appearance: none !important;
background-color: rgba(255,255,255,1) !important;
border-top: 0px solid #083242 !important;
-moz-border-top-colors:rgba(20,30,44,.6) !important;
}

#downloads * { opacity:1 !important; }

#tools {
height: 27px !important;
max-height: 27px !important;
min-height: 27px !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAbCAIAAAAyOnIjAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAGtJREFUeNokirENgzAURD/PdkAIo0gZIqzBjCzCCJSpQpuKFkaIzXeCSa64d3o6GR8v5mVl1w9Rkw2q9mSMiaCJ966EqL+dff4ls7V3RL7k4p8CZwzOGsqLoyodja9prw3+5mWYnkLXHwIMAHMlKu7qvs6tAAAAAElFTkSuQmCC") !important;
border-top: 1px solid transparent !important;
border-left: 1px solid rgba(255,255,255,0.35) !important;
border-right: 1px solid rgba(248,248,248,0.5) !important;
border-bottom: 2px solid transparent !important;
opacity: 1 !important;
background-position: 0 -1px;
background-repeat: repeat-x;
-moz-background-clip: border !important;
-moz-appearance: none !important;
-moz-border-image: url("data:image/png;base64,") 1 1 2 1 / 1px 1px 2px 1px stretch stretch;
background-color: #fff !important;
padding: 0 !important;
margin: 0 !important;
color: white !important;
}

#tools spacer {
margin: 0 !important;
padding: 0 !important;
display: none !important;
max-width: none !important;
min-width: 0px !important;
height: 27px !important;
max-height: 27px !important;
background-image: none !important;
}

#tools spacer.visible {
-moz-appearance: none !important;
list-style-image: none !important;
-moz-border-left-colors: transparent transparent rgba(0,0,0,.45) !important;
-moz-border-right-colors: transparent transparent rgba(255,255,255,.3) !important;
border: 1px solid transparent !important;
border-left-width: 3px !important;
border-right-width: 3px !important;
background: none !important;
min-width: 0 !important;
display: -moz-box !important;
margin: 0px 0px 0px !important;
padding: 0 !important;
max-width: none !important;
height: 22px !important;
-moz-border-radius: 0 !important;
}

#tools toolbarbutton {
height: 26px !important;
max-height: 26px !important;
min-width: 0px !important;
-moz-image-region: auto !important;
-moz-appearance: none !important;
-moz-box-orient: horizontal !important;
margin: 0 !important;
padding: 0 !important;
color: #fff !important;
border: 3px solid transparent !important;
opacity: 1 !important;
background-position: 0 0px;
background-repeat: repeat-x;
-moz-background-clip: border !important;
text-shadow: 0px 1px 2px rgba(0,0,0,1), 1px 1px 3px rgba(0,0,0,0.5), 0 0 3px rgba(0,0,0,0.4) !important;
-moz-border-radius: 3px !important;
-moz-border-image: none !important;
background-image: none !important;
}

#tools toolbarbutton:not([disabled="true"]):hover {
-moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;
}

#tools toolbarbutton:not([disabled="true"]):active:hover {
-moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;
}


#tools toolbarbutton {
list-style-image: url("data:image/png;base64,") !important;
}

#tools toolbarbutton .toolbarbutton-icon {
height: 16px !important;
width: 16px !important;
}

#tools toolbarbutton .toolbarbutton-text {
opacity: 1 !important;
display: -moz-box !important;
}

#tools toolbarbutton[disabled="true"] .toolbarbutton-icon,
#tools toolbarbutton[disabled="true"] {
text-shadow: none !important;
opacity: 1 !important;
}

#tools toolbarbutton[disabled="true"] .toolbarbutton-text {
opacity: .7 !important;
}

#tools #tooladd { list-style-image: url("data:image/png;base64,") !important; }
#tools #toolplay { -moz-image-region: rect(0px,80px,16px,64px) !important; }
#tools #toolpause { -moz-image-region: rect(0px,96px,16px,80px) !important; }
#tools #toolcancel { -moz-image-region: rect(0px,112px,16px,96px) !important; }
#tools #toolmovetop { -moz-image-region: rect(64px,16px,80px,0px) !important; }
#tools #toolmoveup { -moz-image-region: rect(64px,32px,80px,16px) !important; }
#tools #toolmovedown { -moz-image-region: rect(64px,48px,80px,32px) !important; }
#tools #toolmovebottom { -moz-image-region: rect(64px,64px,80px,48px) !important; }


#tools #tooladd[disabled="true"] { list-style-image: url("data:image/png;base64,") !important; }
#tools #toolplay[disabled="true"] { -moz-image-region: rect(16px,80px,32px,64px) !important; }
#tools #toolpause[disabled="true"] { -moz-image-region: rect(16px,96px,32px,80px) !important; }
#tools #toolcancel[disabled="true"] { -moz-image-region: rect(16px,112px,32px,96px) !important; }
#tools #toolmovetop[disabled="true"] { -moz-image-region: rect(80px,16px,96px,0px) !important; }
#tools #toolmoveup[disabled="true"] { -moz-image-region: rect(80px,32px,96px,16px) !important; }
#tools #toolmovedown[disabled="true"] { -moz-image-region: rect(80px,48px,96px,32px) !important; }
#tools #toolmovebottom[disabled="true"] { -moz-image-region: rect(80px,64px,96px,48px) !important; }

/*
Different Icons
#tools #tooladd { list-style-image: url("data:image/png;base64,") !important; }
#tools #toolplay { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolpause { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolcancel { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolmovetop { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolmoveup { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolmovedown { list-style-image: url(data:image/png;base64,) !important; }
#tools #toolmovebottom { list-style-image: url(data:image/png;base64,) !important; }
*/

#tools #tooldonate {
width: 116px !important;
overflow: hidden !important;
min-width: 0px !important;
-moz-image-region: rect(58px,296px,84px,180px) !important;
-moz-appearance: toolbarbutton !important;
display: none !important;
}

#tools #tooldonate:before {
content: 'DONATE';
}

#footer > statusbarpanel {
padding: 0px !important;
border: 0px solid !important;
-moz-appearance: none !important;
}

}
