css:
@-moz-document domain("dziennik.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #1688c8 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #1688c8 !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #1688c8 !important;
}
/*HEADER*/
.pageHeader .corporateMenu,.pageHeader .mainMenu,.siteNavigation .menuWrapper,#globeIcon{
    background-color: #1c1c1c;
}
.pageHeader.reduced .serviceLogo img{
    background: #fff;
    border: 3px solid #1688c8;
}
#tabMenu .firstLevel .mainMenu > li:hover > a:not(.homePageUrl){
    background: #1688c8;
    color: #1c1c1c !important;
}
#tabMenu .firstLevel .mainMenu .bubbleGroup, #tabMenu .firstLevel .mainMenu .bubbleMenu{
    background-color: #1c1c1c;
    border: 1px solid #1688c8;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.specialReports{
    background: #1c1c1c;
    border: 1px solid #1688c8;
}
.specialReports .menuReports h3{
    color: red !important;
}
.specialSection .wrapper header a h2, .specialSection .wrapper header > h2,.listItem a:hover .itemTitle .title,.topicList li:hover a span{
    color: #fff !important;
}
.topContent .detailSection .pageWrapper,.detailSection .pageContent,.fourImages::before,.fullWidth .widget.widgetFeed .widgetContent::before,.mainpage .edgpWidget,.topicList li:hover{
    background: #1c1c1c !important;
}
.widget .header > div .sectionTitle,.title{
    color: #1688c8 !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.pageContent .detailSection,.streamNews{
    background: #1c1c1c;
}
#tabMenu .secondLevel .breadcrumbs li a,.dateModified, .datePublished,.streamNews .container .streamLine a:hover .title{
    color: #fff !important;
}
#tabMenu .secondLevel .breadcrumbs li a:hover{
    text-decoration: underline;
}
#newDetail .videoScrollClass,.relatedTopic,.pageContent .articleDetail .promoFrame,.detailAllBoxes .leftButtons .reportError,.detailAllBoxes .leftButtons .printButton{
    background: #1c1c1c !important;
    border: 1px solid #1688c8;
}
.relatedTopic:hover{
    background: #1688c8;
}
.relatedTopic:hover a{
    color: #1c1c1c !important;
}
.detailAllBoxes .leftButtons .printButton span{
    color: #1688c8 !important;
}
.detailAllBoxes .leftButtons .printButton span svg g{
    fill: #1688c8;
}
/*KOMENTARZE*/
.listAuthor{
    color: #1688c8;
}
.listDate,.listContent{
    color: #fff;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
#tabMenu .secondLevel .subMenu li a{
    color: #fff !important;
}
#tabMenu .secondLevel .subMenu li a:hover{
    text-decoration: underline;
}
.resultHeader{
    color: #1688c8;
}
.paginateNumber a.current{
    color: #1c1c1c !important;
}
.resultItem:not(:last-child)::after{
    background: #1688c8;
}
/*KONTAKT*/
.tableCore .otable tbody tr:nth-child(2n+1){
    background: #1c1c1c;
}
.tableCore .otable tbody tr:nth-child(2n){
    background: #1688c8;
}
.tableCore .otable tbody td,.pageContent .articleDetail ol li::before{
    color: #fff;
}
.tableCore .otable tbody tr:hover td{
    background: #1688c8;
    color: #1c1c1c !important;
}
/*KONTAKT*/
/*PODSTRONY*/
/*FOOTER*/
.copyrights:after{
    content: " Ciemny motyw by Maciejozaman";
    color: #fff;
}
/*FOOTER*/
}
