css:
@-moz-document url-prefix("https://www.comptoir-hardware.com/") {
#page-body {background-color: #303030 !important;}
.wrapper-bg1 {background-color: #151515 !important; background: none;}
.mainmiddle-wrapper-m {background-color: #303030 !important;}
#date, .mainmiddle-wrapper-m p, .mainmiddle-wrapper-m p span, a.home_news, h6, tbody tr td, div.home_content_news a {color: white;}
div.mod-trans {background-color: #151515 !important;}
div.mod-rounded {display: none;}
table.home_news {background-image: none;}
table.home_news_quick {background: none;}
table.home_news:hover {background-color: grey;}
table.cdh tr:nth-child(even) {background: none; background-color: grey;}
table.cdh tr:nth-child(odd) {background: none; background-color: grey;}
.home_titre h3 {background: none; background-color: #BFBFBF;}
.home_titre h3:after {border-left: 25px solid #BFBFBF;}
div.tip, span.tip {background: none; background-color: #505050; color: white;}
#pagination-digg a:link, #pagination-digg a:visited {color: #777777;}
div.note, span.note {background: none; background-color: #151515;}
#bottom {display: none;}
}
