css:
@-moz-document domain(fastmail.com) {

body {
    font-family: sans-serif !important;
    font-size: 13px;
    background-color: #ddd;
}
.v-PageHeader .v-Button {
    background-image: none !important;
    background-color: rgba(255, 255, 255, 0.1);
    height: 2.025rem;
}

.v-PageHeader .v-Button:hover {
    background-color: rgba(255, 255, 255, 0.2);
}

.v-Sources-group {
    margin: 0.65rem 0;
}

.v-PushSelect-option {
    background-image: none !important;
    background-color: rgba(183, 183, 183, .2);
    box-shadow: none;
}

.v-Button--standard {
    background-image: none !important;
    box-shadow: none;
}

.v-PushSelect-option:hover {
    background-color: rgba(183, 183, 183, .6);
}


.app-source > .v-Icon {
    margin: 0 0.3125rem 0.125rem 0;
    width: 1.25rem;
    height: 1.25rem;
}

.is-read {
    background-color: rgba(145, 145, 145, .01) !important;
}

.is-unread {
    background-color: rgba(128, 154, 232, .09) !important;
}

.u-font-proxima {
  font-size: 0.812rem !important;
}
}
