@-moz-document domain("control.heavynode.com") {
/* GENERAL CSS */
body, html {background: #191919}
.bg-white {background-color: #222!important; color: white!important}
.text-muted {color: #ccc}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {color: white;}
code {color: white; background-color: #1d1d1d}
.shadow {box-shadow: none;}

/* NAVBAR */
.navbar-nav .nav-item a {color: white!important;}
.navbar-light .navbar-toggler-icon {color: white}
.navbar-brand img {content: url(https://control.heavynode.com/themes/heavynode/img/brand/logo-white-color.png?t=c52fa5779305fb42a90ebe0145565af5);}
.navbar-vertical {box-shadow: none}
@media (max-width: 767.98px) {
    .navbar-collapse.collapsing, .navbar-collapse.show {background: #222; color: white!important;}
    .navbar-collapse .navbar-collapse-header {border-color: #1d1d1d;}
}


/* CARDS */
.card {border-color: #1d1d1d}
.card-header {color: white!important; background: #222!important; border-color: #1d1d1d!important}
.card-body {background-color: #2e2e2e; color: white!important}
.card-footer {background-color: #222}

/* DROPDOWNS */
.dropdown .disable-menu-hide {background-color: #1d1d1d!important}
.dropdown-menu {background: #1d1d1d}
.dropdown-menu .dropdown-item {color: white}
.dropdown-menu .dropdown-divider {border-color: #2e2e2e}

/* BADGES */

/* FORMS */
.form-control:disabled, .form-control[readonly] {background: #1d1d1d}
.form-control:focus {background-color: #1d1d1d; color: white;}
.form-control, .form-control-rounded, .form-control-prepended {color: white; background-color: #2e2e2e; border: 1px solid #1d1d1d}
.input-group-text {color: white; background: #1d1d1d; border-color: #1d1d1d}

/* MODALS */
.modal-header {color: white; background-color: #222}
.modal-body {color: white; background: #2e2e2e}
.modal-footer {color: white; background-color: #222}
.modal-title {color: white}

/* TABLES */
.table > thead > tr > td.table-selected, .table > tbody > tr > td.table-selected, .table > tfoot > tr > td.table-selected, .table > thead > tr > th.table-selected, .table > tbody > tr > th.table-selected, .table > tfoot > tr > th.table-selected, .table > thead > tr.table-selected > td, .table > tbody > tr.table-selected > td, .table > tfoot > tr.table-selected > td, .table > thead > tr.table-selected > th, .table > tbody > tr.table-selected > th, .table > tfoot > tr.table-selected > th {background-color: #2e2e2e}
.table .thead-light th {border-color: black;}
.thead-light tr {border-color: #191919}
.thead-light tr th {color: white; background: #1d1d1d!important;}
td, td a {color: white!important; background-color: #222}
.table th, .table td {border-color: #191919}
.table thead th {border-color: #191919}

/* FILE MANAGER */
.ace-chrome .ace_gutter {background: #333333; color: #8F908A}
.ace-chrome .ace_print-margin {width: 1px; background: #555651}
.ace-chrome {background-color: #272822; color: #F8F8F2}
.ace-chrome .ace_cursor {color: #F8F8F0}
.ace-chrome .ace_marker-layer .ace_selection {background: #49483E}
.ace-chrome.ace_multiselect .ace_selection.ace_start {box-shadow: 0 0 3px 0px #272822;}
.ace-chrome .ace_marker-layer .ace_step {background: rgb(102, 82, 0)}
.ace-chrome .ace_marker-layer .ace_bracket {margin: -1px 0 0 -1px; border: 1px solid #49483E}
.ace-chrome .ace_marker-layer .ace_active-line {background: #202020}
.ace-chrome .ace_gutter-active-line {background-color: #272727}
.ace-chrome .ace_marker-layer .ace_selected-word {border: 1px solid #49483E}
.ace-chrome .ace_invisible {color: #52524d}
.ace-chrome .ace_entity.ace_name.ace_tag, .ace-chrome .ace_keyword, .ace-chrome .ace_meta.ace_tag, .ace-chrome .ace_storage {color: #F92672}
.ace-chrome .ace_punctuation, .ace-chrome .ace_punctuation.ace_tag {color: #fff}
.ace-chrome .ace_constant.ace_character, .ace-chrome .ace_constant.ace_language, .ace-chrome .ace_constant.ace_numeric, .ace-chrome .ace_constant.ace_other {color: #AE81FF}
.ace-chrome .ace_invalid {color: #F8F8F0; background-color: #F92672}
.ace-chrome .ace_invalid.ace_deprecated {color: #F8F8F0; background-color: #AE81FF}
.ace-chrome .ace_support.ace_constant, .ace-chrome .ace_support.ace_function {color: #66D9EF}
.ace-chrome .ace_fold {background-color: #A6E22E; border-color: #F8F8F2}
.ace-chrome .ace_storage.ace_type, .ace-chrome .ace_support.ace_class, .ace-chrome .ace_support.ace_type {font-style: italic; color: #66D9EF}
.ace-chrome .ace_entity.ace_name.ace_function, .ace-chrome .ace_entity.ace_other, .ace-chrome .ace_entity.ace_other.ace_attribute-name, .ace-chrome .ace_variable {color: #A6E22E}
.ace-chrome .ace_variable.ace_parameter {font-style: italic; color: #FD971F}
.ace-chrome .ace_string {color: #E6DB74}
.ace-chrome .ace_comment {color: #75715E}
.ace-chrome .ace_indent-guide {background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWPQ0FD0ZXBzd/wPAAjVAoxeSgNeAAAAAElFTkSuQmCC) right repeat-y}

/* SCROLL BARS */
::-webkit-scrollbar {width: 8px;}
::-webkit-scrollbar-track {-webkit-border-radius: 10px; border-radius: 10px;}
::-webkit-scrollbar-thumb {-webkit-border-radius: 10px; border-radius: 0px; background: #191919;}
::-webkit-scrollbar-thumb:window-inactive {background: #191919;}

}
