css:
@-moz-document domain("next.voz.vn") {
/** Custom CSS By Ga9xVn - Make The Next VOZ Great Again **/
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-size: 13px!important;
    border-radius: 0px!important
}

a,p,span,h1,h2,h3,h4,h5,h6, dt, dd, time, .bbWrapper {
    font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif!important;
}

.bbCodeCode, .message-body {
    font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif!important;
    font-size: 12px;
}

body {
    background: #201F25;
    color: #FFFFFF;
    margin: 0;
    min-width: 360px;
    max-width: 1120px;
    margin: 0 auto;
}

a:hover, a:active {
    color: #FF6600;
    text-decoration: underline;
}

.p-body-inner {
    padding: 0;
    background: #f2f2f2;
}

.p-body-pageContent .block {
    margin-bottom: 15px;
}

.block-header {
    background: #869BBF url(https://forums.voz.vn/images/gradients/gradient_tcat.gif) repeat-x top left;
    height: 30px;
    font-weight: bold!important;
    padding: inherit;
    display: flex;
    align-items: center;
    padding-left: 10px;
}

.block-header a {
    color: #FFFFFF;
}

.node+.node {
    border: none;
}

.node-body {
    background: #F5F5FF;
    height: 50px;
}

.node-body .node-main {
    padding: 0;
    padding-left: 10px;
    border: 1px solid #fff;
}

.node-body .node-icon {
    background: #E1E4F2 url(https://forums.voz.vn/images/statusicon/forum_new.gif) no-repeat center;
    text-indent: -9999px;
    padding: 0;
    border-left: 1px solid #fff;
    border-bottom: 1px solid #fff;
}

.node-body .node-title {
    font-weight: bold;
}

.node-body .node-stats>dl.pairs.pairs--rows:first-child {
    border-right: 2px solid #fff;
}

.node-body .node-stats>dl.pairs.pairs--rows {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.node-body .node-stats {
    padding: 0;
    border: 1px solid #fff;
}

.node-body .node-stats dt {
    display: none;
}

.node-body .node-extra {
    padding: 0;
    padding-left: 10px;
    background: #E1E4F2;
    border: 1px solid #fff;
}

.node-body .node-extra span, .node-body .node-extra a {
    font-weight: bold;
    font-size: 12px!important;
}

.node-body .node-extra span {
    font-weight: normal;
    border: none;
}

.node-body .node-extra .node-extra-icon {
    display: none;
}

.p-breadcrumbs, .p-body-header, .p-body-main {
    display: flex;
    flex-direction: column;
    margin: 0 auto;
    background: transparent;
    padding: 8px;
    width: 100%;
    table-layout: unset;
}

.p-breadcrumbs, .p-body-header {
    background: transparent;
    padding: 12px 0;
    margin: 0;
    display: flex;
}

.p-breadcrumbs {
    display: flex;
    flex-direction: initial;
    padding: 12px;
}

.p-breadcrumbs li {
    float: inherit;
}

.p-breadcrumbs li a span {
    color: #23497C;
}

.p-body-main--withSidebar .p-body-content {
    padding: 0;
    width: 100%;
}

.p-body-sidebar {
    width: 100%;
}

.p-body-pageContent .block-outer:first-child {display: none;}

.block-container {
    background: transparent;
    border: 1px solid #B0BDC6;
    margin: initial;
}

nav.pageNavWrapper.pageNavWrapper--mixed {
    border: 1px solid #728fbf;
    border-right: none;
}

.pageNav-page {
    border-right: 1px solid #B0BDC6!important;
    background: #F5F5FF;
}

li.pageNav-page.pageNav-page--current {
    background: #E1E4F2;
    border: none;
    color: #000;
    font-weight: bold;
}

a.pageNav-jump.pageNav-jump--next {
    background: #F5F5FF;
}

.block--messages .message {
    padding-top: 30px;
    position: relative;
    background: transparent;
    border: 1px solid #B0BDC6;
}

.message-cell.message-cell--user {
    flex: 1;
    width: 100%;
    border: none;
    background: #E1E4F2;
    border-bottom: 1px solid #fff;
}

.message-inner {
    flex-direction: column;
    align-items: flex-start;
}

.message-cell.message-cell--user section {
    display: flex;
    align-items: center;
}

.message-cell.message-cell--user section .message-userArrow {
    display: none;
}

.message-cell.message-cell--user section .message-userDetails {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
}

.message-cell.message-cell--user section .message-avatar {
    margin-right: 8px;
}

.message-cell.message-cell--user section .message-avatar a span {
    font-size: 35px!important;
    font-weight: bold;
}

span.avatar-u1127286-m {
}

aside.message-signature {
    border-top: 1px solid #e2e4f2;
    padding-top: 15px;
    color: #000;
    margin-top: 15px;
}

.avatar img {
    background: transparent;
}

.bbCodeBlock-title {
    color: #000;
    font-weight: bold;
    background: transparent;
    padding-bottom: 6px;
}

.bbCodeBlock--expandable {
    border-left: none;
    border: 1px solid #d4d4d4;
    background: #E1E4F2;
}

.bbCodeBlock-title:after {
    font-weight: normal;
}

.bbCodeBlock-title a.bbCodeBlock-sourceJump:after {
    font-weight: normal;
}

a.bbCodeBlock-sourceJump {
    font-size: 12px!important;
}

.bbCodeBlock-content {
    padding-top: 6px;
}

form.block.js-quickReply .message-inner {
    display: flex;
    flex-direction: row;
    align-items: center;
}

form.block.js-quickReply .message-inner .message-cell.message-cell--user {
    display: none;
}

.message-userArrow {
    display: none;
}

.bbCodeBlock-expandContent {
    color: #000;
    font-style: italic;
}

.bbCodeBlock {
    border: 1px solid #eee;
    color: #000;
    font-style: italic;
}

header.message-attribution.message-attribution--split {
    position: absolute;
    top: 0;
    background: #5C7099 url(https://forums.voz.vn/images/gradients/gradient_thead.gif) repeat-x top left;
    width: 100%;
    left: 0;
    padding: 5px;
    height: 30px;
    color: #fff;
}

.pageNavSimple-el.pageNavSimple-el--first {
    border-right: 1px solid #728fbf;
}

.pageNavSimple-el.pageNavSimple-el--prev, .pageNavSimple-el.pageNavSimple-el--next {
    color: #728fbf;
    background: transparent;
    border-right: 1px solid #728fbf;
    margin-right: 0;
}

.pageNavSimple-el.pageNavSimple-el--current {
    color: #738FBF;
    margin: 0;
    background: transparent;
    border-color: #728fbf;
    border-right: 1px solid #728fbf;
}

.pageNav-jump {
    background: transparent;
    border-right: 1px solid #728fbf;
}

.pageNavSimple-el {
    width: 100%;
}

.p-title-value {
    font-weight: bold;
    font-size: 20px!important;
    color: #000;
    display: block;
}

.block-body .structItemContainer {
    margin-top: 15px;
}

.message .reactionsBar, .block-body .structItemContainer>.structItem:first-child, .reaction {
    display: none!important;
}

a.button.button--icon.button--icon--bolt, a.button--cta.button.button--icon.button--icon--write:before, a.button--cta.button.button--icon.button--icon--write .button-text, .node-body .node-extra span.label, .node-body .node-extra span.label-append, .block[data-widget-key="forum_overview_new_profile_posts"], .block[data-widget-key="forum_overview_new_posts"] {
    display: none;
}

a.button--cta.button.button--icon.button--icon--write {
    background: url(https://forums.voz.vn/images/buttons/newthread.gif) center no-repeat;
    border: none;
    width: 110px;
    height: 26px;
}

.p-title-pageAction {
    margin: 0;
}

.p-body-header {
    align-items: flex-start;
    padding: 12px 12px 0 10px;
}

.p-navSticky.p-navSticky--primary {
    position: unset;
}

nav.p-nav {
    margin: 0 auto;
}

.p-sectionLinks {
    margin: 0 auto;
    width: 100%;
    padding-top: 5px;
}

a.p-navgroup-link {
    background: transparent;
}

.p-navgroup {
    margin-left: 0!important;
}

.p-nav-opposite {
    margin-right: 0;
}

html#XF {
    background: #201F25;
}

header#header {
    background: transparent;
    padding: 15px 0;
}

.p-pageWrapper {
    background: transparent;
}

.pairs.pairs--justified>dd {
    text-align: left;
    float: left;
}

h3.block-minorHeader a {
    font-weight: bold;
}

h3.block-minorHeader {
    font-weight: bold;
}

a.actionBar-action.actionBar-action--reply {
    background: url(https://forums.voz.vn/images/buttons/quote.gif) no-repeat center;
    width: 75px;
    height: 25px;
    display: block;
    font-size: 0px!important;
}

a.actionBar-action.actionBar-action--report {
    background: url(https://forums.voz.vn/images/buttons/report.gif) no-repeat center;
    font-size: 0px!important;
    width: 22px;
    height: 25px;
}

span.label-append, span.label {
    display: none;
}

.block-outer {
    background: transparent;
}

.message-cell.message-cell--main {
    background: #f5f5ff;
}

.p-breadcrumbs>li:before {
    color: #23497b;
    content: "\f105";
    padding-right: 6px;
}

.p-breadcrumbs>li:after, .p-breadcrumbs>li:first-child:before {
    display: none
}

h4.menu-header {
    color: #5c7099;
    font-weight: bold;
}

a[data-xf-click="overlay"]:before {
    display: none;
}

a[data-xf-click="overlay"] span {
    padding: 0 12px;
    color: #fff;
    background: #23497b;
}

a.actionBar-action.actionBar-action--edit {
    background: url(https://forums.voz.vn/images/buttons/edit.gif) center no-repeat;
    font-size: 0px!important;
    height: 25px;
    width: 75px;
}

.message-actionBar.actionBar {
    margin-top: 15px;
    padding-top: 12px;
}

.actionBar-set.actionBar-set--internal {
    float: none;
    padding: 0;
    margin: 0;
    display: flex;
    align-items: center;
}

a.actionBar-action.actionBar-action--delete {
    height: 22px;
    margin-top: 3px;
    display: block;
    width: 75px;
    background: #F44336;
    color: #fff;
    text-align: center;
    border-radius: 10px!important;
    font-size: 12px!important;
    display: flex;
    align-items: center;
    justify-content: center;
}

.actionBar-set.actionBar-set--external {
    float: right;
    margin: 0;
}

.shareButtons.shareButtons--iconic {
    padding: 0 12px;
}

.structItem-cell.structItem-cell--icon {
    vertical-align: middle;
    padding-top: 16px;
}

.pageNavWrapper--mixed .pageNav {
    display: flex;
}

.block-outer-main {
    float: right;
}

.pageNavSimple {
    display: none!important;
}

.pageNav-page {
    display: block!important;
}

.block-outer.block-outer--after {
}

ul.pageNav-main {
    display: flex;
}

.block-outer:first-child {
    display: none;
}

div.block[data-type="thread"] .block-outer:first-child {
    display: none;
}

.block-filterBar {
    background: #5C7099 url(https://forums.voz.vn/images/gradients/gradient_thead.gif) repeat-x top left;
}

.block-filterBar a {
    color: #fff;
}

ul.message-attribution-opposite.message-attribution-opposite--list .message-newIndicator {
    display: none;
}

h3.menu-header {
    color: #5c7099;
    font-weight: bold;
}

.message-cell.message-cell--user {
}

.message-attribution-user .avatar {
    display: flex;
    margin-right: 5px;
}

li.message-attribution-user {
    display: flex;
    justify-content: center;
    align-items: center;
}

.message-attribution.message-attribution--plain ul.listInline.listInline--bullet {
    display: flex;
    align-items: center;
}

.message-cell.message-cell--main .message-actionBar.actionBar {
}

#profile-posts .message--simple .message-cell.message-cell--user {
    display: none;
}

#profile-posts .block--messages .message {
    padding-top: 0;
}

span.avatar.avatar--l.avatar--default.avatar--default--dynamic span {
    font-size: 50px!important;
}

.p-title {
    width: 100%;
    display: flex;
}

a.p-navgroup-link.p-navgroup-link--textual.p-navgroup-link--logIn span {
    background: transparent;
}

.overlay {
    background: #e2e4f2;
}

footer#footer {
    background: #3E5C92;
    color: lightblue;
    height: 200px;
}

.p-footer-linkList>li a {
    color: lightblue;
}

.p-footer-copyright {
    height: 150px;
    padding-bottom: 25px;
    display: flex;
    justify-content: center;
    align-items: flex-end;
}

.fr-wrapper.show-placeholder {
    background: #ffffff!important;
}

.fr-toolbar.fr-ltr.fr-desktop.fr-top.fr-basic {
    background: #E1E1E2!important;
    font-weight: bold!important;
}

@media (max-width: 768px) {
    .structItem-minor {
        float: left;
        padding: 4px;
        height: 30px;
    }

    .p-breadcrumbs>li {
        display: inherit;
    }

    .p-body-main {
        padding: 0;
        margin: 0;
    }

    .node-body .node-extra {
        display: none
    }

    .node-body .node-main {
        border-bottom: none;
        padding-top: 5px;
    }

    .p-body-pageContent .block-outer {
        display: block;
        padding: 12px;
    }

    .pageNavWrapper--mixed .pageNavSimple {
        display: inline-flex;
        width: 100%;
    }

    .p-body-header {
        margin-bottom: 12px;
        padding-top: 12px;
    }

    .structItem-cell.structItem-cell--meta {
        display: none;
    }

    .structItem-cell.structItem-cell--latest {
        padding: 1px;
        margin: 0;
    }
}

@media (max-width: 992px) {
    .p-body-sidebar {
        display: flex;
        flex-direction: column;
        margin: unset;
        margin-top: 15px;
    }

    .p-body-sidebar .block {
        width: 100%;
        margin: 0;
        margin-bottom: 15px;
        flex: unset;
    }

    .p-body-sidebar .block .block-container {
        border-left: none;
        border-right: none;
    }

    .p-body-sidebar:after {
        display: none;
    }
}
}
