@author         MIT License Copyright (c)2020 Yubelious!
==/UserStyle== */

@-moz-document url-prefix("https://duelingnexus.com/") {
/*SECTION 1 NONAVBAR*/
/*This affects the site while the navbar is colapsed.*/
.app-navbar-hidden #navbar, .app-navbar-hidden #socket-connection, .app-navbar-hidden #login-area, .app-navbar-hidden #register-area, .app-navbar-hidden #recover-area {
    display: none
}
.app-navbar-hidden, .app-navbar-hidden div[data-v-26bcedb3]/*Login area*/, .app-navbar-hidden div[data-v-26bcedb3]/*Login area*/ .app-navbar-hidden #news-area, .app-navbar-hidden #nexus-gui-popup-background {
    height: 100%;
    width: 100% !important;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0 !important;
}
}

@-moz-document url-prefix("https://duelingnexus.com/") {
/*SECTION 2 GLOBAL*/
/*This code affects the whole site.*/
body {
    font-size: 1em;
    height: 100vh;
    margin: 0;
    position: relative;
}
article {
    padding: 1%
}
div#app {
    margin-bottom: 40px
}
#navbar button, form button, article button, input[type="text"], form input[type="password"], a, input, label, article , #navbar button:focus, #navbar button:hover, form button:focus, form button:hover, button:focus, article button:hover, a:focus, a:hover, input:focus, input:hover, label:focus, label:hover, form button:focus, form button:hover {
    -moz-transition-duration: .2s;
    -o-transition-duration: .2s;
    transition-duration: .2s;
}
#home-area {
    background-color: rgba(0, 0, 0, 0);
    border-width: 0;
}
#home-area h2, #home-area button {
    display: none
}
#duel-area, #gamelist-area[data-v-33d70790], #custom-game-area[data-v-7fa67ee2], #decks-area[data-v-417e0dbe], #my-profile[data-v-402aeb34], #leaderboard-page, #donate-area[data-v-5ccd2ad4] {
    box-sizing: border-box;
    display: block;
    overflow-y: auto;
    min-height: 0;
    max-height: 100vh;
    width: auto;
    min-width: 0;
    max-width: 100vw;
    margin: 0;
    position: absolute;
    right: 12%;
    left: 12%;
}
#duel-area {
    height: 67.5%;
    top: 25%;
}
article button, .fake-button[data-v-417e0dbe]/*Refers to the fake button(s) on the Deck Editor page.*/, .fake-button[data-v-402aeb34]/*Refers to the fake button(s) on the My Profile page.*/, select, input[type="text"] {
    font-size: 2.25vh;
    text-align: left;
    background-color: #111925;
    border-color: #595e67;
    border-width: 1px;
    padding: .5vh;
}
article button:hover, .fake-button[data-v-417e0dbe]:hover/*Refers to the fake button(s) on the Deck Editor page.*/, .fake-button[data-v-402aeb34]:hover/*Refers to the fake button(s) on the My Profile page.*/ {
    background-color: #4f1158;
    border-color: #84598b;
}
article button img, article .fake-button img {
    float: right;
    height: 4vh;
    width: 4vh;
}
a, input, label {
    opacity: .7;
    cursor: pointer;
}
a:hover, input:hover, label:hover {
    opacity: 1
}
label {
    line-height: 1
}
#gamelist-area[data-v-33d70790], #custom-game-area[data-v-7fa67ee2], #decks-area[data-v-417e0dbe], #my-profile[data-v-402aeb34], #leaderboard-page, #donate-area[data-v-5ccd2ad4] {
    height: 75%;
    top: 15%;
}
article {
    border: 1px solid #ffffff26;
    background-color: #101522b4;
    border-radius: 7px;
}
#navbar, #socket-connection {
	opacity: 1;
	animation-name: fadein;
	animation-iteration-count: 1;
	animation-timing-function: ease-in;
	animation-duration: .5s
}
#home-area {
    animation: disappear 2s;
    font-size: 5vh;
    background-color: rgba(0, 0, 0, .5);
    height: 100%;
    width: 100%;
    margin: 0;
    padding-top: 30%;
    opacity: 0;
    z-index: -1;
    position: absolute;
    top: 0;
    left: 0;
}
@keyframes disappear {
    0% {opacity: 1}
    25% {opacity: 1}
    100% {opacity: 0}
}
@keyframes fadein {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}
@media (orientation: landscape) {
h1, h2, h3 {
    letter-spacing: .1vw
}
h1 {
    font-size: 2vw
}
h2 {
    font-size: 1.75vw;
    margin: 1%;
}
h3 {
    font-size: 1.5vw
}
h4 {
    font-size: 1.25vw
}
li {
    font-size: 1vw
}
}
@media (orientation: portrait) {
h1, h2, h3 {
    letter-spacing: .2vh
}
h1 {
    font-size: 4vh
}
h2 {
    font-size: 3.5vh;
    margin: 1%;
}
h3 {
    font-size: 3vh
}
h4 {
    font-size: 2.5vh
}
li {
    font-size: 2vh
}
}
}

@-moz-document url("https://duelingnexus.com/login"), url("https://duelingnexus.com/register"), url("https://duelingnexus.com/recover"), url("https://duelingnexus.com/privacy_policy.html") {
/*SECTION 3 LOGIN*/
/*This section applies to the pages you can access without logging in.*/
#navbar-middle:before {
    display: none
}
#news-area {
    height: 100%;
    width: 100%;
    max-width: 100vw;
    margin: 0;
    padding: 0;
    position: absolute;
}
#news-area:before, div#app:before {
    content: ' ';
    background: url('https://cdn.discordapp.com/attachments/670405796747673620/754166777587564655/Logo_Full.png') no-repeat center;
    background-size: auto 100%;
    height: 40%;
    width: 100%;
    display: block;
    opacity: 1;
    position: absolute;
    top: auto;
    right: 0;
    bottom: 30%;
    left: auto;
}
#socket-connection, #navbar-right, #news-area h3, #news-area p, #news-area strong, #news-area em {
    font-size: 0;
    opacity: 0;
}
#login-loading {
    font-size: 4vh;
    margin: 0;
    padding-top: 30%;
}
a {
    padding: 0;
    text-decoration: underline;
    opacity: .75;
}
a:focus {
    font-style: italic
}
a:hover {
    opacity: 1
}
h1, h2, h3 {
    font-variant: small-caps;
    line-height: 1;
    text-transform: none;
    -moz-user-select: none;
    -o-user-select: none;
    user-select: none;
    margin: .5vh;
    padding: 0;
}
#newsMain {
    height: 100vh;
    width: 100vw;
    position: relative;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
}
.news_list {
    margin: 5vh 2.5vw;
    height: 85vh;
    width: auto;
    max-width: 100%;
    overflow-y: scroll;
    position: absolute;
    top: 0;
}
}

@-moz-document url("https://duelingnexus.com/home") {
/*SECTION 4 HOME*/
/*This section affects the page that loads when you first login*/
#navbar-middle > button:nth-of-type(1) {
    background-color: #4f1158 !important
}
#navbar-right {
    z-index: 2
}
#news-area {
    box-sizing: border-box;
    opacity: 1;
    display: block;
    overflow-y: auto;
    min-height: 0;
    max-height: 100vh;
    height: 60%;
    width: auto;
    min-width: 0;
    max-width: 100vw;
    margin: 0;
    position: absolute;
    top: 30%;
    right: 12%;
    left: 12%;
}
article:not(#home-area) {
    animation: appear 2s
}
@keyframes appear {
	0% {
		opacity: 0;
	}
    11% {
        opacity: 0;
    }
	100% {
		opacity: 1;
	}
}
}

@-moz-document url("https://duelingnexus.com/duel"), url("https://duelingnexus.com/gamelist"), url("https://duelingnexus.com/hostgame") {
/*SECTION 5 DUEL ZONE*/
/*This section effects the pages that show up when you click the Duel Zone button.*/
#app {
    overflow-y: auto
}
#navbar-middle > button:nth-of-type(2) {
    background-color: #4f1158 !important
}
/*duel area*/
#duel-area p {
    margin: 0
}
h3, #duel-area > div > p:nth-of-type(7) {
    text-align: left;
    margin: 0;
}
h3 {
    padding: 1vh 0 .5vh 10%
}
hr {
    border-color: #595e67;
    border-style: solid;
    border-width: 0 0 1px 0;
    width: 100%;
}
select {
    height: 100%;
    padding: 0;
}
#duel-area > div > p > button {
    font-size: 1.25vw;
    height: 90%;
    width: 90%;
    padding: 3% 3% 3% 6%;
}
#duel-area > div > p > button, select {
    text-align: left
}
#duel-area button img {
    height: 2vw;
    width: 2vw;
    margin: 0;
    padding: 5% 1%;
    float: right;
}
#duel-area > div {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: .1fr .5fr .1fr .5fr 1fr .1fr .5fr 1fr .1fr .5fr 1fr .1fr .5fr 1fr .1fr;
    grid-template-areas: "hr1 hr1 hr1" "serversettings1 serversettings1 serversettings1" "hr2 hr2 hr2" "quickplay duelstatus duelstatus" "quickplay1 quickplay2 quickplay3" "hr3 hr3 hr3" "singlemode . ." "singlemode1 singlemode2 singlemode3" "hr4 hr4 hr4" "competitiveplay currentrating currentrating" "competitiveplay1 competitiveplay2 competitiveplay3" "hr5 hr5 hr5" "customgame . ." "customgame1 . .";
}
#duel-area > div > h3:first-of-type {
    grid-area: quickplay
}
#duel-area > div > h3:nth-of-type(2) {
    grid-area: singlemode
}
#duel-area > div > h3:nth-of-type(3) {
    grid-area: competitiveplay
}
#duel-area > div > h3:nth-of-type(4) {
    grid-area: customgame
}
#duel-area > div > h3:nth-of-type(5) {
    display: none
}
#duel-area > div > p:first-of-type {
    grid-area: quickplay1
}
#duel-area > div > p:nth-of-type(2) {
    grid-area: quickplay2
}
#duel-area > div > p:nth-of-type(3) {
    grid-area: quickplay3
}
#duel-area > div > p:nth-of-type(4) {
    grid-area: singlemode1
}
#duel-area > div > p:nth-of-type(5) {
    grid-area: singlemode2
}
#duel-area > div > p:nth-of-type(6) {
    grid-area: competitiveplay1
}
#duel-area > div > p:nth-of-type(7) {
    grid-area: currentrating;
    text-transform: uppercase;
    text-align: right;
    padding: 1vh 10% .5vh 0;
}
#duel-area > div > p:nth-of-type(8) {
    grid-area: customgame1
}
#duel-area > div > p:nth-of-type(9) {
    grid-area: serversettings1
}
hr:first-of-type {
    grid-area: hr1
}
hr:nth-of-type(2) {
    grid-area: hr2
}
hr:nth-of-type(3) {
    grid-area: hr3
}
hr:nth-of-type(4) {
    grid-area: hr4
}
hr:nth-of-type(5) {
    grid-area: hr5
}
/*gamelist*/
#gamelist-buttons[data-v-33d70790] {
    height: 6vh;
    max-height: 10%;
    overflow: visible;
    margin: 0;
}
#gamelist-buttons[data-v-33d70790] button {
    line-height: 5vh
}
#gamelist-filters[data-v-33d70790] {
    max-height: 20%;
    overflow: visible;
    margin: 0;
}
#gamelist-filters[data-v-33d70790] p {
    margin: 2%
}
#gamelist-container[data-v-33d70790] {
    overflow-y: scroll;
    overflow-x: hidden;
    height: 64%;
}
/*table*/
td, th {
    font-size: 2.25vh;
    max-width: 15vw;
}
/*custom game area*/
#custom-game-area button, #custom-game-area select {
    font-size: 1vw;
    height: 100%;
    width: 100%;
}
#custom-game-area p {
    margin: 0;
    position: relative;
}
#custom-game-area[data-v-7fa67ee2] {
    display: grid;
    grid-gap: 5%;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 1fr .75fr .75fr .75fr 1fr .75fr .75fr .75fr .75fr 1fr;
    grid-template-areas: "header1 header1 header1" "backbutton . ." "select1 select2 select3" "morebutton . ." "header2 header2 header2" "select4 select5 select6" "select7 select8 select9" "select10 select11 select12" "createbutton createbutton save";
}
#custom-game-area p:nth-of-type(1) {
    grid-area: backbutton
}
#custom-game-area p:nth-of-type(2) {
    grid-area: select1
}
#custom-game-area p:nth-of-type(3) {
    grid-area: select2
}
#custom-game-area p:nth-of-type(4) {
    grid-area: select3
}
#custom-game-area p:nth-of-type(5) {
    grid-area: morebutton
}
#custom-game-area p:nth-of-type(6) {
    grid-area: select4
}
#custom-game-area p:nth-of-type(7) {
    grid-area: select5
}
#custom-game-area p:nth-of-type(8) {
    grid-area: select6
}
#custom-game-area p:nth-of-type(9) {
    grid-area: select7
}
#custom-game-area p:nth-of-type(10) {
    grid-area: select8
}
#custom-game-area p:nth-of-type(11) {
    grid-area: select9
}
#custom-game-area p:nth-of-type(12) {
    grid-area: select10
}
#custom-game-area p:nth-of-type(13) {
    grid-area: select11
}
#custom-game-area p:last-of-type {
    grid-area: createbutton
}
#custom-game-area p:last-of-type button {
    font-size: 1.5vw;
    text-align: center;
}
}

@-moz-document url("https://duelingnexus.com/decks") {
/*SECTION 6 DECK EDITOR*/
/*This section applies to the page that loads when you click the Deck Editor button.*/
#app {
    overflow-y: auto
}
#navbar-middle > button:nth-of-type(3) {
    background-color: #4f1158 !important
}
h2[data-v-417e0dbe] {
    overflow: visible;
    max-height: 6%;
}
#decks-buttons[data-v-417e0dbe] {
    overflow: visible;
    max-height: 15%;
    margin-bottom : 1%;
}
#decks-buttons button, button[data-v-417e0dbe], .fake-button[data-v-417e0dbe] {
    text-align: center;
    box-sizing: border-box;
    width: 24%;
    padding: 1vh;
    margin-right: 1%;
}
#decks-container {
    overflow-y: auto;
    max-height: 75%;
    position: absolute;
    bottom: 2%;
}
table[data-v-417e0dbe] {
    width: 99%;
    table-layout: fixed;
}
.td-big {
    text-align: left;
    text-overflow: ellipsis;
    white-space: nowrap;
    width: 65%;
    min-width: 0;
    overflow: hidden;
}
td {
    font-size: 1vw;
    text-align: center;
    padding: .75vw;
}
}

@-moz-document url("https://duelingnexus.com/profile") {
/*SECTION 7 PROFILE*/
/*This section applies to the page that loads when you click the Profile button.*/
#navbar-middle > button:nth-of-type(4) {
    background-color: #4f1158 !important
}
.remove-custom-button[data-v-402aeb34], .fake-button[data-v-402aeb34] {
    text-align: center;
    line-height: 1;
    box-sizing: border-box;
    width: 70%;
    padding: 1vw;
}
}

@-moz-document url("https://duelingnexus.com/leaderboard") {
/*SECTION 8 LEADERBOARD*/
/*This section applies to the page that loads when you click the Leaderboard button.*/
#navbar-middle > button:nth-of-type(5) {
    background-color: #4f1158 !important
}
}

@-moz-document url-prefix("https://duelingnexus.com/donate") {
/*SECTION 9 DONATE*/
/*This section applies to the page that loads when you click the Donate button.*/
#navbar-middle > button:nth-of-type(6) {
    background-color: #4f1158 !important
}
}

@-moz-document url-prefix("https://duelingnexus.com/game"), url-prefix("https://duelingnexus.com/editor") {
/*SECTION 10 GAMEGLOBAL*/
/*This section applies to the game and editor pages. Much of the code is shared.*/
html {
    overflow: auto
}
div {
    line-height: 1
}
h4 {/*Big headers.*/
    font-size: 1.75vw
}
h4:not(#card-name) {
    min-height: 2vw
}
h6 {/*Small headers.*/
    font-size: 1.5vh
}
#editor-container, #game-container, .engine-dark-box {
    margin: 0
}
#card-column, #editor-decks-column, #game-siding-column {
    height: 40vw
}
#card-column {/*Moves the left column to the left wall of the screen.*/
    width: 24vw;
    float: left;
    margin: 0 .5vw;
}
#editor-decks-column, #game-siding-column {
    width: 35.5vw;
    padding: 0;
}
#editor-search-column, #game-siding-column-2 {
    height: 40vw;
    width: 36.3vw;
    overflow: hidden;
    margin: 0 .5vw;
    padding: 0;
    position: relative;
}
.engine-text-box, #card-description-box, #editor-main-deck, #siding-main-deck, #editor-extra-deck, #editor-side-deck, #siding-extra-deck, #siding-side-deck {
    border-width: 1px;
    border-color: #595e67;
    border-style: solid;
}
.engine-dark-box, #card-column, #card-name, #card-picture-container, #card-description-box, #editor-main-deck, #siding-main-deck, #editor-extra-deck, #editor-side-deck, #siding-extra-deck, #siding-side-deck, .engine-text-box, #editor-search-results {
    box-sizing: border-box
}
#card-name, #editor-deck-main-title, #editor-search-title, #siding-deck-main-title,#editor-deck-extra-title, #editor-deck-side-title, #siding-deck-extra-title, #siding-deck-side-title {
	margin: 0
}
#card-picture-container, #card-picture, #siding-main-deck, #editor-main-deck {
    height: 20.5vw !important;
    min-height: 0;
}
#card-picture-container, #siding-main-deck, #editor-main-deck {
    margin: 1vw 0 .5vw 0
}
#card-picture {
    width: auto;
    max-width: 100%;
}
#siding-extra-deck, #siding-side-deck, #editor-extra-deck, #editor-side-deck {
    height: 5.25vw !important
}
#siding-extra-deck, #editor-extra-deck {
    margin: .5vw 0;
}
#siding-side-deck, #editor-side-deck {
    margin: .5vw 0 0;
}
.engine-text-box {
    font-size: 1.5vw;
    height: 2.3vw;
    padding: .15vw;
    border-color: #AAAAAA;
    border-width: 1px;
}
/*ENGINE BUTTONS*/
.engine-button:not(.game-phase-button), select#rs-ext-banlist {
    -moz-transition-duration: .2s;
    -o-transition-duration: .2s;
    transition-duration: .2s;
    font-family: "Arial Narrow", inherit;
    border-width: .15vw;
    cursor: pointer;
    min-height: 4vh;/* If this causes problems, return to 0;*/
    padding: 0 .5vw;
}
.engine-button:active {
    -moz-transition-duration: .2s;
    -o-transition-duration: .2s;
    transition-duration: .2s;
    font-style: italic;
}
table#game-field {
    margin-left: 1.5vw;
}
}

@-moz-document url-prefix("https://duelingnexus.com/editor/") {
/*SECTION 11 EDITOR*/
/*This section affects the Editor pages, where you build your deck.*/
/*Editor top*/
@media (orientation: landscape) {
body {
    overflow: auto
}
#editor-menu-container {
    background-image: linear-gradient(-20deg, #121724, #010711);
    border-width: 0 0 1px 0;
    border-style: solid;
    border-color: #595e67;
    margin: 0 0 1% 0;
}
#editor-menu-content {
    width: 100%;
}
#editor-menu-title, .engine-button, span {
    font-size: 2.25vh
}
#rs-ext-menu-expand {
    height: 2.5vw;
    width: 2.5vw;
    margin: 0;
    top: 8vh;
}
#rs-ext-banlist, .engine-button {
    border: 1px solid
}
#rs-ext-banlist, #editor-menu-title:nth-of-type(3), #editor-select-cardpool, .engine-button-navbar {
    font-size: 1.2vw;
    color: white;
    height: 4vh;
    float: left;
    margin: 2vh 0 0 .5vh;
}
#rs-ext-banlist, #editor-select-cardpool, .engine-button-default {
    background-color: #111925
}
.engine-button-primary {
    background-color: #00c4ff
}
.engine-button-danger {
    background-color: #e31212
}
.engine-button[disabled], .engine-button:disabled {
    background-color: #5e111d;
    cursor: not-allowed;
}
.engine-button[active], .engine-button:active {
    background-color: #8518c3
}
#editor-menu-container, #editor-menu-content, #editor-menu-title {
    height: 8vh
}
#editor-menu-content {
    padding: 0;
    left: 0;
}
#editor-menu-title:first-of-type {
    line-height: 3.5;
    text-align: right;
    word-wrap: break-word;
    overflow-y: auto;
    float: right;
    width: auto;
    max-width: 25vw;
    padding-right: 1vh;
}
#editor-menu-spacer {
    display: none
}
/*Card Column*/
#card-column {
    display: grid;
    grid-template-areas: "header" "picture" "description";
}
#card-name {
	grid-area: header;
    height: max-content;
    padding: .5%;
}
#card-picture {
	grid-area: picture
}
#card-description-box {
	grid-area: description;
    font-size: 2.5vh;
    text-align: justify;
    cursor: s-resize;
    overflow-x: hidden;
    overflow-y: auto;
    padding: .5vh 1vh;
