css:
@-moz-document url(https://trello.com) {
  .window {
      width: 70%
  }
  .window .window-main-col {
      width: 70%
  }
  .window-sidebar {
      width: 20%
  }
  .list-wrapper {
      width: 33%;
  }
  .list-cards > * {
      width: 100%;
      max-width: 100%;
  }
  .list-card-members {
      margin-top: -20px;
  }
  .list-card-title {
      float: left;
      margin-right: 10px;
  }
  .list-card-members .member, .list-card-members .member .member-avatar {
      height: 20px;
      width: 20px;
  }
  .body-card-label-text-on .card-label.mod-card-front, 
  .body-card-label-text .card-label.mod-card-front {
      height: 12px;
      line-height: 13px;
  }
  .card-label.mod-card-front {
      font-size: 10px;
  }
}
