css:
@-moz-document url-prefix("https://brick-hill.trade") {

* {
    text-decoration: none !important;
}

a[href="https://brick-hill.trade/discord"], a[href="https://www.patreon.com/brickhilltrade"], a[href="https://chrome.google.com/webstore/detail/bffdjkmpmipknmnhnncaolghmmlgejbj/"], ins.adsbygoogle, .footer {
    display: none !important;
}

.main-panel, .dropdown.show .dropdown-menu:not(.inner), .dropdown-menu.show, .modal-content {
    background: #202020 !important;
    font-weight: normal !important;
}
.dropdown-menu:before {
    border-bottom-color: #202020 !important;
}
.card, .breadcrumb, .panel-header,
.sidebar[data-color="red"]:after, 
.sidebar[data-color="blue"]:after,
.sidebar[data-color="green"]:after, 
.sidebar[data-color="orange"]:after,
.sidebar[data-color="yellow"]:after, 
.sidebar[data-color="black"]:after {
    background: #181818 !important;
}

.card-chart .card-header .card-title, .stats, .breadcrumb a[href="https://brick-hill.trade"], a:not([href]):not([tabindex]), .form-control, .table th, .card-chart .table td, .card .card-header .card-title,
.card .card-body, .dropdown.show .dropdown-menu:not(.inner) {
    color: #bdbdbd !important;
}
.breadcrumb a[href="https://brick-hill.trade"]:hover {
    color: #fff !important;
}
.breadcrumb a[href="https://brick-hill.trade"] {
    transition: color .3s;
}
a:not([href]):not([tabindex]) {
    transition: background-color .3s;
}

.dropdown-item {
    color: white !important;
}

.dropdown-item:hover {
    background: #313131 !important;
}

.btn-info, .btn {
    background-color: #1e1e1e !important;
    border: 1px solid #393939 !important;
}

.dropdown-item:last-child:hover {
    background: transparent !important;
}

.alert.alert-success, .alert.alert-warning {
    background-color: #7b7b7b !important;
}

a, .modal-title {
    color: white;
    font-weight: bolder;
}
a:hover, label {
    color: #c6c6c6;
}
a {
    transition: color .3s;
}
.form-text {
    color: white !important;
}
.card-img:hover {
filter: brightness(70%) saturate(1);
}
.card-img {
    transition: filter .3s;
}
.close {
    color: white !important;
    text-shadow: none !important;
}
.btn-danger {
    background: transparent !important;
    border: 1px solid #dd2020 !important;
    color: #dd2020 !important;
    border-radius: 2.5px;
}
.btn-danger:hover {
    background: #dd20200a !important;
}

.card-chart .table td, .table td {
    border-bottom-color: #2a2a2a !important;
    border-top-color: #2a2a2a !important;
}
.dropdown-menu .dropdown-divider, .modal-footer {
    background-color: transparent !important;
    border-top: 1px solid #ffffff17 !important;
}
.modal-header {
    background-color: transparent !important;
    border-bottom: 1px solid #ffffff17 !important;
}

.table td a[href="https://brick-hill.trade/user/"] {
    color: white !important;
}

.dropdown.show .dropdown-menu:not(.inner) {
    color: white;
    font-weight: bold;
}

.breadcrumb-item.active {
    color: #848484;
}

.sidebar .nav li > a {
    border-radius: 2.5px;
}
.sidebar {
    box-shadow: none !important;
}

.sidebar .nav li > a:hover {
    background-color: #ffffff05 !important;
}
a:not([href]):not([tabindex]):hover {
    background-color: #ffffff08 !important;
    cursor: pointer;
}

.card-chart .dropdown .btn {
    border-color: #bdbdbd;
    color: #bdbdbd;
}
.card-chart .dropdown .btn:hover {
    border-color: #fff;
    color: #fff;
}


.form-control {
    background: #262626 !important;
    border-radius: 2.5px !important;
    border: 1px solid #262626 !important;
}
}
