css:
@-moz-document domain("menway.interia.pl") {
html,body{
    background: #000;
    color: #fff;
}
:root{
    scrollbar-color: #c7282d #000;
}
a,h1,h2,h3,h4{
    color: #c7282d !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #000 !important;
    color: #fff !important;
    border: 1px solid #c7282d !important;
}
input[type="submit"]:hover{
    background: #c7282d !important;
    color: #fff !important;
}
/*HEADER*/
.common-header{
    background: #000;
}
.common-header__svg-path,.common-header__icon--svg-path{
    fill: #c7282d;
}
.common-header__popup{
    background: #000;
    border: 1px solid #c7282d;
    top: 81px;
}
#common-search .search-hide-button{
    right: 38px;
    top: 25px;
    color: #c7282d;
    background: #000;
    border: 1px solid #c7282d;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.page-top,.breadcrumbs,.page-container,.page-footer-main{
    background: #000;
}
.tile-carousel-category,.tile-magazine-category,.brief-button-more{
    background: transparent;
    border: 2px solid #c7282d;
}
.tile-carousel-category:hover,.tile-magazine-category:hover,.brief-button-more:hover{
    background: #c7282d;
}
.carousel-mixer .carousel-mixer-left, .carousel-mixer .carousel-mixer-right, .carousel-mixer .ex-carousel-mixer-button{
    background: #c7282d7a;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.inpl-gallery{
    background: #000;
}
.recommendation-tabs .tabs .tab.active{
    border-color: #c7282d;
    color: #c7282d;
}
.recommendation-tabs .tabs .tab.active::before{
    border-color: #c7282d transparent transparent transparent;
}
.is-comment-bubble{
    background: transparent;
    border: 2px solid #c7282d;
}
.is-comment-bubble::after{
    display: none;
}
/*KOMENTARZE*/
.forum__header-filter-button,.forum__header-label-count,.forum__add-bottom-message a{
    color: #c7282d !important;
}
.forum__header-filter-button:hover,.forum__add-bottom-message-inside{
    color: #fff !important;
}
.forum__header-filter-button:active, .forum__header-filter-button:hover{
    background: #c7282d;
    color: #fff;
}
.forum__header-filter-button::before, .forum__header-filter-button::before{
    filter: invert(18%) sepia(53%) saturate(6054%) hue-rotate(349deg) brightness(83%) contrast(85%);
}
.forum__header-filter-button:hover::before, .forum__header-filter-button:hover::before{
    filter: invert(100%) sepia(100%) saturate(0%) hue-rotate(329deg) brightness(109%) contrast(103%);
}
.forum__add-nohate-container{
    background: #000;
    border: 0;
}
.forum__comment-option-abuse:hover,.forum__comment-block{
    background-color: #000;
}
.forum__add-bottom-message::before{
    background-color: #c7282d;
}
.forum--is-big .forum__add-bottom-submit{
    background: transparent;
    border: 2px solid #c7282d;
}
.forum--is-big .forum__add-bottom-submit:hover{
    background: #c7282d;
}
.forum--is-big .forum__comment-action--promote:hover{
    background: #c7282d;
}
.forum__comment-action--promote:hover .forum__comment-action--promote-text{
    color: #000 !important;
}
.forum__comment-action--voteup::before{
    filter: invert(43%) sepia(27%) saturate(6434%) hue-rotate(119deg) brightness(101%) contrast(101%);
}
.forum__comment-action--votedown::before{
    filter: invert(20%) sepia(100%) saturate(6877%) hue-rotate(358deg) brightness(104%) contrast(113%);
}
.forum__comment-action:hover{
    background: transparent;
    border: 3px solid #c7282d;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
.pagination-list .paginator-el.current span,.pagination-list .paginator-el.default:hover span{
    background: #c7282d;
}
.pagination-list .paginator-el.default span{
    border: 3px solid #c7282d
}
.pagination-list .next span, .pagination-list .prev span{
    border-color: #c7282d;
    color: #c7282d !important;
}

.pagination-list .next:hover span, .pagination-list .prev:hover span,.pagination-list .next.enable:hover span, .pagination-list .prev.enable:hover span{
    background: #c7282d;
    color: #fff !important;
    border-color: #c7282d;
}
.pagination-list .next span::after, .pagination-list .prev span::after,.pagination-list .next span::after{
    background-color: #c7282d;
    border: 0;
    height: 39px;
    width: 39px;
}
.pagination-list .next span::after{
    background-image: url(data:image/svg+xml,%3C%3Fxml%20version%3D%221.0%22%20encoding%3D%22UTF-8%22%20standalone%3D%22no%22%3F%3E%0A%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20enable-background%3D%22new%200.5%200.5%2016%2012%22%20height%3D%2216%22%20viewBox%3D%220.5%200.5%2012%2016%22%20width%3D%2212%22%20version%3D%221.1%22%3E%0A%20%3Cmetadata%20id%3D%22metadata3002%22%2F%3E%0A%20%3Cpath%20id%3D%22path2996%22%20d%3D%22m%200.5%2C16.5%2012%2C-8%20-12%2C-8%200%2C16%20z%20M%207.708%2C8.5%203.5%2C11.295%203.5%2C5.703%207.708%2C8.5%20z%22%20fill%3D%22%239a9da1%22%2F%3E%0A%3C%2Fsvg%3E%0A) !important;
}
/*NAPISZ-DO-NAS*/
.send-to-us .attachement label::before{
    background: transparent;
    border: 3px solid #c7282d;
}
.send-to-us .attachement label:hover::before{
    background: #c7282d;
    color: #fff !important;
}
/*NAPISZ-DO-NAS*/
/*NEWSROOM*/
.newsroom-calendar-month{
    border-color: #c7282d;
}
.newsroom-calendar-link:hover,.linksblock-category-list::before{
    background: #c7282d;
}
.linksblock-category-item{
    background: #000;
}
/*NEWSROOM*/
/*PODSTRONY*/
/*FOOTER*/
#common_copyright_1 p:after{
    content: " Ciemny motyw by Maciejozaman";
    color: #c7282d;
    cursor: none;
}
/*FOOTER*/
}
