css:
@-moz-document domain("last.fm") {
:root {
    --black: #000000;
    --darker: #0b0b0b;
    --dark: #111111;
    --brighter-dark: #222;
    --gray: #bcbcbc;
    --light-gray: #ebebeb;
    --white: #ffffff;
    --red: #ba0000;
    --dark-red: #8e0000;
    --turquoise: #13b0d4;
}



/* HOME PAGE */

.main-content, 
.container,
.content-top-has-nav .content-top, .two-column-layout .content-top {
    background-color: var(--dark);
}

.content-top-has-nav .content-top::after, .two-column-layout .content-top::after {
    border-top: 1px solid var(--dark-red);
}

.homefeed .content-top .secondary-nav-item-link--active {
    color: var(--white);
}
.secondary-nav-item-link {
    color: var(--gray);
}

.secondary-nav-item-link:focus, 
.secondary-nav-item-link:hover,
.homefeed .content-top .secondary-nav-item-link--active, 
.homefeed .content-top .secondary-nav-item-link--active:focus, 
.homefeed .content-top .secondary-nav-item-link--active:hover{
    color: var(--white);
}

.homefeed .content-top .secondary-nav-item--artists .secondary-nav-item-link:hover::after,
.homefeed .content-top .secondary-nav-item--albums .secondary-nav-item-link:hover::after,
.homefeed .content-top .secondary-nav-item--tracks .secondary-nav-item-link:hover::after,
.homefeed .content-top .secondary-nav-item--events .secondary-nav-item-link:hover::after,
.homefeed--artists .content-top .secondary-nav-item-link--active::after,
.homefeed--albums .content-top .secondary-nav-item-link--active::after,
.homefeed--tracks .content-top .secondary-nav-item-link--active::after,
.homefeed--events .content-top .secondary-nav-item-link--active::after
{
    background-color: var(--dark-red);
}



.recs-feed .context {
    background: #1d1d1d;
    box-shadow: inset 1px 0 #252525,inset -1px -1px #0c0c0c;
    color: #d8d8d8;
}

a {
    color: var(--turquoise);
}



.stationlink-list .stationlink {
    color: var(--gray);
}

.stationlink-list .stationlink:focus, .stationlink-list .stationlink:hover {
    color:var(--white);
}

.stationlink-list .stationlink::after {
    border-bottom: 1px solid var(--dark-red);
}

.stationlink-list .stationlink::before {
    background-color: var(--light-gray);   
    -webkit-filter: invert(100%);
    filter: invert(100%);
}



.stationlink-list .stationlink:focus::before, .stationlink-list .stationlink:hover::before {
    background-color: var(--gray);  
}

.subscribe-cta,
.mpu-subscription-upsell,
.mpu-subscription-upsell--mpu {
    display: none;
}



.lazy-features-footer {
    display: none;
}



.footer {
    background-color: var(--black);
}

.footer .row {
    background-color: var(--black);
}




/* LIVE */





/* MUSIC */

.content-top-header {
    color: var(--red);
}

.minimal-navigation-item-link, .minimal-navigation-item-link:focus, .minimal-navigation-item-link:hover {
    color: var(--gray);
}

.minimal-navigation-item-link--active, .minimal-navigation-item-link--active:focus, .minimal-navigation-item-link--active:hover {
    color: var(--white);
}

.music-section-heading {
    color: var(--white);
}

.music-more-artists-item-name a, 
.music-more-artists-item-name a:focus, 
.music-more-artists-item-name a:hover,
.music-releases-item-name a, 
.music-releases-item-name a:focus, 
.music-releases-item-name a:hover {
    color: var(--white);
}

.music-releases-item-artist,
.column-tracks-item-name,
.column-tracks-item-artist,
.music-gallery-artist{
    color: var(--white);
}

.music-section-rediscover-subscribe-banner-cta {
    background-color: var(--dark);
}





/* SEARCH */

.artist-result-heading {
    color: var(--white);
}

.album-result-heading {
    color: var(--white);
}



/* RECCOMENDATIONS */

.music-recommended-artists-artist-name,
.music-recommended-artists-context,
.pagination-page,
.pagination-next
{
    color: var(--white);
}

.music-recommended-albums-item-name,
.music-recommended-albums-album-artist,
.music-recommended-albums-album-context
{
    color: var(--white);
}

.recommended-tracks-item-name,
.recommended-tracks-item-artist
{
    color: var(--white);
}

.big-tags-item-context {
    color: var(--white);
}

p {
    color: var(--gray);  
}




/* CHARTS */

.adaptive-skin-wrapper,
.row{
    background-color: var(--dark);
}

.two-column-layout .container {
    background-size: 0;
    background-position: 0 0;
    background-color: var(--dark);
}

._buffer-reset, .sidebar-heading {
    color: var(--red);
}

.sidebar-group-heading {
    color: var(--white);
}

.resource-list--sidebar-item-name {
    color: var(--white);
}
.js-link-block.globalchart-item {
    background-color: var(--black);
    box-shadow: 0 1px var(--dark),inset 0 -1px var(--dark);
}
.globalchart a, .globalchart a:focus, .globalchart a:hover {
    color: var(--white);
}

.footer-top-row {
    background-color: var(--black);
}
.two-column-layout .container {
    background-color: #00000000;   
}

/* EVENTS */

.events-filter,
.events-filter-recommended,
.dropdown-menu-clickable-button:focus, 
.dropdown-menu-clickable-button:hover 
{
    color: var(--white);
}

.events-filters
{
    border-bottom: 1px solid var(--red);
}

.events-list-item-venue--address,
.events-list-item-event--lineup
{
    color: var(--gray);
}
.events-list-anhv1 a, 
.events-list-anhv1 a:focus, 
.events-list-anhv1 a:hover,
.events-list-item-event--title,
.events-list-item-venue--title 
{
    color: var(--white);
}




/* FEATURES */

.features-header .features-teaser-body,
.features-teaser-wrap--promoted .features-teaser-body {
    background: var(--dark);
}

.features-teaser-title a, .features-teaser-title a:focus, .features-teaser-title a:hover {
    color: var(--white);
}





/* FEATURES POST */

.features-post-header-body {
    background-color: var(--dark);
}

.features-post-header-title {
    color: var(--red);
}

.features-post-artist {
    color: var(--white);
}

.features-post-byline-item.features-post-author {
    color: var(--white)
}




/* PROFILE */

.header-avatar-add,
.header-avatar-add:not(.cta--inactive):focus, 
.header-avatar-add:not(.cta--inactive):hover{
    background-color: #5a1717;
}

.header--overview {
    background-color: var(--darker);
}

.chartlist-play-button::before {
    background-color: var(--light-gray);   
    -webkit-filter: invert(100%);
    filter: invert(100%);
}

.chartlist-play-button:focus:focus::before, .chartlist-play-button:focus:hover::before, .chartlist-play-button:hover:focus::before, .chartlist-play-button:hover:hover::before {
    box-shadow: 0 0 0 1px rgba(255,255,255,.15);
    background-color: var(--gray);  
}

.text-colour-link, h2 {
    color: var(--red);
}

.btn-secondary {
    background-color: var(--dark-red);
    color: var(--white);
}

body:not(.js-focus-visible) body:not([data-whatinput="touch"]) .btn-secondary:focus, body:not([data-whatinput="touch"]) .btn-secondary .focus-visible, body:not([data-whatinput="touch"]) .btn-secondary:hover {
    background-color: var(--red);
    color: var(--white);
}


@-webkit-keyframes AnimationName {
    0%{background-position:0% 50%}
    50%{background-position:100% 51%}
    100%{background-position:0% 50%}
}
@-moz-keyframes AnimationName {
    0%{background-position:0% 50%}
    50%{background-position:100% 51%}
    100%{background-position:0% 50%}
}
@keyframes AnimationName {
    0%{background-position:0% 50%}
    50%{background-position:100% 51%}
    100%{background-position:0% 50%}
}

.chartlist-row--highlight, .chartlist-row--now-scrobbling {
    /*background-color: #5a1717;*/
    background: linear-gradient(300deg, var(--black), #900);
    background-size: 200% 200%;
    -webkit-animation: AnimationName 10s ease infinite;
    -moz-animation: AnimationName 10s ease infinite;
    animation: AnimationName 10s ease infinite;
}

.chartlist-name,
.chartlist-artist {
    color: var(--gray);
}

.chartlist-break::after, 
.chartlist-row::after 
{
    border-bottom: 1px solid var(--brighter-dark);   
}

.chartlist-row:hover {
    background-color: var(--black);
}


.chartlist-count-bar-slug {
    background-image: linear-gradient(to right , var(--dark-red), var(--red));;
}

a:focus .chartlist-count-bar-slug, a:hover .chartlist-count-bar-slug {
    background-color: var(--dark-red);
}

.chartlist-count-bar-link,
.chartlist-count-bar-link:focus, .chartlist-count-bar-link:hover{
    color: var(--white);
}

.listening-report-promo {
    background-color: var(--darker);
}
.listening-report-promo:focus, .listening-report-promo:hover {
    background-color: var(--black);
}
.listening-report-promo-title
{
    color: var(--gray);
}
.listening-report-promo-date {
    color: var(--dark-red);
}

.shout-form textarea {
    background-color: var(--black);   
}

.chartlist-index {
    color: var(--white);
}

.chartlist tr:nth-child(1) td.chartlist-index {
    font-weight: bold;
    color: var(--red);
}





/* LISTENING REPORT */

.user-dashboard-heading {
    color: var(--red);
}

.user-dashboard-history-subscribe-banner-cta {
    display: none;
}

.duration-data,
.duration-data-value > .js-ticker ,
.user-dashboard-small-data-point-value > .js-ticker,
.user-dashboard-small-data-point-value > .js-ticker
{
    color: var(--white);
}

.highcharts-series.highcharts-series-1.highcharts-column-series.highcharts-color-undefined.highcharts-tracker > a > rect
{
    fill: var(--red);
}

.user-dashboard-module {
    background: var(--black);
    border-color: var(--dark-red);
}

.user-dashboard-loved-tracks .user-dashboard-big-datapoint-value a {
    color: var(--black);
}

.user-dashboard-catalogue-item-total a {
    text-shadow: -1px -1px 0 var(--black), 1px -1px 0 var(--black), -1px 1px 0 var(--black), 1px 1px 0 var(--black);
}

.user-dashboard-module .user-dashboard-module-share:hover, body:not([data-whatinput="mouse"]) .user-dashboard-module .user-dashboard-module-share:focus,
.user-dashboard-module:hover .user-dashboard-module-share, body:not([data-whatinput="mouse"]) .user-dashboard-module:focus .user-dashboard-module-share
{
    opacity: 0.1;
}

.highcharts-text-outline {
    fill: var(--black);
    stroke: var(--black);
}

tspan {
    color: var(--white);
    fill: var(--white);
}

.user-dashboard-catalogue-item-name.user-dashboard-catalogue-item-link.link-block-target {
    color: var(--white);
}


.user-dashboard-catalogue-item-artist-name.user-dashboard-catalogue-item-link {
    color: var(--gray);
}
                                        
                                        

.highcharts-background {
    fill: var(--black);
}

.user-dashboard-module-title.js-user-dashboard-module-title {
    color: var(--red);
}

.discovery-stat-top-item-name a, .discovery-stat-top-item-name a:focus, .discovery-stat-top-item-name a:hover {
    color: var(--white);   
}
.discovery-stat-top-item-artist:focus, .discovery-stat-top-item-artist:hover, .discovery-stat-top-item-artist a, .discovery-stat-top-item-artist a:focus, .discovery-stat-top-item-artist a:hover {
    color: var(--gray);
}

.link-block-target.user-dashboard-leaderboard-profile-link {
    color: var(--white);
}

.user-dashboard-leaderboard-item--highlight .user-dashboard-leaderboard-stat {
    color: var(--gray);
}



/* Listening Clock*/
#SvgjsG1144 {
    stroke: var(--black);
}
#SvgjsCircle1169 {
    fill: var(--black);
}
#SvgjsText1199, #SvgjsText1200, #SvgjsText1201, #SvgjsText1202 {
    fill: var(--gray);
}
#SvgjsSvg1203 #Layer_1 g path{
    fill: var(--red);
}
#SvgjsG1023 g circle{
    fill: var(--red);
}





/* LIBRARY */

.col-sidebar._buffer-sidebar > figure >div > div > svg > .highcharts-background {
    fill: var(--dark);
}

.highcharts-point {
    fill: #8e000070;
}

.highcharts-axis-labels > text {
    fill: var(--white) !important;
}






/* fOLLOWING */

.user-list-name {
    color: var(--white);
}






/* BOOKMARKS */

.music-bookmarks-artists-item-name {
    color: var(--white);
}






/* ARTIST */

.section-with-separator::after {
    background-color: var(--dark);
}

.wiki-block-inner,
.catalogue-metadata-heading,
.catalogue-metadata-description{
   color: var(--gray); 
}

.similar-albums-item-name a, .similar-albums-item-name a:focus, .similar-albums-item-name a:hover {
    color: var(--white);
}

.text-18 {
    color: var(--red);
}

.artist-similar-artists-sidebar-item-name a, .artist-similar-artists-sidebar-item-name a:focus, .artist-similar-artists-sidebar-item-name a:hover {
    color: var(--white);
}

.events-list-item-event-name.link-block-target >span {
    color: var(--white);
}

.events-list-item-venue > span {
    color: var(--gray);
}

.artist-top-albums-item-name a, .artist-top-albums-item-name a:focus, .artist-top-albums-item-name a:hover {
    color: var(--white);
}

.listeners-section-item-name a, .listeners-section-item-name a:focus, .listeners-section-item-name a:hover {
    color: var(--white);
}

.catalogue-overview-similar-artists-full-width-item-name a, .catalogue-overview-similar-artists-full-width-item-name a:focus, .catalogue-overview-similar-artists-full-width-item-name a:hover {
    color: var(--white);   
}

.resource-external-link,
.resource-external-link:hover {
    color: var(--white);
}

.shout-user a {
    color: var(--white);
}

.js-playlink-station.desktop-playlink.inline-section-control.stationlink {
    color: var(--white);
}
                    




/* SIMILAR ARTISTS */

.similar-artists-item-name {
    color: var(--white);
}

.similar-artists-item-wiki-inner-2 {
    color: var(--gray);
}

.section-playlink::before {
    background-color: var(--light-gray);   
    -webkit-filter: invert(100%);
    filter: invert(100%);
}

.section-playlink:hover::before {
    box-shadow: 0 0 0 1px rgba(255,255,255,.15);
    background-color: var(--gray);  
}

.section-playlink.js-playlink-station {
    color: var(--white);
}
            





/* TRACK */

.source-album-artist a, .source-album-artist a:focus, .source-album-artist a:hover, .source-album-name a, .source-album-name a:focus, .source-album-name a:hover {
    color: var(--white);
}

.track-similar-tracks-item-name a, .track-similar-tracks-item-name a:focus, .track-similar-tracks-item-name a:hover {
    color: var(--white);
}

.about-artist-name a, .about-artist-name a:focus, .about-artist-name a:hover {
    color: var(--white);
}

.catalogue-overview-similar-artists-item-name a, .catalogue-overview-similar-artists-item-name a:focus, .catalogue-overview-similar-artists-item-name a:hover {
    color: var(--white);
}

.stationlink::before {
    background-color: var(--light-gray);   
    -webkit-filter: invert(100%);
    filter: invert(100%);
}

.stationlink:focus::before, .stationlink:hover::before {
     box-shadow: 0 0 0 1px rgba(255,255,255,.15);
    background-color: var(--gray);  
}

.video-preview-upload-cta {
    background-color: var(--black);
    box-shadow: 0 0 0 2px var(--dark-red);
}

.video-preview-upload-cta:not(.cta--inactive):focus, .video-preview-upload-cta:not(.cta--inactive):hover {
    background-color: var(--dark);
}

.highcharts-plot-bands-0 > path {
    fill: var(--brighter-dark);
}

.similar-items-sidebar-item-name {
    color: var(--white);
}
.resource-list--release-list-item-name {
    color: var(--white);
}
.shout--deleted, .shout--reported {
    background: var(--dark);
}





/* WIKI */

.factbox-item,
#factbox-more-members
{
    color: var(--gray);
}


.factbox-item > h4{
    color: var(--red);
}

.alert-warning {
    background-color: var(--brighter-dark);
}

.content-form textarea,
.content-form input[type="text"]
{
    background-color: var(--black);
    border: 1px solid var(--black);
    color: var(--gray);
}

.content-form textarea:focus,
.content-form input[type="text"]:focus
{
    border: 1px solid var(--red);
}

.control-label {
    color: var(--red);
}

#stylerules {
    background-color: var(--brighter-dark);
    color: var(--gray);
}




/* OBSESSIONS */

.obsession-history-play-all::before {
    background-color: var(--light-gray);   
    -webkit-filter: invert(100%);
    filter: invert(100%);
    color: var(--white);
}

.obsession-history-play-all.js-playlink-station.inline-section-control
{
    color: var(--white);
}
                    

.obsession-history-play-all:hover::before {
    box-shadow: 0 0 0 1px rgba(255,255,255,.15);
    background-color: var(--gray);  
}

.obsession-search-result-header {
    color: var(--red);
}

.obsession-candidate-heading {
    color: var(--white);
}

.modal-dialog.popup_content,
.modal-content {
    background-color: var(--brighter-dark);
    box-shadow: none;
}

.content-form textarea:focus {
    box-shadow: none;
}

.content-form textarea:hover:not([disabled]) {
    border-color: var(--red);
}





/* MESSAGES */

.inbox-notifications .inbox-notifications__item {
    color: var(--gray);
}

.inbox-notifications .inbox-notifications__item--highlight {
     background-color: var(--brighter-dark);
}

.inbox-notifications__item--highlight-hover:hover {
    background-color: var(--black);
}

.inbox-message-subject {
    color: var(--red);
}

.inbox-message-message {
    color: var(--white);
}

.inbox-message-view .inbox-message-message blockquote {
    background: var(--black);
    border: 1px solid var(--red);
}

body:not([data-whatinput="touch"]) .inbox-notifications .inbox-notifications__item--hover:hover {
    background-color: var(--black);
}

.inbox .inbox-message {
    background-color: var(--black);
}

.inbox .inbox-message--unviewed {
    background-color: var(--brighter-dark);
}

.inbox .inbox-message--unviewed .inbox-message-status::after {
    background-color: var(--red);
}





/* ADS */

.lastfm-ad {
    display: none;
}
.full-bleed-ad-container, .sidebar-ad-container{
    background-color: var(--dark);
}
}
