css:
@-moz-document domain("www.wanikani.com") {
.lessons-and-reviews__reviews-button--0 {
    background-color: #b1b1b1 !important;
}
.lessons-and-reviews__lessons-button--0 {
    background-color: #b1b1b1 !important;
}
.radical-icon--locked,
.kanji-icon--locked {
    background-image: linear-gradient(180deg, #ccc 0%, #BFBFBF 100%) !important;
}
.kanji-icon {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
.lessons-and-reviews__lessons-button {
    background-color: #ffa7ec;
}
.radical-icon {
    background-color: #8fd3ff;
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
}
.lessons-and-reviews__reviews-button {
    background-color: #8fd3ff;
}
.dashboard section.srs-progress ul li:first-child {
    background-image: linear-gradient(-45deg, #ffbfe1, #ffa7ec);
}
#lessons-summary #radicals > div > ul > li.kanji,
#lessons-summary #kanji > div > ul > li.kanji,
#lessons-summary #vocabulary > div > ul > li.kanji {
    background-color: #ffa7ec;
}
ul.single-character-grid li[class|=kanji],
ul.single-character-grid tr[class|=kanji],
ul.multi-character-grid li[class|=kanji],
ul.multi-character-grid tr[class|=kanji],
.kotoba-table-list li[class|=kanji],
.kotoba-table-list tr[class|=kanji] {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
ul.single-character-grid li[class|=vocabulary],
ul.single-character-grid tr[class|=vocabulary],
ul.multi-character-grid li[class|=vocabulary],
ul.multi-character-grid tr[class|=vocabulary],
.kotoba-table-list li[class|=vocabulary],
.kotoba-table-list tr[class|=vocabulary] {
    background-image: linear-gradient(to bottom, #d9abff, #c48eff);
}
header #main-info.vocabulary {
    background-image: linear-gradient(180deg, #d9abff, #c48eff);
}
header #main-info.vocabulary #character {
    text-shadow: 5px 5px 0 #877ac3;
}
header #main-info.vocabulary #meaning {
    text-shadow: 3px 3px 0 #877ac3;
}
.dashboard section.srs-progress ul li:nth-child(2) {
    background-image: linear-gradient(-45deg, #d9abff, #c48eff);
}
#question #character.vocabulary {
    background-image: linear-gradient(180deg, #d9abff, #c48eff);
    text-shadow: 5px 5px 0 #877ac3;
}
.dashboard section.srs-progress ul li:nth-child(3) {
    background-image: linear-gradient(-45deg, #bdbfff, #999dff);
}
.dashboard section.srs-progress ul li:nth-child(4) {
    background-image: linear-gradient(-45deg, #ade7ff, #8fd3ff);
}
#question #character.radical {
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
    text-shadow: 5px 5px 0 #6098ce;
}
#answer-form fieldset.correct button,
#answer-form fieldset.correct input[type=text],
#answer-form fieldset.correct input[type=text]:disabled {
    background-color: #c3df91 !important;
}
#answer-form fieldset.incorrect button,
#answer-form fieldset.incorrect input[type=text],
#answer-form fieldset.incorrect input[type=text]:disabled {
    background-color: #ee9cb2 !important;
}
#reviews-summary #incorrect h2 {
    background-color: #ee9cb2;
}
#reviews-summary #correct h2 {
    background-color: #c3df91;
}
#reviews-summary #incorrect .apprentice > ul > li.vocabulary,
#reviews-summary #incorrect .guru > ul > li.vocabulary,
#reviews-summary #incorrect .master > ul > li.vocabulary,
#reviews-summary #incorrect .enlightened > ul > li.vocabulary,
#reviews-summary #incorrect .burned > ul > li.vocabulary,
#reviews-summary #correct .apprentice > ul > li.vocabulary,
#reviews-summary #correct .guru > ul > li.vocabulary,
#reviews-summary #correct .master > ul > li.vocabulary,
#reviews-summary #correct .enlightened > ul > li.vocabulary,
#reviews-summary #correct .burned > ul > li.vocabulary {
    background-color: #c48eff;
}
#reviews-summary #incorrect .apprentice > ul > li.radicals,
#reviews-summary #incorrect .guru > ul > li.radicals,
#reviews-summary #incorrect .master > ul > li.radicals,
#reviews-summary #incorrect .enlightened > ul > li.radicals,
#reviews-summary #incorrect .burned > ul > li.radicals,
#reviews-summary #correct .apprentice > ul > li.radicals,
#reviews-summary #correct .guru > ul > li.radicals,
#reviews-summary #correct .master > ul > li.radicals,
#reviews-summary #correct .enlightened > ul > li.radicals,
#reviews-summary #correct .burned > ul > li.radicals {
    background-color: #8fd3ff;
}
#lessons-summary #radicals h2 {
    background-color: #8fd3ff;
}
#lessons-summary #kanji h2 {
    background-color: #ffa7ec;
}
#question #character.kanji {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
    text-shadow: 5px 5px 0 #c06dc3;
}

#lessons-summary #vocabulary h2 {
    background-color: #c48eff;
}
#reviews-summary #incorrect .apprentice > ul > li.kanji,
#reviews-summary #incorrect .guru > ul > li.kanji,
#reviews-summary #incorrect .master > ul > li.kanji,
#reviews-summary #incorrect .enlightened > ul > li.kanji,
#reviews-summary #incorrect .burned > ul > li.kanji,
#reviews-summary #correct .apprentice > ul > li.kanji,
#reviews-summary #correct .guru > ul > li.kanji,
#reviews-summary #correct .master > ul > li.kanji,
#reviews-summary #correct .enlightened > ul > li.kanji,
#reviews-summary #correct .burned > ul > li.kanji {
    background-color: #ffa7ec;
}

.wk_keisei ul.single-character-grid li[id|=kanji],
.wk_keisei ul.multi-character-grid li[id|=kanji] {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}

.wk_keisei ul.single-character-grid li[id|=radical],
.wk_keisei ul.multi-character-grid li[id|=radical] {
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
}
.wk_keisei ul.single-character-grid li[id|=phonetic],
.wk_keisei ul.multi-character-grid li[id|=phonetic] {
    background-image: linear-gradient(to bottom, #aedb87, #8bd495);
}
ul.single-character-grid li[class|=vocabulary],
ul.single-character-grid tr[class|=vocabulary],
ul.multi-character-grid li[class|=vocabulary],
ul.multi-character-grid tr[class|=vocabulary],
.kotoba-table-list li[class|=vocabulary],
.kotoba-table-list tr[class|=vocabulary] {
    background-color: #a092f9;
}
ul.single-character-grid li[class|=kanji],
ul.single-character-grid tr[class|=kanji],
ul.multi-character-grid li[class|=kanji],
ul.multi-character-grid tr[class|=kanji],
.kotoba-table-list li[class|=kanji],
.kotoba-table-list tr[class|=kanji] {
    background-color: #ffa7ec;
}
.vocabulary-icon {
    background-image: linear-gradient(to bottom, #d9abff, #c48eff);
}
ul.single-character-grid li[class|=radical],
ul.single-character-grid tr[class|=radical],
ul.multi-character-grid li[class|=radical],
ul.multi-character-grid tr[class|=radical],
.kotoba-table-list li[class|=radical],
.kotoba-table-list tr[class|=radical] {
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
}
.bg-pink-gradient {
    background-color: #ffa7ec;
}
#lessons-summary #radicals > div > ul > li.vocabulary,
#lessons-summary #kanji > div > ul > li.vocabulary,
#lessons-summary #vocabulary > div > ul > li.vocabulary {
    background-color: #c48eff;
}
.sitemap__expandable-chunk--radicals {
    background: #8fd3ff;
}
.sitemap__expandable-chunk--vocabulary {
    background: #c48eff;
}
.sitemap__expandable-chunk--kanji {
    background: #ffa7ec;
}
ul.single-character-grid li[class|=radical],
ul.single-character-grid tr[class|=radical],
ul.multi-character-grid li[class|=radical],
ul.multi-character-grid tr[class|=radical],
.kotoba-table-list li[class|=radical],
.kotoba-table-list tr[class|=radical] {
    background-color: #8fd3ff;
}
#ss_quiz[data-itype="vocabulary"] .qwrap,
#ss_quiz[data-itype="vocabulary"] .summary .que {
    background-color: #d9abff;
}
header.quiz #main-info.vocabulary #character {
    text-shadow: 5px 5px 0 #877ac3;
}
#ss_quiz[data-itype="kanji"] .qwrap,
#ss_quiz[data-itype="kanji"] .summary .que {
    background-color: #ffa7ec;
}
#ss_quiz[data-itype="radical"] .qwrap,
#ss_quiz[data-itype="radical"] .summary .que {
    background-color: #8fd3ff;
}
header #main-info.kanji {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
header #main-info.kanji #character {
    text-shadow: 5px 5px 0 #c06dc3;
}
header #main-info.radical #character {
    text-shadow: 5px 5px 0 #6098ce;
}
#lessons-summary #radicals > div > ul > li.radicals,
#lessons-summary #kanji > div > ul > li.radicals,
#lessons-summary #vocabulary > div > ul > li.radicals {
    background-color: #8fd3ff;
}
header #main-info.radical {
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
}
.dashboard section.system-alert,
.dashboard section.alert-where-user-scripts-cant-ignore {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
.wk_keisei ul.single-character-grid li[id|=kanji],
.wk_keisei ul.multi-character-grid li[id|=kanji] {
    background-color: #ffa7ec;
}
.heatmaps .day_items .char.kanji {
    background: #ffa7ec;
}
.heatmaps .day_items .char.vocabulary {
    background: #c48eff;
}
.heatmaps .day_items .char.radical {
    background: #8fd3ff;
}
.popover.srs .popover-content ul li:first-child {
    background-image: linear-gradient(to bottom, #ade7ff, #8fd3ff);
}
.popover.srs .popover-content ul li:nth-child(2) {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
.popover.srs .popover-content ul li:last-child {
    background-image: linear-gradient(to bottom, #d9abff, #c48eff);
}
.vocabulary {
    background-color: #c48eff;
}

.kanji {
    background-color: #ffa7ec;
}
.radical {
    background-color: #8fd3ff;
}

.dashboard section.system-alert,
.dashboard section.alert-where-user-scripts-cant-ignore,
.dashboard section.sale-alert {
    background-image: linear-gradient(to bottom, #ffbfe1, #ffa7ec);
}
}
